import{_ as i,C as r,c as t,o as s,ag as l,G as a}from"./chunks/framework.S-Qvb3wi.js";const m=JSON.parse('{"title":"Kapitel 1 – Methodik & Zielgruppe","description":"Warum dieses Dossier kein Appell ist – sondern ein Werkzeug. Für alle, die verstehen wollen, wie strukturelle Blockade funktioniert – und wie man ihr begegnen kann.\\n","frontmatter":{"title":"Kapitel 1 – Methodik & Zielgruppe","description":"Warum dieses Dossier kein Appell ist – sondern ein Werkzeug. Für alle, die verstehen wollen, wie strukturelle Blockade funktioniert – und wie man ihr begegnen kann.\\n"},"headers":[],"relativePath":"01_methodik-und-zielgruppe.md","filePath":"01_methodik-und-zielgruppe.md","lastUpdated":1753279356000}'),o={name:"01_methodik-und-zielgruppe.md"};function u(d,e,h,g,c,k){const n=r("Footer");return s(),t("div",null,[e[0]||(e[0]=l('<h1 id="kapitel-1-–-methodik-zielgruppe" tabindex="-1">Kapitel 1 – Methodik &amp; Zielgruppe <a class="header-anchor" href="#kapitel-1-–-methodik-zielgruppe" aria-label="Permalink to &quot;Kapitel 1 – Methodik &amp; Zielgruppe&quot;">​</a></h1><p><em>Dies ist kein Bericht. Kein Appell. Kein Versuch, dich zu überzeugen.</em></p><p>Dieses Dossier ist ein Werkzeug – entstanden aus Frustration, Recherche und der Frage:<br><strong>Was genau blockiert gesellschaftlichen Wandel – und wie kann man es sichtbar machen?</strong></p><h2 id="🎯-was-dieses-dossier-bietet" tabindex="-1">🎯 Was dieses Dossier bietet <a class="header-anchor" href="#🎯-was-dieses-dossier-bietet" aria-label="Permalink to &quot;🎯 Was dieses Dossier bietet&quot;">​</a></h2><p>„Die Schlinge“ arbeitet mit dokumentierter Realität und verdichteter Darstellung – aber ohne Skandalton, Schuldzuweisung oder moralische Überhöhung.</p><p>Es ist:</p><ul><li>systemisch statt skandalös</li><li>analysierend statt appellierend</li><li>modular statt chronologisch</li></ul><p>Die Kapitel sind <strong>eigenständig lesbar</strong> und folgen einer <strong>diagnostischen Logik</strong>:<br> Erst das Muster, dann die Ebenen, dann die Hebel.</p><h2 id="👥-fur-wen-es-geschrieben-ist" tabindex="-1">👥 Für wen es geschrieben ist <a class="header-anchor" href="#👥-fur-wen-es-geschrieben-ist" aria-label="Permalink to &quot;👥 Für wen es geschrieben ist&quot;">​</a></h2><p>Für Menschen, die …</p><ul><li><strong>gegen Windmühlen arbeiten</strong> – in Bildung, Medien, Recht, Verwaltung, Aktivismus</li><li><strong>mehr als nur informiert sein wollen</strong> – sondern wirksam</li><li>sich <strong>nicht beruhigen lassen</strong>, sondern Strukturen verstehen wollen</li></ul><p>Du brauchst keine Fachvorkenntnisse –<br> aber die Bereitschaft, Muster zu erkennen, auch wenn sie unbequem sind.</p><h2 id="🧭-wie-man-dieses-dossier-lesen-kann" tabindex="-1">🧭 Wie man dieses Dossier lesen kann <a class="header-anchor" href="#🧭-wie-man-dieses-dossier-lesen-kann" aria-label="Permalink to &quot;🧭 Wie man dieses Dossier lesen kann&quot;">​</a></h2><ul><li><strong>Modular</strong>: Du musst nicht vorne beginnen – einsteigen ist überall möglich</li><li><strong>Quellenbasiert</strong>: Alle Aussagen sind öffentlich überprüfbar</li><li><strong>Mit Haltung</strong>: Die Sprache ist klar, konfrontativ – nicht neutral, aber begründet</li></ul><blockquote><p><em>„Dies ist kein Dokument. Es ist ein Schnitt.“</em><br> Ein Schnitt durch Jahrzehnte der Beruhigung, Ablenkung und Symbolpolitik.</p></blockquote><h2 id="🚫-was-du-nicht-bekommst" tabindex="-1">🚫 Was du nicht bekommst <a class="header-anchor" href="#🚫-was-du-nicht-bekommst" aria-label="Permalink to &quot;🚫 Was du nicht bekommst&quot;">​</a></h2><ul><li>Keine Schuldzuweisung an Einzelne</li><li>Kein Appell an individuelle Moral</li><li>Kein Versuch, zu beruhigen</li></ul><blockquote><p><em>Nicht die Fakten fehlen – sondern die Struktur, die ihnen Wirkung verleiht.</em></p></blockquote><h2 id="🔍-methodik-quellenstandards" tabindex="-1">🔍 Methodik &amp; Quellenstandards <a class="header-anchor" href="#🔍-methodik-quellenstandards" aria-label="Permalink to &quot;🔍 Methodik &amp; Quellenstandards&quot;">​</a></h2><p>Die Inhalte dieses Dossiers basieren auf <strong>öffentlich zugänglichen, überprüfbaren Quellen</strong> – aus:</p><ul><li>Wissenschaft</li><li>Journalismus</li><li>Recht</li><li>Zivilgesellschaft</li></ul><p><strong>Grundsätze der Quellenarbeit:</strong></p><ul><li><strong>Mindestens zwei unabhängige Quellen</strong> für zentrale Aussagen</li><li><strong>Bevorzugung von Primärdokumenten</strong></li><li><strong>Direktverlinkung</strong> (DOI, URL, Fußnoten)</li><li><strong>Keine unbelegten Einzelbehauptungen</strong></li><li><strong>Klare Trennung</strong> zwischen dokumentarischen und szenisch-fiktionalen Elementen</li></ul><h2 id="🎭-rolle-der-fiktion" tabindex="-1">🎭 Rolle der Fiktion <a class="header-anchor" href="#🎭-rolle-der-fiktion" aria-label="Permalink to &quot;🎭 Rolle der Fiktion&quot;">​</a></h2><p>Alle szenischen Elemente in diesem Dossier sind <strong>fiktionalisiert</strong>. Sie beruhen auf dokumentierten Mustern, abstrahieren jedoch von konkreten Fällen.</p><p>Ziel ist die <strong>Verdichtung struktureller Wirkmechanismen</strong> – <strong>nicht</strong> die Darstellung realer Personen, Gespräche oder Ereignisse.</p><hr><blockquote><p><strong>Hinweis:</strong><br> Dieses Dossier versteht sich als <strong>offenes, modulares Werkzeug-Dossier</strong>. Es lädt ausdrücklich zur kollaborativen Nutzung, Erweiterung und didaktischen Adaption ein – im Sinne eines lebendigen, gemeinsam nutzbaren Tools für systemische Analyse und gesellschaftliche Bildung.</p><p><strong>Offen für alle:</strong><br> Dieses Dossier ist barrierefrei und als „lebendes Dokument“ auf <a href="https://github.com/bjoernboettle/die-schlinge" target="_blank" rel="noreferrer">GitHub</a> veröffentlicht. Jede:r ist eingeladen, mitzuwirken, zu ergänzen oder zu diskutieren – via Pull Request, Issue oder Mail.<br> Lizenz: <a href="https://creativecommons.org/licenses/by-sa/4.0/deed.de" target="_blank" rel="noreferrer">CC BY-SA 4.0</a><br> Kontakt: <a href="mailto:kontakt@die-schlinge.de" target="_blank" rel="noreferrer">kontakt@die-schlinge.de</a></p></blockquote>',28)),a(n)])}const b=i(o,[["render",u]]);export{m as __pageData,b as default};
