import{_ as n,C as r,c as i,o as a,ag as t,G as o}from"./chunks/framework.S-Qvb3wi.js";const l="/assets/wissen-system-wirkung.excalidraw.TnDhVTm1.png",b=JSON.parse('{"title":"Kapitel 3 – Warum Wissen nicht reicht","description":"Das zentrale Muster der Schlinge – als visuelle Brücke zwischen Ohnmacht und Analyse.\\n","frontmatter":{"title":"Kapitel 3 – Warum Wissen nicht reicht","description":"Das zentrale Muster der Schlinge – als visuelle Brücke zwischen Ohnmacht und Analyse.\\n"},"headers":[],"relativePath":"03_warum-wissen-nicht-reicht.md","filePath":"03_warum-wissen-nicht-reicht.md","lastUpdated":1753279356000}'),d={name:"03_warum-wissen-nicht-reicht.md"};function u(h,e,g,c,m,k){const s=r("Footer");return a(),i("div",null,[e[0]||(e[0]=t('<h1 id="kapitel-3-–-warum-wissen-nicht-reicht" tabindex="-1">Kapitel 3 – Warum Wissen nicht reicht <a class="header-anchor" href="#kapitel-3-–-warum-wissen-nicht-reicht" aria-label="Permalink to &quot;Kapitel 3 – Warum Wissen nicht reicht&quot;">​</a></h1><h2 id="🧠-wissen-ist-da-–-wirkung-bleibt-aus" tabindex="-1">🧠 Wissen ist da – Wirkung bleibt aus <a class="header-anchor" href="#🧠-wissen-ist-da-–-wirkung-bleibt-aus" aria-label="Permalink to &quot;🧠 Wissen ist da – Wirkung bleibt aus&quot;">​</a></h2><p>Die Daten sind klar. Die Modelle präzise. Die Prognosen erschreckend konstant.</p><p><strong>Seit 1990 – dem Zeitpunkt eines breiten wissenschaftlichen Konsenses über die menschengemachte Erderwärmung – wurden rund 56 % aller fossilen CO₂-Emissionen seit 1750 ausgestoßen. Rechnet man zusätzlich die Emissionen aus Landnutzungsänderungen hinzu, liegt der Anteil bei etwa 45 % aller anthropogenen CO₂-Emissionen seit Beginn der Industrialisierung.</strong><sup class="footnote-ref"><a href="#fn1" id="fnref1">[1]</a></sup> Die Klimakrise ist also kein Ergebnis von Unwissen – sondern von unterlassener Umsetzung.</p><p>Trotzdem steigt der Ausstoß. Der Wandel bleibt aus.</p><blockquote><p><em>Was fehlt, ist nicht Wissen – sondern Wirkung.</em></p></blockquote><p><img src="'+l+'" alt="Systemgrafik – Wissen wird neutralisiert"><em>Abb. 1: Das System der Verzögerung – Wissen verpufft, weil es systematisch neutralisiert wird.</em></p><p>Was uns bremst, ist kein Mangel an Fakten – sondern ein Zusammenspiel aus:</p><ul><li><strong>verteilter Verantwortung</strong></li><li><strong>rollenbasierter Ablenkung</strong></li><li><strong>institutioneller Trägheit</strong></li></ul><h2 id="🪢-das-system-der-verzogerung-–-kompakt" tabindex="-1">🪢 Das System der Verzögerung – kompakt <a class="header-anchor" href="#🪢-das-system-der-verzogerung-–-kompakt" aria-label="Permalink to &quot;🪢 Das System der Verzögerung – kompakt&quot;">​</a></h2><blockquote><p>📌 <strong>Kontext</strong>:<br> Die Aussage „Verzögerung ist System“ wird hier konkretisiert – durch ein strukturiertes Zusammenspiel aus Akteuren, Mechanismen und Verzögerungsmustern, das nicht zufällig wirkt, sondern systemisch.</p></blockquote><table tabindex="0"><thead><tr><th>🧠 <strong>Wissen ist da</strong></th><th>🕸️ <strong>Aber das System…</strong></th><th>🪤 <strong>…führt zu</strong></th><th>⏳ <strong>Und das Ergebnis ist</strong></th></tr></thead><tbody><tr><td>✅ Klimadaten sind eindeutig</td><td>🔄 Verantwortung wird verteilt oder verschleiert</td><td>😶 Gefühl von Ohnmacht</td><td>🧨 Handlung bleibt aus</td></tr><tr><td>✅ Forschung ist sich einig</td><td>🗞️ Medien inszenieren Debatten trotz Konsens („False Balance“)</td><td>🎯 Aufmerksamkeit wird abgelenkt</td><td>⏳ Zeit vergeht – ohne Wirkung</td></tr><tr><td>✅ Lösungen sind bekannt</td><td>🧑‍💼 Politik setzt auf Symbolik statt Struktur</td><td>🙍 Schuld wird individualisiert</td><td>🔁 Der Status quo stabilisiert sich</td></tr><tr><td>✅ Szenarien warnen seit Jahrzehnten</td><td>🏭 Industrie verzögert gezielt (PR, Lobby, Legalismus)</td><td>🥱 Müdigkeit, Rückzug, Resignation</td><td>🚨 Kipppunkte rücken näher</td></tr></tbody></table><h2 id="📌-was-daraus-folgt" tabindex="-1">📌 Was daraus folgt <a class="header-anchor" href="#📌-was-daraus-folgt" aria-label="Permalink to &quot;📌 Was daraus folgt&quot;">​</a></h2><ul><li>Dieses System braucht keine Lüge – <strong>nur Lücken</strong>.</li><li>Verzögerung ist kein Betriebsfehler – sondern <strong>Systemarchitektur</strong>.</li><li><strong>Wer das Muster erkennt, erkennt auch seine Bruchstellen.</strong></li></ul><hr><blockquote><p><em>„Die Schlinge wirkt, weil niemand sie unterbricht.“</em></p></blockquote>',16)),o(s),e[1]||(e[1]=t('<hr class="footnotes-sep"><section class="footnotes"><ol class="footnotes-list"><li id="fn1" class="footnote-item"><p>Friedlingstein, P., Jones, M. W., O’Sullivan, M., Andrew, R. M., Bakker, D. C. E., Canadell, J. G., et al. (2023). Global Carbon Budget 2023. <em>Earth System Science Data</em>, 15, 5301–5334. <a href="https://doi.org/10.5194/essd-15-5301-2023" target="_blank" rel="noreferrer">https://doi.org/10.5194/essd-15-5301-2023</a>. Berechnung auf Basis des Supplement-Datensatzes („Historical Budget“, Version 1.1, Sheet „global_annual“): Von den insgesamt 1773 Gt fossilen CO₂-Emissionen seit 1750 wurden 988 Gt (56 %) nach 1990 ausgestoßen. Unter Einbezug der Emissionen aus Landnutzung (LULUCF) beträgt der Anteil nach 1990 rund 45 % (1159 von 2575 Gt CO₂). <a href="#fnref1" class="footnote-backref">↩︎</a></p></li></ol></section>',2))])}const f=n(d,[["render",u]]);export{b as __pageData,f as default};
