import{_ as i,C as s,c as t,o as r,ag as l,G as a}from"./chunks/framework.S-Qvb3wi.js";const o="/assets/systemkarte.excalidraw.DEcDnXCe.png",b=JSON.parse('{"title":"Kapitel 6 – Schlüssel­ebenen","description":"Warum dieses Dossier systemisch denkt – und wie die verschiedenen Ebenen von Wissenschaft bis Ethik zusammenwirken.\\n","frontmatter":{"title":"Kapitel 6 – Schlüssel­ebenen","description":"Warum dieses Dossier systemisch denkt – und wie die verschiedenen Ebenen von Wissenschaft bis Ethik zusammenwirken.\\n"},"headers":[],"relativePath":"06_schluesselebenen/00_einleitung.md","filePath":"06_schluesselebenen/00_einleitung.md","lastUpdated":1753279356000}'),u={name:"06_schluesselebenen/00_einleitung.md"};function d(c,e,h,m,g,k){const n=s("Footer");return r(),t("div",null,[e[0]||(e[0]=l('<h1 id="kapitel-6-–-schlussel­ebenen" tabindex="-1">Kapitel 6 – Schlüssel­ebenen <a class="header-anchor" href="#kapitel-6-–-schlussel­ebenen" aria-label="Permalink to &quot;Kapitel 6 – Schlüssel­ebenen&quot;">​</a></h1><p>Die Klimakrise ist kein rein technisches Problem.<br> Sie ist ein komplexes Geflecht aus Wissen, Macht, Routinen – <strong>ein systemisches Phänomen</strong>, das sich nicht mit Fakten allein bewältigen lässt.</p><h2 id="🎯-worum-es-in-diesem-kapitel-geht" tabindex="-1">🎯 Worum es in diesem Kapitel geht <a class="header-anchor" href="#🎯-worum-es-in-diesem-kapitel-geht" aria-label="Permalink to &quot;🎯 Worum es in diesem Kapitel geht&quot;">​</a></h2><p>Diese Einführung skizziert die <strong>zentralen gesellschaftlichen, politischen und kommunikativen Ebenen</strong>,<br> auf denen Klimawissen entsteht – aber auch in Spannung zu anderen Interessen steht.</p><p>Es geht nicht um Schuld, sondern um <strong>Strukturen, Verantwortung und Handlungsspielräume</strong>:</p><ul><li>Wer wusste wann – und wie wurde mit diesem Wissen umgegangen?</li><li>Welche Rolle spielen Medien, Märkte, Institutionen und gesellschaftliche Narrative?</li><li>Wo wird gehandelt – und wo bestehen Muster des Zögerns?</li></ul><h2 id="🧩-der-systemische-blick" tabindex="-1">🧩 Der systemische Blick <a class="header-anchor" href="#🧩-der-systemische-blick" aria-label="Permalink to &quot;🧩 Der systemische Blick&quot;">​</a></h2><p>Jede dieser <strong>Schlüsselebenen</strong> wird in einem eigenen Kapitel vertieft.<br> Gemeinsam zeigen sie, <strong>wie Klimaverzögerung strukturell verankert ist – und wo Möglichkeiten für Veränderung liegen.</strong></p><blockquote><p><strong>„Verzögerung ist kein Zufall – sie folgt Mustern.“</strong></p></blockquote><p>Dieses Dossier macht diese Muster sichtbar –<br> und lädt dazu ein, Räume für Wandel gezielt zu erkennen und zu nutzen.</p><h2 id="🗺️-systemkarte-akteure-dynamiken-ebenen" tabindex="-1">🗺️ Systemkarte: Akteure, Dynamiken, Ebenen <a class="header-anchor" href="#🗺️-systemkarte-akteure-dynamiken-ebenen" aria-label="Permalink to &quot;🗺️ Systemkarte: Akteure, Dynamiken, Ebenen&quot;">​</a></h2><p><img src="'+o+'" alt="Systemkarte – Akteure, Dynamiken, Rückkopplungen"><em>Überblick zu Kapitel 6: Wissenschaft, Industrie, Politik, Medien, Öffentlichkeit – und ihre Wechselwirkungen.</em></p><p><strong>So liest man die Grafik:</strong></p><ul><li><strong>Kreise</strong> zeigen die Ebenen: <ul><li>🔵 Kernakteure (Wissenschaft, Industrie…)</li><li>🟡 systemische Verzögerungsfaktoren</li><li>🟢 mögliche Korrektive (Gerichte, NGOs…)</li><li>🔴 Öffentlichkeit (wirkt reaktiv, resonanzabhängig)</li></ul></li><li><strong>Pfeile</strong> markieren Einflussrichtungen: <ul><li><strong>stark &amp; solide</strong> = direkter Macht-Einfluss (z. B. Lobby)</li><li><strong>gestrichelt</strong> = indirekte oder systemische Wirkung</li><li><strong>dünn</strong> = Rückkopplungen (z. B. Framing, Polarisierung)</li></ul></li></ul><p>Diese Visualisierung kann beim Lesen der folgenden Unterkapitel als Landkarte dienen.</p>',15)),a(n)])}const f=i(u,[["render",d]]);export{b as __pageData,f as default};
