import{_ as r,C as a,c as s,o as i,ag as t,G as l}from"./chunks/framework.S-Qvb3wi.js";const b=JSON.parse('{"title":"7.2 System unter Druck","description":"Fünf prägnante Bruchstellen – als Tabelle erzählt: von Gerichtsurteilen bis Klimanotstand. Ideal für den Überblick.\\n","frontmatter":{"title":"7.2 System unter Druck","description":"Fünf prägnante Bruchstellen – als Tabelle erzählt: von Gerichtsurteilen bis Klimanotstand. Ideal für den Überblick.\\n","hint":"Quellenprüfung durchführen"},"headers":[],"relativePath":"07_gegenmacht/02_system-unter-druck.md","filePath":"07_gegenmacht/02_system-unter-druck.md","lastUpdated":1753279356000}'),o={name:"07_gegenmacht/02_system-unter-druck.md"};function d(f,e,c,h,g,u){const n=a("Footer");return i(),s("div",null,[e[0]||(e[0]=t('<h1 id="_7-2-system-unter-druck-–-funf-bruchstellen-im-vergleich" tabindex="-1">7.2 System unter Druck – fünf Bruchstellen im Vergleich <a class="header-anchor" href="#_7-2-system-unter-druck-–-funf-bruchstellen-im-vergleich" aria-label="Permalink to &quot;7.2 System unter Druck – fünf Bruchstellen im Vergleich&quot;">​</a></h1><blockquote><p>Nicht jeder Wandel beginnt mit einem Plan.<br> Manche Veränderungen entstehen, weil Menschen <strong>nicht locker lassen</strong>.<br> Andere, weil <strong>Gerichte konsequenter urteilen</strong> als Parlamente.<br> Oder weil <strong>Städte schneller handeln</strong> als Staaten.</p></blockquote><p>Diese fünf Beispiele zeigen, wie sich Spannungen im System entladen – punktuell, aber wirkungsvoll.<br> Die folgende Tabelle versammelt zentrale Interventionen auf verschiedenen Ebenen – von lokalen Stadtparlamenten bis zu internationalen Gerichtshöfen.</p><h2 id="📊-uberblick-wer-bringt-bewegung-ins-system" tabindex="-1">📊 Überblick: Wer bringt Bewegung ins System? <a class="header-anchor" href="#📊-uberblick-wer-bringt-bewegung-ins-system" aria-label="Permalink to &quot;📊 Überblick: Wer bringt Bewegung ins System?&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Ort / Ebene</th><th>Ereignis</th><th>Wirkung</th></tr></thead><tbody><tr><td><strong>Uganda</strong></td><td>Klage gegen die geplante EACOP-Pipeline wegen Verstößen gegen Menschen- und Landrechte (eingereicht 2020, dokumentiert 2022).<sup class="footnote-ref"><a href="#fn1" id="fnref1">[1]</a></sup> Staatliche Repression gegen Proteste wurde parallel berichtet.<sup class="footnote-ref"><a href="#fn2" id="fnref2">[2]</a></sup></td><td><strong>Gerichtlicher Widerstand</strong> gegen fossile Großprojekte</td></tr><tr><td><strong>Dänemark</strong></td><td>Politisch und rechtlich verbindlich beschlossene Abkehr von der Ölförderung (2020): keine neuen Lizenzen bis 2050.<sup class="footnote-ref"><a href="#fn3" id="fnref3">[3]</a></sup><sup class="footnote-ref"><a href="#fn4" id="fnref4">[4]</a></sup></td><td><strong>Verbindliches Ausstiegsmodell</strong> statt politischer Absichtserklärungen</td></tr><tr><td><strong>Ecuador</strong></td><td>Urteil 1149-19-JP/22: Indigene Weltanschauungen beeinflussen erstmals die nationale Rechtsprechung.<sup class="footnote-ref"><a href="#fn5" id="fnref5">[5]</a></sup></td><td><strong>Anerkennung indigener Rechte</strong> gegenüber extraktivistischer Politik</td></tr><tr><td><strong>Weltweit</strong></td><td>Über 2 000 Städte in mehr als 30 Ländern rufen den <strong>Klimanotstand</strong> aus.<sup class="footnote-ref"><a href="#fn6" id="fnref6">[6]</a></sup> Viele davon agieren ambitionierter als ihre nationalen Regierungen.<sup class="footnote-ref"><a href="#fn7" id="fnref7">[7]</a></sup></td><td><strong>Kommunen</strong> aals Vorreiter für ehrgeizige Klimapolitik (wobei der Begriff „Klimanotstand“ häufig symbolisch ist und keine rechtliche Bindung entfaltet).</td></tr><tr><td><strong>Global</strong></td><td>Die Zahl von <strong>Klimaklagen</strong> steigt rasant – inzwischen in über 50 Ländern dokumentiert. <sup class="footnote-ref"><a href="#fn8" id="fnref8">[8]</a></sup> <sup class="footnote-ref"><a href="#fn9" id="fnref9">[9]</a></sup> Immer mehr davon argumentieren mit Menschenrechten und erzielen teils Erfolge. <sup class="footnote-ref"><a href="#fn10" id="fnref10">[10]</a></sup></td><td><strong>Gerichte</strong> als Treiber von Generationengerechtigkeit</td></tr></tbody></table><h2 id="🧩-was-zeigt-sich-in-diesen-fallen" tabindex="-1">🧩 Was zeigt sich in diesen Fällen? <a class="header-anchor" href="#🧩-was-zeigt-sich-in-diesen-fallen" aria-label="Permalink to &quot;🧩 Was zeigt sich in diesen Fällen?&quot;">​</a></h2><p>Die fünf Beispiele veranschaulichen unterschiedliche Strategien von Gegenmacht:</p><ul><li><p><strong>Rechtsprechung als Hebel</strong><br> Gerichte greifen ein, wenn Parlamente versagen – etwa in Uganda, Ecuador oder bei Klimaklagen.</p></li><li><p><strong>Politischer Gestaltungswille</strong><br> Staaten können aus eigener Initiative neue Wege beschreiten – wie Dänemark mit dem gesetzlich geregelten Ölausstieg.</p></li><li><p><strong>Städtische Dynamik</strong><br> Kommunen handeln häufig schneller und mutiger als Nationalstaaten – wie die Klimanotstandserklärungen zeigen.</p></li></ul><p>Allen Fällen gemeinsam ist:<br> Die bestehenden Spielregeln werden <strong>nicht abgeschafft</strong>, sondern <strong>von innen heraus infrage gestellt</strong>.</p><p>Klagen, Gesetze oder symbolische Beschlüsse erzeugen <strong>Störungen im System</strong> – mit realen politischen, rechtlichen und gesellschaftlichen Folgen.</p><h2 id="📊-matrix-systemische-bewertung-zentraler-bruchstellen" tabindex="-1">📊 Matrix: Systemische Bewertung zentraler Bruchstellen <a class="header-anchor" href="#📊-matrix-systemische-bewertung-zentraler-bruchstellen" aria-label="Permalink to &quot;📊 Matrix: Systemische Bewertung zentraler Bruchstellen&quot;">​</a></h2><p>Diese Matrix vergleicht fünf prägnante Eingriffe ins klimapolitische System – entlang dreier strategischer Dimensionen:</p><table tabindex="0"><thead><tr><th><strong>Fall / Ort</strong></th><th><strong>Wirkebene</strong> (lokal/national/global)</th><th><strong>Veränderungstiefe</strong> (symbolisch / regulatorisch / paradigmatisch)</th><th><strong>Replizierbarkeit</strong> (hoch / mittel / gering)</th><th><strong>Zentraler Hebel</strong></th></tr></thead><tbody><tr><td>Uganda (EACOP)</td><td>regional</td><td>regulatorisch</td><td>mittel</td><td>Gerichtsurteil</td></tr><tr><td>Dänemark (Ölgesetz)</td><td>national</td><td>paradigmatisch</td><td>hoch</td><td>Gesetzgebung</td></tr><tr><td>Ecuador (Indigenenrechte)</td><td>national</td><td>paradigmatisch</td><td>gering</td><td>Verfassungsrecht</td></tr><tr><td>Städte (Klimanotstand)</td><td>lokal / kommunal</td><td>symbolisch → teils regulatorisch</td><td>hoch</td><td>Stadtratsbeschluss</td></tr><tr><td>Klimaklagen weltweit</td><td>global</td><td>regulatorisch → teils paradigmatisch</td><td>mittel</td><td>strategische Prozessführung</td></tr></tbody></table><h2 id="⚡-fazit-wo-es-risse-gibt-kann-etwas-neues-entstehen" tabindex="-1">⚡ Fazit: Wo es Risse gibt, kann etwas Neues entstehen <a class="header-anchor" href="#⚡-fazit-wo-es-risse-gibt-kann-etwas-neues-entstehen" aria-label="Permalink to &quot;⚡ Fazit: Wo es Risse gibt, kann etwas Neues entstehen&quot;">​</a></h2><p>Diese fünf Bruchstellen markieren <strong>keine Revolution</strong>, aber sie machen deutlich:</p><p><strong>Das System ist nicht undurchlässig.</strong><br> Es entstehen Risse – im Recht, in der Politik, im kollektiven Denken.</p><blockquote><p>Nicht jeder Riss ist sichtbar.<br> Aber manche lassen Licht durch.</p></blockquote><h2 id="🧾-quellenbasis" tabindex="-1">🧾 Quellenbasis <a class="header-anchor" href="#🧾-quellenbasis" aria-label="Permalink to &quot;🧾 Quellenbasis&quot;">​</a></h2><blockquote><p>Diese Tabelle listet alle belegten Aussagen aus dem Kapitel.</p></blockquote><table tabindex="0"><thead><tr><th>Quelle / Autor</th><th>Jahr</th><th>Quelle (Typ)</th><th>Belegt was?</th><th>Verlinkung / Fundort</th></tr></thead><tbody><tr><td>Human Rights Watch</td><td>2023</td><td>Investigative Recherche</td><td>Repression gegen Proteste gegen EACOP in Uganda</td><td><sup class="footnote-ref"><a href="#fn2" id="fnref2:1">[2:1]</a></sup></td></tr><tr><td>Reuters</td><td>2020</td><td>Medienbericht</td><td>Dänemarks Verzicht auf neue Öl-Förderlizenzen</td><td><sup class="footnote-ref"><a href="#fn3" id="fnref3:1">[3:1]</a></sup></td></tr><tr><td>Climate Emergency Declaration</td><td>2025</td><td>NGO-Bericht</td><td>Überblick über globale Klimanotstandserklärungen</td><td><sup class="footnote-ref"><a href="#fn6" id="fnref6:1">[6:1]</a></sup></td></tr><tr><td>UNEP</td><td>2023</td><td>Wissenschaftliche Studie</td><td>Anstieg und Verbreitung von Klimaklagen weltweit</td><td><sup class="footnote-ref"><a href="#fn8" id="fnref8:1">[8:1]</a></sup></td></tr><tr><td>AFIEGO</td><td>2022</td><td>NGO-Bericht</td><td>Klage gegen EACOP vor ostafrikanischem Gericht</td><td><sup class="footnote-ref"><a href="#fn1" id="fnref1:1">[1:1]</a></sup></td></tr><tr><td>Danish Ministry of Climate, Energy and Utilities</td><td>2020</td><td>Offizielles Dokument</td><td>Gesetzliche Grundlagen für fossilen Rückbau Dänemarks</td><td><sup class="footnote-ref"><a href="#fn4" id="fnref4:1">[4:1]</a></sup></td></tr><tr><td>Corte Constitucional del Ecuador</td><td>2022</td><td>Gerichtsurteil</td><td>Indigene Rechte in ecuadorianischem Verfassungsrecht</td><td><sup class="footnote-ref"><a href="#fn5" id="fnref5:1">[5:1]</a></sup></td></tr><tr><td>CDP</td><td>2023</td><td>NGO-Bericht / Datenplattform</td><td>Städtevergleich zu Klimaambitionen</td><td><sup class="footnote-ref"><a href="#fn7" id="fnref7:1">[7:1]</a></sup></td></tr><tr><td>Sabin Center for Climate Change Law</td><td>2023</td><td>Wissenschaftliche Datenbank</td><td>Datenbasis zu weltweiten Klimaklagen</td><td><sup class="footnote-ref"><a href="#fn9" id="fnref9:1">[9:1]</a></sup></td></tr><tr><td>Peel, J., &amp; Osofsky, H. M.</td><td>2018</td><td>Peer-Review-Artikel</td><td>Menschenrechtlicher Fokus in Klimaklagen</td><td><sup class="footnote-ref"><a href="#fn10" id="fnref10:1">[10:1]</a></sup></td></tr></tbody></table>',20)),l(n),e[1]||(e[1]=t('<hr class="footnotes-sep"><section class="footnotes"><ol class="footnotes-list"><li id="fn1" class="footnote-item"><p>AFIEGO (2022): <em>Factsheet – EACOP case at the East African Court of Justice, February 2022</em>. [NGO-Bericht]. Verfügbar unter: <a href="https://www.afiego.org/wp-content/uploads/2024/03/Factsheeet-EACOP-case-at-the-East-African-Court-of-Justice-February-2022.pdf" target="_blank" rel="noreferrer">https://www.afiego.org/wp-content/uploads/2024/03/Factsheeet-EACOP-case-at-the-East-African-Court-of-Justice-February-2022.pdf</a> [Zugriff am: 20.07.2025]. <a href="#fnref1" class="footnote-backref">↩︎</a> <a href="#fnref1:1" class="footnote-backref">↩︎</a></p></li><li id="fn2" class="footnote-item"><p>Human Rights Watch (2023): <em>Uganda: Oil pipeline protests stifled</em>. [Investigative Recherche]. Verfügbar unter: <a href="https://www.hrw.org/news/2023/11/02/uganda-oil-pipeline-protests-stifled" target="_blank" rel="noreferrer">https://www.hrw.org/news/2023/11/02/uganda-oil-pipeline-protests-stifled</a> [Zugriff am: 20.07.2025]. <a href="#fnref2" class="footnote-backref">↩︎</a> <a href="#fnref2:1" class="footnote-backref">↩︎</a></p></li><li id="fn3" class="footnote-item"><p>Reuters (2020): <em>Denmark pins green credentials on end to North Sea oil hunt</em>. [Medienbericht]. Verfügbar unter: <a href="https://www.reuters.com/article/business/energy/denmark-pins-green-credentials-on-end-to-north-sea-oil-hunt-idUSKBN28E26Y" target="_blank" rel="noreferrer">https://www.reuters.com/article/business/energy/denmark-pins-green-credentials-on-end-to-north-sea-oil-hunt-idUSKBN28E26Y</a> [Zugriff am: 20.07.2025]. <a href="#fnref3" class="footnote-backref">↩︎</a> <a href="#fnref3:1" class="footnote-backref">↩︎</a></p></li><li id="fn4" class="footnote-item"><p>Danish Ministry of Climate, Energy and Utilities (2020): <em>Climate Agreement for Energy and Industry 2020</em> (Faktaark, Englisch). [Offizielles Dokument]. Verfügbar unter: <a href="https://www.kefm.dk/Media/C/B/faktaark-klimaaftale%20%28English%20august%2014%29.pdf" target="_blank" rel="noreferrer">https://www.kefm.dk/Media/C/B/faktaark-klimaaftale%20(English%20august%2014).pdf</a> [Zugriff am: 20.07.2025]. <a href="#fnref4" class="footnote-backref">↩︎</a> <a href="#fnref4:1" class="footnote-backref">↩︎</a></p></li><li id="fn5" class="footnote-item"><p>Corte Constitucional del Ecuador (2022): <em>Sentencia 1149‑19‑JP/22</em>. [Gerichtsurteil]. Verfügbar unter: <a href="https://esacc.corteconstitucional.gob.ec/storage/api/v1/10_DWL_FL/e2NhcnBldGE6J2VzY3JpdG8nLCB1dWlkOidjNDMwZWUyMy1jMTQyLTQ1NzktYjVlNC1mZjEwYzk4ZmY5MDMucGRmJ30%3D" target="_blank" rel="noreferrer">https://esacc.corteconstitucional.gob.ec/storage/api/v1/10_DWL_FL/e2NhcnBldGE6J2VzY3JpdG8nLCB1dWlkOidjNDMwZWUyMy1jMTQyLTQ1NzktYjVlNC1mZjEwYzk4ZmY5MDMucGRmJ30%3D</a> [Zugriff am: 20.07.2025]. <a href="#fnref5" class="footnote-backref">↩︎</a> <a href="#fnref5:1" class="footnote-backref">↩︎</a></p></li><li id="fn6" class="footnote-item"><p>Climate Emergency Declaration (2025): <em>Climate emergency declarations cover 1 billion citizens across 2 366 jurisdictions in 40 countries</em>. [NGO-Bericht]. Verfügbar unter: <a href="https://climateemergencydeclaration.org/climate-emergency-declarations-cover-15-million-citizens/" target="_blank" rel="noreferrer">https://climateemergencydeclaration.org/climate-emergency-declarations-cover-15-million-citizens/</a> [Zugriff am: 20.07.2025]. <a href="#fnref6" class="footnote-backref">↩︎</a> <a href="#fnref6:1" class="footnote-backref">↩︎</a></p></li><li id="fn7" class="footnote-item"><p>CDP (2023): <em>Cities A‑List and disclosure data</em>. [NGO-Bericht / Datenplattform]. Verfügbar unter: <a href="https://www.cdp.net/en/data/scores" target="_blank" rel="noreferrer">https://www.cdp.net/en/data/scores</a> [Zugriff am: 20.07.2025]. <a href="#fnref7" class="footnote-backref">↩︎</a> <a href="#fnref7:1" class="footnote-backref">↩︎</a></p></li><li id="fn8" class="footnote-item"><p>UNEP (2023): <em>Global Climate Litigation Report – 2023 Status Review</em>. [Wissenschaftliche Studie]. Verfügbar unter: <a href="https://www.unep.org/resources/report/global-climate-litigation-report-2023-status-review" target="_blank" rel="noreferrer">https://www.unep.org/resources/report/global-climate-litigation-report-2023-status-review</a> [Zugriff am: 20.07.2025]. <a href="#fnref8" class="footnote-backref">↩︎</a> <a href="#fnref8:1" class="footnote-backref">↩︎</a></p></li><li id="fn9" class="footnote-item"><p>Sabin Center for Climate Change Law (2023): <em>Climate Litigation Database</em>. [Wissenschaftliche Datenbank]. Verfügbar unter: <a href="https://climate.law.columbia.edu/content/climate-change-litigation" target="_blank" rel="noreferrer">https://climate.law.columbia.edu/content/climate-change-litigation</a> [Zugriff am: 20.07.2025]. <a href="#fnref9" class="footnote-backref">↩︎</a> <a href="#fnref9:1" class="footnote-backref">↩︎</a></p></li><li id="fn10" class="footnote-item"><p>Peel, J., &amp; Osofsky, H. M. (2018): <em>A rights turn in climate change litigation?</em> <em>Transnational Environmental Law</em>, 7(1), 37–67. [Peer-Review-Artikel]. Verfügbar unter: <a href="https://doi.org/10.1017/S2047102517000292" target="_blank" rel="noreferrer">https://doi.org/10.1017/S2047102517000292</a> [Zugriff am: 20.07.2025]. <a href="#fnref10" class="footnote-backref">↩︎</a> <a href="#fnref10:1" class="footnote-backref">↩︎</a></p></li></ol></section>',2))])}const p=r(o,[["render",d]]);export{b as __pageData,p as default};
