import{_ as n,C as r,c as i,o as a,ag as s,G as o}from"./chunks/framework.S-Qvb3wi.js";const l="/assets/matrix-des-wandels.excalidraw.DPcz2Ovu.png",p=JSON.parse('{"title":"7.3 Systemwechsel konkret","description":"Neun politische Eingriffe – gegliedert nach Maßstab: international, national, kommunal. Jedes Beispiel zeigt: Wie Struktur das Ziel sichert – nicht Moral, sondern Mechanik.\\n","frontmatter":{"title":"7.3 Systemwechsel konkret","description":"Neun politische Eingriffe – gegliedert nach Maßstab: international, national, kommunal. Jedes Beispiel zeigt: Wie Struktur das Ziel sichert – nicht Moral, sondern Mechanik.\\n"},"headers":[],"relativePath":"07_gegenmacht/03_systemwechsel-konkret.md","filePath":"07_gegenmacht/03_systemwechsel-konkret.md","lastUpdated":1753279356000}'),d={name:"07_gegenmacht/03_systemwechsel-konkret.md"};function h(u,e,g,c,m,b){const t=r("Footer");return a(),i("div",null,[e[0]||(e[0]=s('<h1 id="_7-3-systemwechsel-konkret-–-9-hebel-auf-3-ebenen" tabindex="-1">7.3 Systemwechsel konkret – 9 Hebel auf 3 Ebenen <a class="header-anchor" href="#_7-3-systemwechsel-konkret-–-9-hebel-auf-3-ebenen" aria-label="Permalink to &quot;7.3 Systemwechsel konkret – 9 Hebel auf 3 Ebenen&quot;">​</a></h1><p>Dieses Kapitel zeigt, wie Gesetze, Verträge und Regeln verändert werden müssen –<br> damit Klimaschutz nicht von Einsicht abhängt, sondern vom System selbst.</p><h2 id="🧭-der-systemwechsel-beginnt-nicht-mit-einsicht-sondern-mit-architektur" tabindex="-1">🧭 Der Systemwechsel beginnt nicht mit Einsicht, sondern mit Architektur <a class="header-anchor" href="#🧭-der-systemwechsel-beginnt-nicht-mit-einsicht-sondern-mit-architektur" aria-label="Permalink to &quot;🧭 Der Systemwechsel beginnt nicht mit Einsicht, sondern mit Architektur&quot;">​</a></h2><p>Ein System kippt nicht durch Kritik.<br> Es kippt, wenn es sich selbst widerspricht.</p><p>Was wäre, wenn die Regeln den Planeten schützten – ganz automatisch?<br> Wenn kein Gesetz mehr durchkäme, das uns zurück ins Fossile zieht?</p><p>Dieses Kapitel zeigt, wie Macht neu geordnet werden kann – und Blockaden enden.<br><strong>Drei Maßstabsebenen. Neun Eingriffe. Kein Appell. Eine Anleitung.</strong></p><h2 id="🧭-matrix-des-wandels" tabindex="-1">🧭 Matrix des Wandels <a class="header-anchor" href="#🧭-matrix-des-wandels" aria-label="Permalink to &quot;🧭 Matrix des Wandels&quot;">​</a></h2><p><img src="'+l+'" alt="Matrix des Wandels – 9 systemische Hebel"><em>Ein Raster aus Denken, Handeln und Regeln – auf drei Wirkungsebenen. Lesbar als Kompass für Systemwechsel.</em></p><h2 id="🌍-internationale-ebene-–-wenn-vertrage-fesseln-sind" tabindex="-1">🌍 INTERNATIONALE EBENE – Wenn Verträge Fesseln sind <a class="header-anchor" href="#🌍-internationale-ebene-–-wenn-vertrage-fesseln-sind" aria-label="Permalink to &quot;🌍 INTERNATIONALE EBENE – Wenn Verträge Fesseln sind&quot;">​</a></h2><h3 id="_1-klimavorbehalt-in-allen-handelsvertragen" tabindex="-1">1. Klimavorbehalt in allen Handelsverträgen <a class="header-anchor" href="#_1-klimavorbehalt-in-allen-handelsvertragen" aria-label="Permalink to &quot;1. Klimavorbehalt in allen Handelsverträgen&quot;">​</a></h3><p><strong>Was:</strong><br> Jeder neue Handelsvertrag enthält eine Klausel: <strong>Klimaschutz hat Vorrang vor Investoreninteressen.</strong></p><p><strong>Wie:</strong><br> Verankerung auf Basis des Pariser Abkommens (Klimazielvertrag von 2015). WTO-kompatible Klimaausnahmen im Vertragsrecht.</p><p><strong>Fiktionale Szene:</strong><br><em>2029, Den Haag.</em> Zwei Männer vor Gericht – einer vertritt ein Bergwerk, der andere einen Gletscher.<br> Die Richterin sagt:</p><blockquote><p>„Klimaziele sind kein Anhang. Sie sind Verfassungsgrund.“</p></blockquote><h3 id="_2-investorenschutz-neu-schreiben" tabindex="-1">2. Investorenschutz neu schreiben <a class="header-anchor" href="#_2-investorenschutz-neu-schreiben" aria-label="Permalink to &quot;2. Investorenschutz neu schreiben&quot;">​</a></h3><p><strong>Was:</strong><br> Unternehmen dürfen Staaten <strong>nicht mehr</strong> für Klimaschutzmaßnahmen verklagen.</p><p><strong>Wie:</strong><br> ISDS-Klauseln (Investor-Staat-Schiedsverfahren) streichen – oder ersetzen durch öffentliche Schiedsgerichte mit Klimaschutzklausel.</p><p><strong>Fiktionale Szene:</strong><br> Ein kanadischer Konzern fordert Entschädigung für den Kohleausstieg. Das Gericht urteilt:</p><blockquote><p>„Die Zukunft ist kein Geschäftsrisiko.“</p></blockquote><h3 id="_3-co2-grenzen-an-der-zollschranke" tabindex="-1">3. CO₂-Grenzen an der Zollschranke <a class="header-anchor" href="#_3-co2-grenzen-an-der-zollschranke" aria-label="Permalink to &quot;3. CO₂-Grenzen an der Zollschranke&quot;">​</a></h3><p><strong>Was:</strong><br> Importe aus klimaschädlicher Produktion werden teurer.<br> Klimakompatible Staaten erhalten Vorteile.</p><p><strong>Wie:</strong><br> Ein CO₂-Grenzausgleich (CBAM) wird kombiniert mit Pariser Kompatibilität als Handelsvoraussetzung.</p><p><strong>Fiktionale Szene:</strong><br> Eine indische Stahlfabrik investiert in grüne Technik – nicht aus Überzeugung, sondern weil der Markt es verlangt.</p><h2 id="🏛️-nationale-ebene-–-gesetze-die-nicht-mehr-lugen-konnen" tabindex="-1">🏛️ NATIONALE EBENE – Gesetze, die nicht mehr lügen können <a class="header-anchor" href="#🏛️-nationale-ebene-–-gesetze-die-nicht-mehr-lugen-konnen" aria-label="Permalink to &quot;🏛️ NATIONALE EBENE – Gesetze, die nicht mehr lügen können&quot;">​</a></h2><h3 id="_4-klimawirkungsprufung-mit-vetopflicht" tabindex="-1">4. Klimawirkungsprüfung mit Vetopflicht <a class="header-anchor" href="#_4-klimawirkungsprufung-mit-vetopflicht" aria-label="Permalink to &quot;4. Klimawirkungsprüfung mit Vetopflicht&quot;">​</a></h3><p><strong>Was:</strong><br> Jedes Gesetz braucht eine CO₂-Bilanz.<br> Verfehlt es den Klimapfad, wird es automatisch gestoppt.</p><p><strong>Wie:</strong><br> Verpflichtend im Klimaschutzgesetz und in der Parlamentsordnung. Prüfung durch ein unabhängiges Gremium.</p><p><strong>Fiktionale Szene:</strong><br> Ein Bonus für Plug-in-Hybride wird beschlossen.<br> Der Bericht zeigt: +1,2 % Emissionen.<br> Der Klimarat sagt Nein. Der Bundestag schweigt. Das Gesetz fällt.</p><h3 id="_5-co2-preis-mit-dividende-–-gesetzlich-fixiert" tabindex="-1">5. CO₂-Preis mit Dividende – gesetzlich fixiert <a class="header-anchor" href="#_5-co2-preis-mit-dividende-–-gesetzlich-fixiert" aria-label="Permalink to &quot;5. CO₂-Preis mit Dividende – gesetzlich fixiert&quot;">​</a></h3><p><strong>Was:</strong><br> Der CO₂-Preis steigt automatisch.<br> Die Einnahmen werden pro Kopf an die Bevölkerung zurückgezahlt.</p><p><strong>Wie:</strong><br> Gesetzlich indexierter Preisanstieg. Rückzahlung über die Finanzämter.</p><p><strong>Fiktionale Szene:</strong><br> 2027. Heizöl wird teurer – gleichzeitig erhalten alle Haushalte 1.000 € zurück.<br> Der Effekt: Klimaschutz rechnet sich – sozial gerecht.</p><h3 id="_6-keine-subvention-ohne-klimatreue" tabindex="-1">6. Keine Subvention ohne Klimatreue <a class="header-anchor" href="#_6-keine-subvention-ohne-klimatreue" aria-label="Permalink to &quot;6. Keine Subvention ohne Klimatreue&quot;">​</a></h3><p><strong>Was:</strong><br> Staatliche Gelder fließen <strong>nur</strong> in Projekte, die mit dem 1,5°-Ziel vereinbar sind.</p><p><strong>Wie:</strong><br> Klimakriterien im Haushaltsrecht und bei Vergabeverfahren verbindlich verankern.</p><p><strong>Fiktionale Szene:</strong><br> Ein Flughafenausbau soll gefördert werden –<br> bis der Haushaltsausschuss sagt: „Nicht auf diesem Pfad.“</p><h2 id="🏘️-kommunale-ebene-–-macht-die-nah-ist" tabindex="-1">🏘️ KOMMUNALE EBENE – Macht, die nah ist <a class="header-anchor" href="#🏘️-kommunale-ebene-–-macht-die-nah-ist" aria-label="Permalink to &quot;🏘️ KOMMUNALE EBENE – Macht, die nah ist&quot;">​</a></h2><h3 id="_7-co2-budgets-in-burger-innenhand" tabindex="-1">7. CO₂-Budgets in Bürger:innenhand <a class="header-anchor" href="#_7-co2-budgets-in-burger-innenhand" aria-label="Permalink to &quot;7. CO₂-Budgets in Bürger:innenhand&quot;">​</a></h3><p><strong>Was:</strong><br> Jede Kommune erhält ein jährliches CO₂-Limit.<br> Großprojekte werden gemeinsam mit Bürger:innen priorisiert.</p><p><strong>Wie:</strong><br> Verpflichtende Bürgerräte plus gesetzlich verankerte Pfadbindung auf Landesebene.</p><p><strong>Fiktionale Szene:</strong><br> Stadtrat Freiburg. Zwei Projekte konkurrieren: Parkdeck oder Wärmenetz.<br> Die Losgruppe entscheidet: Klimaschutz zuerst.</p><h3 id="_8-klimaprufung-fur-baupolitik" tabindex="-1">8. Klimaprüfung für Baupolitik <a class="header-anchor" href="#_8-klimaprufung-fur-baupolitik" aria-label="Permalink to &quot;8. Klimaprüfung für Baupolitik&quot;">​</a></h3><p><strong>Was:</strong><br> Jede neue Straße, jedes neue Wohngebiet braucht eine CO₂-Wirkungsprüfung.</p><p><strong>Wie:</strong><br> Bauleitplanung wird um Klimakriterien erweitert.<br> Integration in bestehende Umweltprüfungen.</p><p><strong>Fiktionale Szene:</strong><br> Ein Neubaugebiet wird nicht genehmigt – nicht wegen der Kosten.<br> Sondern wegen der Klimabilanz.</p><h3 id="_9-geld-nur-fur-kommunen-mit-pfadtreue" tabindex="-1">9. Geld nur für Kommunen mit Pfadtreue <a class="header-anchor" href="#_9-geld-nur-fur-kommunen-mit-pfadtreue" aria-label="Permalink to &quot;9. Geld nur für Kommunen mit Pfadtreue&quot;">​</a></h3><p><strong>Was:</strong><br> Fördermittel werden an tatsächliche CO₂-Einsparungen geknüpft.</p><p><strong>Wie:</strong><br> Transformationsfonds mit klarer Pfadbindung auf Landes- und Bundesebene.</p><p><strong>Fiktionale Szene:</strong><br> Zwei Städte: Eine hat 60 % eingespart – sie bekommt 40 Millionen.<br> Die andere: nichts. Kein Skandal. Nur Konsequenz.</p><h2 id="📉-machtmatrix" tabindex="-1">📉 Machtmatrix <a class="header-anchor" href="#📉-machtmatrix" aria-label="Permalink to &quot;📉 Machtmatrix&quot;">​</a></h2><table tabindex="0"><thead><tr><th><strong>Ebene</strong></th><th><strong>Vorschlag</strong></th><th><strong>Wirkung</strong></th><th><strong>Mehrheit</strong></th><th><strong>Rückbausicherheit</strong></th></tr></thead><tbody><tr><td>International</td><td>Klimavorbehalt</td><td>Hoch</td><td>Teilweise</td><td>Hoch</td></tr><tr><td>International</td><td>ISDS-Reform</td><td>Mittel</td><td>Schwach</td><td>Mittel</td></tr><tr><td>International</td><td>CO₂-Zölle</td><td>Hoch</td><td>Ja</td><td>Hoch</td></tr><tr><td>National</td><td>Klimawirkungsprüfung</td><td>Hoch</td><td>Ja</td><td>Hoch</td></tr><tr><td>National</td><td>CO₂-Preis + Dividende</td><td>Hoch</td><td>Ja</td><td>Hoch</td></tr><tr><td>National</td><td>Klimaklausel für Subventionen</td><td>Mittel</td><td>Ja</td><td>Ja</td></tr><tr><td>Kommunal</td><td>Budgets + Beteiligung</td><td>Hoch</td><td>Ja</td><td>Mittel</td></tr><tr><td>Kommunal</td><td>Bau-Klimaprüfung</td><td>Mittel</td><td>Ja</td><td>Hoch</td></tr><tr><td>Kommunal</td><td>Förderbindung an CO₂-Pfad</td><td>Hoch</td><td>Ja</td><td>Hoch</td></tr></tbody></table><h2 id="🔄-verbindung-zu-gegenmacht" tabindex="-1">🔄 Verbindung zu &quot;Gegenmacht&quot; <a class="header-anchor" href="#🔄-verbindung-zu-gegenmacht" aria-label="Permalink to &quot;🔄 Verbindung zu &quot;Gegenmacht&quot;&quot;">​</a></h2><p>Nicht alle Hebel dieses Kapitels liegen in den Händen der Leser:innen.<br> Aber ihre Durchsetzung beginnt oft ganz woanders.</p><blockquote><p>Wer national keine Macht hat, kann lokal Druck aufbauen – mit Öffentlichkeit, Klage, Protest.<br> Der Werkzeugkasten zeigt: Manche Hebel sind institutionell – aber ihre Bruchstellen liegen im Alltag.</p></blockquote><h2 id="🧰-handlungsmatrix-fur-alltag-und-akteure" tabindex="-1">🧰 Handlungsmatrix für Alltag und Akteure <a class="header-anchor" href="#🧰-handlungsmatrix-fur-alltag-und-akteure" aria-label="Permalink to &quot;🧰 Handlungsmatrix für Alltag und Akteure&quot;">​</a></h2><table tabindex="0"><thead><tr><th><strong>Wirkfel</strong></th><th><strong>Was kann ich konkret tun?</strong></th></tr></thead><tbody><tr><td><strong>Kommune</strong></td><td>Bürgerrat anstoßen, Klimavorbehalt in Bauleitlinien fordern</td></tr><tr><td><strong>NGO / Bündnis</strong></td><td>Koalition für ein CO₂-gebundenes Fördergesetz gründen</td></tr><tr><td><strong>Bildung / Schule</strong></td><td>„Zukunftsgericht“ simulieren, Klimaverfassungen debattieren</td></tr><tr><td><strong>Medien / Kultur</strong></td><td>Investorenschutz visualisieren, Rückbau dokumentieren, Machtfragen erzählen</td></tr></tbody></table><h2 id="🧩-struktur-statt-einsicht" tabindex="-1">🧩 Struktur statt Einsicht <a class="header-anchor" href="#🧩-struktur-statt-einsicht" aria-label="Permalink to &quot;🧩 Struktur statt Einsicht&quot;">​</a></h2><blockquote><p>Die neun Eingriffe verändern nicht nur Inhalte – sondern Prozesse.<br> Sie verlagern die Verantwortung vom Gewissen zur Architektur.</p></blockquote><p>Ein gutes Gesetz erkennt man daran, dass es <strong>nicht verstanden werden muss, um zu wirken</strong>.</p><p><strong>Wenn Regeln Automatik erzeugen, braucht es keine Empörung mehr.</strong><br><strong>Dann genügt der Ablauf.</strong></p><blockquote><p>Das ist keine Utopie – sondern Rechtsgestaltung im Ernstfall.</p></blockquote><h2 id="🔚-letzter-satz" tabindex="-1">🔚 Letzter Satz <a class="header-anchor" href="#🔚-letzter-satz" aria-label="Permalink to &quot;🔚 Letzter Satz&quot;">​</a></h2><blockquote><p>Dieses Gesetz fällt nicht wegen Empörung – sondern weil es am Pfad scheitert.<br> Wenn Architektur wirkt, braucht es keine Einsicht mehr.<br> Bruchstellen lassen sich bauen.<br> Wer das Recht ändert, ändert das Ergebnis.<br><strong>Systemwandel ist kein Traum. Er ist eine Entscheidung über Regeln.</strong></p></blockquote>',63)),o(t)])}const f=n(d,[["render",h]]);export{p as __pageData,f as default};
