import{_ as r,C as t,c as i,o as a,ag as l,G as s}from"./chunks/framework.S-Qvb3wi.js";const b=JSON.parse('{"title":"7.4 Werkzeugkasten","description":"Praktische Links, Plattformen und Aktionsideen – zugeschnitten auf Schulen, Kommunen, Einzelpersonen und Hochschulen. Präzise statt perfekt.\\n","frontmatter":{"title":"7.4 Werkzeugkasten","description":"Praktische Links, Plattformen und Aktionsideen – zugeschnitten auf Schulen, Kommunen, Einzelpersonen und Hochschulen. Präzise statt perfekt.\\n"},"headers":[],"relativePath":"07_gegenmacht/04_werkzeugkasten.md","filePath":"07_gegenmacht/04_werkzeugkasten.md","lastUpdated":1753279356000}'),o={name:"07_gegenmacht/04_werkzeugkasten.md"};function h(u,e,c,g,d,m){const n=t("Footer");return a(),i("div",null,[e[0]||(e[0]=l('<h1 id="_7-4-werkzeugkasten-–-dein-hebel-gegen-das-system" tabindex="-1">7.4 Werkzeugkasten – Dein Hebel gegen das System <a class="header-anchor" href="#_7-4-werkzeugkasten-–-dein-hebel-gegen-das-system" aria-label="Permalink to &quot;7.4 Werkzeugkasten – Dein Hebel gegen das System&quot;">​</a></h1><blockquote><p>Veränderung beginnt nicht mit Hoffnung, sondern mit einem <strong>klaren Schnitt</strong>.</p></blockquote><h2 id="🧭-uberblick-was-du-tun-kannst" tabindex="-1">🧭 Überblick: Was du tun kannst <a class="header-anchor" href="#🧭-uberblick-was-du-tun-kannst" aria-label="Permalink to &quot;🧭 Überblick: Was du tun kannst&quot;">​</a></h2><p>Wenn du aus einem zerstörerischen System aussteigen willst, brauchst du Werkzeuge – zum <strong>Beobachten</strong>, <strong>Stören</strong> und <strong>Umsteuern</strong>.</p><p>Dieser Werkzeugkasten versammelt konkrete Hebel, die du sofort nutzen kannst. Er ist nicht vollständig, aber präzise.</p><h2 id="🛠️-systemische-hebel-und-tools" tabindex="-1">🛠️ Systemische Hebel und Tools <a class="header-anchor" href="#🛠️-systemische-hebel-und-tools" aria-label="Permalink to &quot;🛠️ Systemische Hebel und Tools&quot;">​</a></h2><p>Hier findest du Ansätze, um in größere Strukturen einzugreifen – rechtlich, medial, politisch oder im Alltag.</p><h3 id="klimaklagen-–-druck-durch-recht" tabindex="-1"><strong>Klimaklagen – Druck durch Recht</strong> <a class="header-anchor" href="#klimaklagen-–-druck-durch-recht" aria-label="Permalink to &quot;**Klimaklagen – Druck durch Recht**&quot;">​</a></h3><ul><li>Klimaklagen verfolgen, unterstützen oder selbst einreichen</li><li>Plattform: <a href="https://climatecasechart.com" target="_blank" rel="noreferrer">climatecasechart.com</a><br> → Beispiel: Die erfolgreiche Klage von <em>Urgenda</em> gegen die niederländische Regierung</li></ul><h3 id="desinformationsnetzwerke-enttarnen" tabindex="-1"><strong>Desinformationsnetzwerke enttarnen</strong> <a class="header-anchor" href="#desinformationsnetzwerke-enttarnen" aria-label="Permalink to &quot;**Desinformationsnetzwerke enttarnen**&quot;">​</a></h3><ul><li>Fossile Lobbys sichtbar machen, Narrative analysieren</li><li>Tools: <a href="https://influenceMap.org" target="_blank" rel="noreferrer">influenceMap.org</a>, <a href="https://www.desmog.com" target="_blank" rel="noreferrer">desmog.com</a><br> → Beispiel: Einfluss von Ölkonzernen auf Klimapolitik öffentlich machen</li></ul><h3 id="klimarate-–-mitwirken-statt-warten" tabindex="-1"><strong>Klimaräte – Mitwirken statt warten</strong> <a class="header-anchor" href="#klimarate-–-mitwirken-statt-warten" aria-label="Permalink to &quot;**Klimaräte – Mitwirken statt warten**&quot;">​</a></h3><ul><li>Bürger:innenräte auf kommunaler oder nationaler Ebene mitgestalten<br> → Viele Städte in Deutschland suchen aktive Mitwirkende für lokale Klimaräte</li></ul><h3 id="whistleblower-innen-starken" tabindex="-1"><strong>Whistleblower:innen stärken</strong> <a class="header-anchor" href="#whistleblower-innen-starken" aria-label="Permalink to &quot;**Whistleblower:innen stärken**&quot;">​</a></h3><ul><li>Schutzräume schaffen für Menschen, die internes Wissen teilen</li><li>Plattform: <a href="https://climatewhistleblowers.org" target="_blank" rel="noreferrer">climatewhistleblowers.org</a></li></ul><h3 id="ngos-–-verbunden-und-handeln" tabindex="-1"><strong>NGOs – Verbünden und handeln</strong> <a class="header-anchor" href="#ngos-–-verbunden-und-handeln" aria-label="Permalink to &quot;**NGOs – Verbünden und handeln**&quot;">​</a></h3><ul><li>Organisationen beobachten, unterstützen oder mitarbeiten</li><li>Beispiele: <em>ClientEarth</em>, <em>Urgenda</em>, <em>Deutsche Umwelthilfe</em>, <em>Fridays for Future</em></li></ul><h3 id="offene-archive-–-beweise-sichern" tabindex="-1"><strong>Offene Archive – Beweise sichern</strong> <a class="header-anchor" href="#offene-archive-–-beweise-sichern" aria-label="Permalink to &quot;**Offene Archive – Beweise sichern**&quot;">​</a></h3><ul><li>Interne Dokumente und Konzernstrategien selbst einsehen</li><li>Quellen: <a href="https://exxonsecrets.org" target="_blank" rel="noreferrer">exxonsecrets.org</a>, <a href="https://climatefiles.org" target="_blank" rel="noreferrer">climatefiles.org</a><br> → Zum Beispiel: Nachvollziehen, wie Exxon seit Jahrzehnten Zweifel sät</li></ul><blockquote><p><strong>Vertiefung:</strong> Welche systemischen Hebel wirken – und wie sie strukturell ansetzen – erfährst du in Kapitel 8 („Gegenmacht – wo sie wirkt“).</p></blockquote><h2 id="🧱-haltung-statt-anleitung" tabindex="-1">🧱 Haltung statt Anleitung <a class="header-anchor" href="#🧱-haltung-statt-anleitung" aria-label="Permalink to &quot;🧱 Haltung statt Anleitung&quot;">​</a></h2><p>Du musst nicht alles perfekt machen.<br> Aber du kannst <strong>gezielt stören</strong>.</p><p>Ein einzelner Bruch kann genügen, um ein ganzes System ins Wanken zu bringen.</p><p>Es geht nicht um eine Schritt-für-Schritt-Anleitung – sondern um Klarheit:</p><ul><li>über die <strong>Muster</strong>, die sich wiederholen</li><li>über die <strong>Risse</strong>, die schon da sind</li><li>über deine <strong>eigene Rolle</strong> im Ganzen</li></ul><p>Wer das Spiel durchschaut, muss nicht auf Hoffnung warten –<br> sondern auf den richtigen Moment, es zu unterbrechen.</p><h2 id="🚪-und-wie-unterbrechen-wir-–-konkret" tabindex="-1">🚪 Und wie unterbrechen wir – konkret? <a class="header-anchor" href="#🚪-und-wie-unterbrechen-wir-–-konkret" aria-label="Permalink to &quot;🚪 Und wie unterbrechen wir – konkret?&quot;">​</a></h2><p>Ob du an einer Schule arbeitest, in einer Behörde, an einer Uni –<br> oder einfach für dich überlegst, was du tun kannst:</p><p><strong>Jeder Ort hat seine eigenen Hebel.</strong></p><h3 id="schulen-–-lernraume-irritieren" tabindex="-1">Schulen – Lernräume irritieren <a class="header-anchor" href="#schulen-–-lernraume-irritieren" aria-label="Permalink to &quot;Schulen – Lernräume irritieren&quot;">​</a></h3><ul><li>Szenen aus <em>„Die Schlinge“</em> im Unterricht analysieren<br> → Welche Verzögerungsstrategien sind erkennbar?</li><li>Projektarbeit: Wo entstehen im Schulalltag „Schlingen“ – also Verzögerungen oder Ausweichmanöver?</li><li>Theaterstück oder Ausstellung entwickeln: <strong>„Fiktion trifft Realität“</strong></li></ul><h3 id="kommunen-–-offentliche-raume-neu-denken" tabindex="-1">Kommunen – Öffentliche Räume neu denken <a class="header-anchor" href="#kommunen-–-offentliche-raume-neu-denken" aria-label="Permalink to &quot;Kommunen – Öffentliche Räume neu denken&quot;">​</a></h3><ul><li>Fossile Werbung im Stadtbild beenden</li><li>Bürgerräte zur lokalen Klimapolitik aufbauen</li><li>Förderprogramme auf fossile Abhängigkeiten prüfen und umgestalten</li></ul><h3 id="einzelpersonen-–-storen-im-kleinen" tabindex="-1">Einzelpersonen – Stören im Kleinen <a class="header-anchor" href="#einzelpersonen-–-storen-im-kleinen" aria-label="Permalink to &quot;Einzelpersonen – Stören im Kleinen&quot;">​</a></h3><ul><li>Alltagsroutinen sichtbar unterbrechen (z. B. autofreier Monat mit Begründung)</li><li>Artikel nicht nur teilen – sondern mit Kontext versehen<br> → Kommentare erzeugen Wirkung</li><li>Leser:innenbriefe oder Online-Kommentare schreiben<br> → Sprache als Werkzeug der Irritation nutzen</li></ul><h3 id="hochschulen-bildung-–-systeme-hinterfragen" tabindex="-1">Hochschulen &amp; Bildung – Systeme hinterfragen <a class="header-anchor" href="#hochschulen-bildung-–-systeme-hinterfragen" aria-label="Permalink to &quot;Hochschulen &amp; Bildung – Systeme hinterfragen&quot;">​</a></h3><ul><li>Studiengänge und Institute auf fossile Verbindungen untersuchen<br> (z. B. Sponsoring, Drittmittel)</li><li>Interdisziplinäre Lesekreise zum Dossier gründen</li><li>Projektseminare entwickeln: <strong>„Störung als Strategie“</strong></li></ul><p><strong>Weiterlesen:</strong></p><p>→ <em>Kapitel 8.5: Risse im System</em> – dokumentierte Urteile, politische Brüche und reale Eingriffe → <em>Kapitel 8.6: Fallbeispiele</em> – konkrete Handlungen und übertragbare Miniaturen</p><h2 id="📚-recherchequellen" tabindex="-1">📚 Recherchequellen <a class="header-anchor" href="#📚-recherchequellen" aria-label="Permalink to &quot;📚 Recherchequellen&quot;">​</a></h2><ul><li>Climate Case Chart – <a href="https://climatecasechart.com" target="_blank" rel="noreferrer">https://climatecasechart.com</a></li><li>InfluenceMap – <a href="https://influencemap.org" target="_blank" rel="noreferrer">https://influencemap.org</a></li><li>DeSmog – <a href="https://www.desmog.com" target="_blank" rel="noreferrer">https://www.desmog.com</a></li><li>Climate Whistleblowers – <a href="https://climatewhistleblowers.org" target="_blank" rel="noreferrer">https://climatewhistleblowers.org</a></li><li>ExxonSecrets – <a href="https://exxonsecrets.org" target="_blank" rel="noreferrer">https://exxonsecrets.org</a></li><li>ClimateFiles – <a href="https://climatefiles.org" target="_blank" rel="noreferrer">https://climatefiles.org</a></li></ul>',41)),s(n)])}const f=r(o,[["render",h]]);export{b as __pageData,f as default};
