import{_ as t,C as i,c as r,o as a,ag as s,G as l}from"./chunks/framework.S-Qvb3wi.js";const b=JSON.parse('{"title":"Kapitel 8 – Was jetzt?","description":"Einleitung in den Handlungsteil: Was folgt aus dem Dossier? Welche Wege, Hebel und Rollen gibt es? Diese Übersicht leitet über – von der Analyse zur Handlung.\\n","frontmatter":{"title":"Kapitel 8 – Was jetzt?","description":"Einleitung in den Handlungsteil: Was folgt aus dem Dossier? Welche Wege, Hebel und Rollen gibt es? Diese Übersicht leitet über – von der Analyse zur Handlung.\\n"},"headers":[],"relativePath":"08_was-jetzt/00_einleitung.md","filePath":"08_was-jetzt/00_einleitung.md","lastUpdated":1753279356000}'),o={name:"08_was-jetzt/00_einleitung.md"};function u(d,e,g,h,c,p){const n=i("Footer");return a(),r("div",null,[e[0]||(e[0]=s('<h1 id="kapitel-8-–-was-jetzt" tabindex="-1">Kapitel 8 – Was jetzt? <a class="header-anchor" href="#kapitel-8-–-was-jetzt" aria-label="Permalink to &quot;Kapitel 8 – Was jetzt?&quot;">​</a></h1><blockquote><p><em>„Was wir tun, zählt – wenn wir wissen, wo wir ansetzen.“</em></p></blockquote><p>Dieses Dossier endet nicht mit einem klassischen Fazit, sondern mit einer Einladung:<br><strong>Was machen wir aus dem Wissen? Und wo beginnt Verantwortung?</strong></p><p>Die vorangegangenen Kapitel haben gezeigt: Die Verzögerung im Klimaschutz ist kein Zufall – sie ist Strategie. Eine Strategie, die systematisch wirkt, aber gerade deshalb <strong>sichtbar und angreifbar</strong> ist.</p><p>Wer die Muster erkennt, kann sie unterbrechen.</p><p>Deshalb stellt dieser Abschnitt nicht die Frage: <em>Was sollten andere tun?</em><br> Sondern: <em>Was ist mein nächster Hebel – mit dem, was ich bin, weiß und zur Verfügung habe?</em></p><h2 id="🧭-deine-werkzeuge-zur-wirkung" tabindex="-1">🧭 Deine Werkzeuge zur Wirkung <a class="header-anchor" href="#🧭-deine-werkzeuge-zur-wirkung" aria-label="Permalink to &quot;🧭 Deine Werkzeuge zur Wirkung&quot;">​</a></h2><p>Dieser Abschnitt liefert keine To-do-Liste – sondern <strong>vier sofort nutzbare Module</strong>, die dich beim Übergang vom Verstehen zum Handeln unterstützen:</p><ul><li><p>🧰 <em>Kapitel 9.1 Handlungskompass</em><br> Für Einzelne, Multiplikator:innen und Entscheidungsträger:innen – mit konkreten Handlungsansätzen.</p></li><li><p>🪴 <em>Kapitel 9.2 Widerstand im Alltag</em><br> Sieben Hebelpunkte für Menschen ohne Amt oder Mandat – strukturell, direkt, wiederholbar.</p></li><li><p>👥 <em>Kapitel 9.3 Alltag &amp; Verantwortung</em><br> Szenarien aus typischen Rollen: Eltern, Lehrkräfte, Stadtbewohner:innen, Rentner:innen u.v.m.</p></li><li><p>🧾 <em>Kapitel9.4 Handlungsraster</em><br> Maßnahmen nach Lebensbereich, Aufwand und Wirkung – ideal für Teams, Unterricht oder Workshops.</p></li></ul><p>Zusätzlich:<br> 🧱 <em>Kapitel 9.5 Einwände &amp; Zweifel</em><br> Kritikpunkte, Gegenargumente – als Resonanzraum, nicht als Abwehr.</p><h2 id="💡-warum-dieser-teil-wichtig-ist" tabindex="-1">💡 Warum dieser Teil wichtig ist <a class="header-anchor" href="#💡-warum-dieser-teil-wichtig-ist" aria-label="Permalink to &quot;💡 Warum dieser Teil wichtig ist&quot;">​</a></h2><blockquote><p>Analyse allein verändert nichts.<br> Handlung braucht Mut – und Kontext.</p></blockquote><p>Dieser Abschnitt will dich nicht belehren – sondern ausstatten.<br> Er bietet:</p><ul><li>Orientierung in komplexer Verantwortung</li><li>Werkzeuge für den Einstieg</li><li>Struktur für strategisches Handeln</li></ul><blockquote><p>„Was jetzt?“ ist keine Schlussfrage.<br> Sondern der Anfang einer Bewegung – <strong>weg von der Schlinge, hin zur Wirkung.</strong></p></blockquote>',15)),l(n)])}const k=t(o,[["render",u]]);export{b as __pageData,k as default};
