import{_ as r,C as t,c as i,o as a,ag as s,G as o}from"./chunks/framework.S-Qvb3wi.js";const m=JSON.parse('{"title":"8.2 Widerstand im Alltag","description":"7 Hebelpunkte für Menschen ohne Macht – damit Analyse nicht in Ohnmacht mündet. Ein Werkzeugkasten für systemische Irritation im Alltag.\\n","frontmatter":{"title":"8.2 Widerstand im Alltag","description":"7 Hebelpunkte für Menschen ohne Macht – damit Analyse nicht in Ohnmacht mündet. Ein Werkzeugkasten für systemische Irritation im Alltag.\\n"},"headers":[],"relativePath":"08_was-jetzt/02_widerstand-im-alltag.md","filePath":"08_was-jetzt/02_widerstand-im-alltag.md","lastUpdated":1753279356000}'),l={name:"08_was-jetzt/02_widerstand-im-alltag.md"};function h(u,e,g,d,c,p){const n=t("Footer");return a(),i("div",null,[e[0]||(e[0]=s('<h1 id="_8-2-widerstand-im-alltag" tabindex="-1">8.2 Widerstand im Alltag <a class="header-anchor" href="#_8-2-widerstand-im-alltag" aria-label="Permalink to &quot;8.2 Widerstand im Alltag&quot;">​</a></h1><blockquote><p>&quot;Du brauchst keinen Plan für die ganze Welt. Nur einen Hebel, der greift, wo andere wegschauen.&quot;</p></blockquote><h2 id="🧭-wenn-analyse-nicht-mehr-reicht" tabindex="-1">🧭 Wenn Analyse nicht mehr reicht <a class="header-anchor" href="#🧭-wenn-analyse-nicht-mehr-reicht" aria-label="Permalink to &quot;🧭 Wenn Analyse nicht mehr reicht&quot;">​</a></h2><p>Du kennst die Strukturen. Du erkennst, wo das System schützt – und wo es versagt.<br> Jetzt brauchst du keinen Appell. Kein Ideal. Sondern einen konkreten Eingriffspunkt. Handlung, die stört. Und Wirkung zeigt.</p><h2 id="🪴-1-systemkeile-statt-spartipps" tabindex="-1">🪴 1. Systemkeile statt Spartipps <a class="header-anchor" href="#🪴-1-systemkeile-statt-spartipps" aria-label="Permalink to &quot;🪴 1. Systemkeile statt Spartipps&quot;">​</a></h2><p>📍 <strong>Wer?</strong> Elternteil, Mieter:in, Angestellte:r<br> 📍 <strong>Wo?</strong> Kantine, Elternbeirat, Hausverwaltung<br> 📍 <strong>Wie?</strong> Antrag, Gespräch, Eskalation</p><blockquote><p>&quot;Warum steht auf dem Speiseplan keine CO₂-Kennzeichnung?&quot;<br> &quot;Wieso setzen wir auf Gas – obwohl Wärmepumpen gefördert werden?&quot;<br> &quot;Unser Chef fährt einen Verbrenner – aber wir sollen Papier sparen?&quot;</p></blockquote><p>👉 <strong>Ziel:</strong> Die versteckte Klimawirkung im Alltag sichtbar machen – und kollektiv verhandelbar.</p><p><strong>Beispiel:</strong> In einem Kita-Elternbeirat kann eine Anfrage zur Energieversorgung des Gebäudes Diskussionen über Wärmedämmung und kommunale Fördermittel auslösen.</p><h2 id="🧱-2-burokratie-hacken" tabindex="-1">🧱 2. Bürokratie hacken <a class="header-anchor" href="#🧱-2-burokratie-hacken" aria-label="Permalink to &quot;🧱 2. Bürokratie hacken&quot;">​</a></h2><p>📍 <strong>Wer?</strong> Bürger:in, Schüler:in, Journalist:in<br> 📍 <strong>Wo?</strong> Stadtverwaltung, Schule, Gemeinderat<br> 📍 <strong>Wie?</strong> IFG-Antrag, Ratsanfrage, Bürger:innenbrief</p><blockquote><p>&quot;Wie viele Dienstflüge hat die Verwaltung im letzten Jahr gemacht?&quot;<br> &quot;Welche Firmen haben den aktuellen Energiebericht mitgeschrieben?&quot;<br> &quot;Welche Lobbyist:innen hatten Zugang zum Klimaplan unserer Stadt?&quot;</p></blockquote><p>👉 <strong>Ziel:</strong> Die verwaltete Klimakrise aus der Aktenablage holen – und ins öffentliche Licht stellen.</p><p><strong>Hinweis:</strong> Das Informationsfreiheitsgesetz (IFG) ermöglicht es jeder Person, Verwaltungsdokumente einzusehen – ein unterschätzter Hebel.</p><h2 id="🎤-3-sprache-storen" tabindex="-1">🎤 3. Sprache stören <a class="header-anchor" href="#🎤-3-sprache-storen" aria-label="Permalink to &quot;🎤 3. Sprache stören&quot;">​</a></h2><p>📍 <strong>Wer?</strong> Vereinsmitglied, Podiumsgast, Elternsprecher:in<br> 📍 <strong>Wo?</strong> Teams-Meeting, Abendveranstaltung, Leserbrief<br> 📍 <strong>Wie?</strong> Nachfrage, Begriff zurückweisen, Frame entlarven</p><blockquote><p>&quot;Technologieoffen? Offen für was – für zehn weitere Jahre fossiles Zögern?&quot;<br> &quot;‚Wirtschaftlich machbar‘ – für wen genau?&quot;<br> &quot;Wenn alle von ‚sozial verträglich‘ reden – wer bestimmt das eigentlich?&quot;</p></blockquote><p>👉 <strong>Ziel:</strong> Die beruhigende Sprache des Weiter-so aufbrechen – durch gezielte Irritation.</p><p><strong>Tipp:</strong> Oft reicht ein kurzer Einwurf, um eingefahrene Begriffe zu hinterfragen – und das Gespräch in Bewegung zu bringen.</p><h2 id="🧘-4-gefuhle-zeigen-–-als-widerstand" tabindex="-1">🧘 4. Gefühle zeigen – als Widerstand <a class="header-anchor" href="#🧘-4-gefuhle-zeigen-–-als-widerstand" aria-label="Permalink to &quot;🧘 4. Gefühle zeigen – als Widerstand&quot;">​</a></h2><p>📍 <strong>Wer?</strong> Vereinsvorstand, Teammitglied, Angehörige:r<br> 📍 <strong>Wo?</strong> Mitgliederversammlung, Schulforum, Betriebsrunde<br> 📍 <strong>Wie?</strong> Stellungnahme, offene Rede, gezieltes Schweigen</p><blockquote><p>&quot;Ich halte es nicht mehr aus, wie wir feiern – während draußen 1,5 °C überschritten werden.&quot;<br> &quot;Ich will nicht mehr über ‚Machbarkeit‘ reden – wenn meine Kinder ihre Zukunft verlieren.&quot;</p></blockquote><p>👉 <strong>Ziel:</strong> Gefühle politisieren – und Ohnmacht in Handlung umleiten.</p><p><strong>Beispiel:</strong> Schweigen in einem Meeting kann stärker irritieren als zehn Argumente – wenn es bewusst platziert wird.</p><h2 id="🔁-5-wiederholen-was-unterbrochen-wird" tabindex="-1">🔁 5. Wiederholen, was unterbrochen wird <a class="header-anchor" href="#🔁-5-wiederholen-was-unterbrochen-wird" aria-label="Permalink to &quot;🔁 5. Wiederholen, was unterbrochen wird&quot;">​</a></h2><p>📍 <strong>Wer?</strong> Aktivist:in, Schüler:in, Leser:in<br> 📍 <strong>Wo?</strong> Social Media, Vorträge, Artikel<br> 📍 <strong>Wie?</strong> Wiederholung, Faktenblock, Visualisierung</p><blockquote><p>&quot;Seit 1979 wissen wir, was kommt.&quot;<br> &quot;1,5 °C ist kein Kompromiss – es ist ein Kipppunkt.&quot;<br> &quot;Emissionslücken sind keine Naturgewalt – sie sind politische Entscheidungen.&quot;</p></blockquote><p>👉 <strong>Ziel:</strong> Das systemische Vergessen sabotieren – durch rhythmische Erinnerung.</p><p><strong>Tipp:</strong> Wiederholung erzeugt Widerstandskraft – vor allem, wenn sie mit klaren Bildern oder Zahlen verknüpft wird.</p><h2 id="🤝-6-rolle-wechseln-–-strategisch" tabindex="-1">🤝 6. Rolle wechseln – strategisch <a class="header-anchor" href="#🤝-6-rolle-wechseln-–-strategisch" aria-label="Permalink to &quot;🤝 6. Rolle wechseln – strategisch&quot;">​</a></h2><p>📍 <strong>Wer?</strong> Vereinsmitglied, Mieter:in, Elternteil, Angestellte:r<br> 📍 <strong>Wo?</strong> Sportverein, Betrieb, Elternabend, Gemeinderat<br> 📍 <strong>Wie?</strong> Satzungsantrag, Mail, Vorschlag, Abstimmung</p><blockquote><p>&quot;Ich beantrage, dass wir keine Sponsorengelder mehr von Ölkonzernen annehmen.&quot;<br> &quot;Unser Wohnblock soll Teil einer Energiegenossenschaft werden.&quot;<br> &quot;Ich fordere eine Klimaprojektwoche – mit echten Kontroversen.&quot;</p></blockquote><p>👉 <strong>Ziel:</strong> Räume und Rollen umfunktionieren – von Verwaltung zu Hebel.</p><p><strong>Beispiel:</strong> Eine Satzungsänderung im Kleingartenverein kann die Grundlage schaffen für gemeinschaftliche Solaranlagen.</p><h2 id="🔓-7-allianzen-suchen-–-außerhalb-der-blase" tabindex="-1">🔓 7. Allianzen suchen – außerhalb der Blase <a class="header-anchor" href="#🔓-7-allianzen-suchen-–-außerhalb-der-blase" aria-label="Permalink to &quot;🔓 7. Allianzen suchen – außerhalb der Blase&quot;">​</a></h2><p>📍 <strong>Wer?</strong> Kirchenmitglied, Feuerwehrfrau, Genossenschaftsgründer:in<br> 📍 <strong>Wo?</strong> Gemeindeversammlung, Vereinsfest, Nachbarschaft<br> 📍 <strong>Wie?</strong> Gespräch, Einladung, Mit-Antrag</p><blockquote><p>&quot;Warum sprechen wir hier über Nächstenliebe – aber nicht über die Generationen, die wir verheizen?&quot;<br> &quot;Könnten wir gemeinsam eine Solaranlage aufs Gerätehaus setzen?&quot;<br> &quot;Lass uns nach Fördermitteln suchen – bevor die Bank wieder Nein sagt.&quot;</p></blockquote><p>👉 <strong>Ziel:</strong> Unwahrscheinliche Koalitionen stiften – gegen die routinierte Spaltung.</p><p><strong>Denkanstoß:</strong> Die Feuerwehr, die Kirchengemeinde oder der Kleingartenverein können unerwartete Bündnispartner sein – wenn die Frage stimmt.</p><h2 id="⚡-fazit" tabindex="-1">⚡ Fazit <a class="header-anchor" href="#⚡-fazit" aria-label="Permalink to &quot;⚡ Fazit&quot;">​</a></h2><blockquote><p><strong>Du brauchst keine Mehrheit. Keine Macht. Kein Mandat.</strong><br> Du brauchst einen Riss. Einen Hebel. Einen Moment, in dem du die Struktur berührst – und sie vibriert.</p></blockquote><p><strong>Störung ist kein Stil. Sie ist eine Strategie.</strong></p>',42)),o(n)])}const k=r(l,[["render",h]]);export{m as __pageData,k as default};
