import{_ as t,C as r,c as i,o as a,ag as l,G as s}from"./chunks/framework.S-Qvb3wi.js";const m=JSON.parse('{"title":"8.3 Alltag & Verantwortung","description":"Ob Lehrkraft, Elternteil, Stadtbewohner:in oder Angestellte:r – jede Rolle hat Hebel. Dieser Abschnitt zeigt typische Ansätze im Alltag – ergänzt um konkrete Szenarien, die wirklich stattfinden (können).\\n","frontmatter":{"title":"8.3 Alltag & Verantwortung","description":"Ob Lehrkraft, Elternteil, Stadtbewohner:in oder Angestellte:r – jede Rolle hat Hebel. Dieser Abschnitt zeigt typische Ansätze im Alltag – ergänzt um konkrete Szenarien, die wirklich stattfinden (können).\\n"},"headers":[],"relativePath":"08_was-jetzt/03_alltag-und-verantwortung.md","filePath":"08_was-jetzt/03_alltag-und-verantwortung.md","lastUpdated":1753279356000}'),o={name:"08_was-jetzt/03_alltag-und-verantwortung.md"};function g(h,e,u,d,c,b){const n=r("Footer");return a(),i("div",null,[e[0]||(e[0]=l('<h1 id="_8-3-alltag-verantwortung-–-typische-rollen-echte-hebel" tabindex="-1">8.3 Alltag &amp; Verantwortung – typische Rollen, echte Hebel <a class="header-anchor" href="#_8-3-alltag-verantwortung-–-typische-rollen-echte-hebel" aria-label="Permalink to &quot;8.3 Alltag &amp; Verantwortung – typische Rollen, echte Hebel&quot;">​</a></h1><blockquote><p>Systemisches Wissen allein reicht nicht.<br> Was fehlt, ist die Brücke: vom Verstehen zum Tun.<br> Dieser Abschnitt zeigt: Deine Rolle begrenzt nicht, was du darfst –<br> aber sie prägt, <em>wo</em> du anfangen kannst.</p></blockquote><h2 id="wie-lesen-🧭" tabindex="-1">Wie lesen? 🧭 <a class="header-anchor" href="#wie-lesen-🧭" aria-label="Permalink to &quot;Wie lesen? 🧭&quot;">​</a></h2><p>Jede Rolle hat ihre eigenen Spielräume – und Grenzen.<br> Deshalb findest du hier:</p><ul><li><strong>Konkrete Hebel</strong>: typische Handlungsansätze im Alltag.</li><li><strong>Echte Szenarien</strong>: was Menschen bereits tun – oder tun könnten.</li><li><strong>Einladungen statt Anweisungen</strong>: Du musst nicht alles tun. Aber du kannst beginnen.</li></ul><h2 id="🎒-schuler-in-oder-student-in-–-lernen-als-buhne-der-wirkung" tabindex="-1">🎒 Schüler:in oder Student:in – Lernen als Bühne der Wirkung <a class="header-anchor" href="#🎒-schuler-in-oder-student-in-–-lernen-als-buhne-der-wirkung" aria-label="Permalink to &quot;🎒 Schüler:in oder Student:in – Lernen als Bühne der Wirkung&quot;">​</a></h2><ul><li><strong>Sichtbar machen:</strong> Ausstellungen, Projektwochen, AGs – Wissen gehört auf die Bühne.</li><li><strong>Mitgestalten:</strong> Hochschulpolitik, SV-Arbeit, Beteiligung einfordern.</li><li><strong>Störungen zulassen:</strong> Schweigen ist bequem. Du darfst es brechen.</li><li><strong>Wissen teilen:</strong> Peer-Learning, Social Media, aufbereitete Recherchen.</li></ul><p>🧭 <strong>Szenario:</strong><br> Ein Biokurs analysiert CO₂-Bilanzen von Alltagsprodukten. Die Ausstellung im Schulflur stößt Gespräche an – auch in anderen Klassen.</p><blockquote><p>Du brauchst kein Amt. Nur Haltung. Und einen Anfang.</p></blockquote><h2 id="📚-lehrkraft-–-vermitteln-heißt-auch-haltung-formen" tabindex="-1">📚 Lehrkraft – Vermitteln heißt auch Haltung formen <a class="header-anchor" href="#📚-lehrkraft-–-vermitteln-heißt-auch-haltung-formen" aria-label="Permalink to &quot;📚 Lehrkraft – Vermitteln heißt auch Haltung formen&quot;">​</a></h2><ul><li><strong>Kontexte aufzeigen:</strong> Klima ist kein neues Fach – sondern ein neuer Blick.</li><li><strong>Fächerübergreifend denken:</strong> Ob Deutsch, Religion oder Mathe – überall lassen sich Zusammenhänge zeigen.</li><li><strong>Didaktik erweitern:</strong> Lernziele neu denken: Wissen <em>und</em> Wirkung.</li><li><strong>Schulraum gemeinsam gestalten:</strong> Mit Kollegium, Eltern, Schulleitung.</li></ul><p>🧭 <strong>Szenario:</strong><br> Das Wahlmodul &quot;Klimaspuren&quot; kombiniert eine CO₂-Analyse der Schule mit einem Antrag an die Schulkonferenz – inklusive konkreter Vorschläge.</p><blockquote><p>Du prägst nicht nur Inhalte. Sondern auch Haltung.</p></blockquote><h2 id="🏙️-stadtbewohner-in-–-raum-beanspruchen-wandel-mitgestalten" tabindex="-1">🏙️ Stadtbewohner:in – Raum beanspruchen, Wandel mitgestalten <a class="header-anchor" href="#🏙️-stadtbewohner-in-–-raum-beanspruchen-wandel-mitgestalten" aria-label="Permalink to &quot;🏙️ Stadtbewohner:in – Raum beanspruchen, Wandel mitgestalten&quot;">​</a></h2><ul><li><strong>Orte politisieren:</strong> Gehwege, Haltestellen, Parks – der öffentliche Raum gehört allen.</li><li><strong>Mobilität hinterfragen:</strong> Weniger Auto, mehr Präsenz – zu Fuß, mit Rad oder ÖPNV.</li><li><strong>Beteiligung einfordern:</strong> Bürgerhaushalte, Anfragen, Beteiligungsplattformen.</li><li><strong>Nachbarschaft stärken:</strong> Gemeinsame Anliegen verbinden – auch ohne große Initiative.</li></ul><p>🧭 <strong>Szenario:</strong><br> Ein Gespräch im Hausflur führt zu einem offenen Brief ans Stadtteilbüro: „Warum fehlen hier sichere Fahrradstellplätze?“</p><blockquote><p>Stadt ist nicht Kulisse. Sie ist Hebel.</p></blockquote><h2 id="👨‍👩‍👧‍👦-elternteil-–-vorbild-sein-ohne-zu-uberfordern" tabindex="-1">👨‍👩‍👧‍👦 Elternteil – Vorbild sein, ohne zu überfordern <a class="header-anchor" href="#👨‍👩‍👧‍👦-elternteil-–-vorbild-sein-ohne-zu-uberfordern" aria-label="Permalink to &quot;👨‍👩‍👧‍👦 Elternteil – Vorbild sein, ohne zu überfordern&quot;">​</a></h2><ul><li><strong>Widersprüche besprechbar machen:</strong> Kinder verstehen mehr, wenn wir ehrlich sind.</li><li><strong>Institutionen mitdenken:</strong> Kita-Weg, Schulessen, Ausflüge – Alltag ist politisch.</li><li><strong>Verbinden statt belehren:</strong> Austausch mit anderen Eltern auf Augenhöhe.</li><li><strong>Stärken statt überfordern:</strong> Verantwortung kindgerecht denken.</li></ul><p>🧭 <strong>Szenario:</strong><br> Drei Familien starten einen &quot;Laufbus&quot; zur Kita. Aus dem Ritual entsteht eine Diskussion über Verkehrssicherheit im Viertel.</p><blockquote><p>Kinder brauchen keine Klima-Held:innen. Sondern Haltung zum Mitwachsen.</p></blockquote><h2 id="🧑‍💼-angestellte-r-–-wandel-am-arbeitsplatz-anstoßen" tabindex="-1">🧑‍💼 Angestellte:r – Wandel am Arbeitsplatz anstoßen <a class="header-anchor" href="#🧑‍💼-angestellte-r-–-wandel-am-arbeitsplatz-anstoßen" aria-label="Permalink to &quot;🧑‍💼 Angestellte:r – Wandel am Arbeitsplatz anstoßen&quot;">​</a></h2><ul><li><strong>Arbeitsort ganzheitlich sehen:</strong> Kantine, Dienstreisen, IT, Lieferketten.</li><li><strong>Ansprechen statt anklagen:</strong> Nachhaltigkeit beginnt im Gespräch.</li><li><strong>Initiative zeigen:</strong> Intranet-Post, AG starten, CO₂-Bilanz vorschlagen.</li><li><strong>Mitstreiter:innen gewinnen:</strong> Kolleg:innen und Betriebsrat einbinden.</li></ul><p>🧭 <strong>Szenario:</strong><br> Eine informelle „Checkliste für grüne Dienstreisen“ wird intern geteilt – und später offiziell übernommen.</p><blockquote><p>Nicht jede:r kann kündigen. Aber viele können etwas sagen.</p></blockquote><h2 id="🛠️-praktisch-tatige-–-klima-ist-praxis" tabindex="-1">🛠️ Praktisch Tätige – Klima ist Praxis <a class="header-anchor" href="#🛠️-praktisch-tatige-–-klima-ist-praxis" aria-label="Permalink to &quot;🛠️ Praktisch Tätige – Klima ist Praxis&quot;">​</a></h2><ul><li><strong>Material bewusst wählen:</strong> Ob Bau, Technik, Pflege – wie gearbeitet wird, macht den Unterschied.</li><li><strong>Kund:innen beraten:</strong> Wissen weitergeben, Alternativen zeigen.</li><li><strong>Routinen überdenken:</strong> Reparieren statt ersetzen, Ressourcen effizient nutzen.</li><li><strong>Branchenweit wirken:</strong> In Kammern, Fortbildungen, Fachnetzwerken.</li></ul><p>🧭 <strong>Szenario:</strong><br> Ein Malerbetrieb testet Lacke auf Pflanzenbasis – spart CO₂ und senkt langfristig die Kosten.</p><blockquote><p>Klima ist kein Elitenthema. Es ist Handwerk.</p></blockquote><h2 id="🤝-sorge-pflege-und-betreuungstatige-–-verantwortung-in-abhangigkeit" tabindex="-1">🤝 Sorge-, Pflege- und Betreuungstätige – Verantwortung in Abhängigkeit <a class="header-anchor" href="#🤝-sorge-pflege-und-betreuungstatige-–-verantwortung-in-abhangigkeit" aria-label="Permalink to &quot;🤝 Sorge-, Pflege- und Betreuungstätige – Verantwortung in Abhängigkeit&quot;">​</a></h2><ul><li><strong>Beziehungen stärken:</strong> Zwischen Generationen, Klient:innen, Kolleg:innen.</li><li><strong>Klimathemen einbringen:</strong> Ernährung, Energie, Mobilität sind Alltag.</li><li><strong>Sprechräume schaffen:</strong> Auch leise Stimmen brauchen Gehör.</li><li><strong>Haltung zeigen:</strong> Nicht parteilich – aber politisch denkend.</li></ul><p>🧭 <strong>Szenario:</strong><br> Eltern in einem Wohnheim dokumentieren die Heizlüfter-Probleme. Daraus entsteht ein Förderantrag für energetische Sanierung.</p><blockquote><p>Wer sorgt, kennt Abhängigkeit. Und Verantwortung.</p></blockquote><h2 id="🕰️-ruhestand-–-erfahrung-wird-handlung" tabindex="-1">🕰️ Ruhestand – Erfahrung wird Handlung <a class="header-anchor" href="#🕰️-ruhestand-–-erfahrung-wird-handlung" aria-label="Permalink to &quot;🕰️ Ruhestand – Erfahrung wird Handlung&quot;">​</a></h2><ul><li><strong>Zeit nutzen:</strong> Briefe schreiben, demonstrieren, Enkel begleiten – ohne Aktivismusdruck.</li><li><strong>Geld bewusst lenken:</strong> Konto, Spenden, Anlagen – wo wirkt dein Kapital?</li><li><strong>Wissen weitergeben:</strong> Zuhören, erzählen, ermutigen.</li><li><strong>Kritisch bleiben:</strong> Altersmilde ist keine Pflicht.</li></ul><p>🧭 <strong>Szenario:</strong><br> Eine Senior:innengruppe organisiert eine Klima-Lesung im Nachbarschaftscafé. Danach: ein Brief an den Stadtrat mit klaren Forderungen.</p><blockquote><p>Wandel braucht Rückenstärkung – auch nach der Erwerbsphase.</p></blockquote><h2 id="😶-ich-bin-unsicher" tabindex="-1">😶 Ich bin unsicher <a class="header-anchor" href="#😶-ich-bin-unsicher" aria-label="Permalink to &quot;😶 Ich bin unsicher&quot;">​</a></h2><ul><li><strong>Du bist nicht allein:</strong> Viele zögern – nicht aus Gleichgültigkeit, sondern aus Respekt.</li><li><strong>Perfektion ist kein Maßstab:</strong> Nicht der „richtige“, sondern der nächste Schritt zählt.</li><li><strong>Ehrlich fragen:</strong> Wo bist du Teil des Problems – oder der Lösung?</li></ul><p>🧭 <strong>Szenario:</strong><br> Du liest diesen Abschnitt. Markierst einen Satz. Schickst ihn weiter.</p><blockquote><p>Ohnmacht hält uns fest.<br> Bewegung beginnt im Kleinen.</p></blockquote><h2 id="🧰-werkzeuge-fur-deinen-kontext" tabindex="-1">🧰 Werkzeuge für deinen Kontext <a class="header-anchor" href="#🧰-werkzeuge-fur-deinen-kontext" aria-label="Permalink to &quot;🧰 Werkzeuge für deinen Kontext&quot;">​</a></h2><blockquote><p>Kein Tool wirkt überall. Aber viele wirken dort, wo sie gebraucht werden.</p></blockquote><table tabindex="0"><thead><tr><th><strong>Rolle / Kontext</strong></th><th><strong>Nützliche Tools &amp; Plattformen</strong></th></tr></thead><tbody><tr><td><strong>Lehrkraft / Bildung</strong></td><td><a href="https://www.klimafakten.de" target="_blank" rel="noreferrer">Klimafakten.de</a>, <a href="https://www.bzga.de" target="_blank" rel="noreferrer">BZgA Unterrichtsmodule</a>, <a href="https://www.greenpeace.de/bildung" target="_blank" rel="noreferrer">Greenpeace Bildungsservice</a></td></tr><tr><td><strong>Stadtbewohner:in</strong></td><td><a href="https://www.openpetition.de" target="_blank" rel="noreferrer">OpenPetition</a>, <a href="https://www.fixmystreet.org" target="_blank" rel="noreferrer">FixMyStreet</a>, Beteiligungsplattform der eigenen Kommune</td></tr><tr><td><strong>Elternteil</strong></td><td><a href="https://www.vcd.org/themen/schulweg/laufbus" target="_blank" rel="noreferrer">VCD Laufbus-Checkliste</a>, Austauschvorlagen für Elternabende, Kita-Verpflegungskonzepte</td></tr><tr><td><strong>Angestellte / Team</strong></td><td><a href="https://uba.co2-rechner.de/de_DE/office" target="_blank" rel="noreferrer">CO₂-Rechner Büro</a>, Klima-AG-Vorlagen, <a href="https://www.thegreenwebfoundation.org" target="_blank" rel="noreferrer">The Green Web Foundation</a></td></tr><tr><td><strong>Zivilgesellschaft / Aktivismus</strong></td><td><a href="https://climateframes.org" target="_blank" rel="noreferrer">ClimateFrames</a>, <a href="https://skillshare.eu.org" target="_blank" rel="noreferrer">Skillshare-Kollektiv</a>, <a href="https://klimawatch.de" target="_blank" rel="noreferrer">Klimawatch Demo-Checklisten</a></td></tr></tbody></table><h2 id="🧾-quellenbasis" tabindex="-1">🧾 Quellenbasis <a class="header-anchor" href="#🧾-quellenbasis" aria-label="Permalink to &quot;🧾 Quellenbasis&quot;">​</a></h2><ul><li>Eigene Recherchen zu Beteiligungsformaten und Werkzeugplattformen</li><li>Erfahrungen und Fallbeispiele aus kommunalen Klimaprojekten</li><li>Bildungs- und Beteiligungshandreichungen u. a. von BZgA, VCD, Greenpeace, UBA</li></ul>',46)),s(n)])}const f=t(o,[["render",g]]);export{m as __pageData,f as default};
