import{_ as i,C as t,c as r,o as s,ag as l,G as o}from"./chunks/framework.S-Qvb3wi.js";const k=JSON.parse('{"title":"Kapitel 9 – Rollen im System","description":"Wie unterschiedliche Akteur:innen im System wirken – und wo ihre spezifischen Möglichkeiten zur Störung der strukturellen Verzögerung liegen.\\n","frontmatter":{"title":"Kapitel 9 – Rollen im System","description":"Wie unterschiedliche Akteur:innen im System wirken – und wo ihre spezifischen Möglichkeiten zur Störung der strukturellen Verzögerung liegen.\\n"},"headers":[],"relativePath":"09_rollen/00_einleitung.md","filePath":"09_rollen/00_einleitung.md","lastUpdated":1753279356000}'),a={name:"09_rollen/00_einleitung.md"};function u(d,e,h,g,c,m){const n=t("Footer");return s(),r("div",null,[e[0]||(e[0]=l('<h1 id="kapitel-9-–-rollen-im-system" tabindex="-1">Kapitel 9 – Rollen im System <a class="header-anchor" href="#kapitel-9-–-rollen-im-system" aria-label="Permalink to &quot;Kapitel 9 – Rollen im System&quot;">​</a></h1><p>Verzögerung ist kein abstraktes Phänomen.<br> Sie wirkt durch Personen, Positionen und Routinen – <strong>und lässt sich nur dort stören, wo sie konkret gemacht wird.</strong></p><p>Dieses Kapitel richtet den Blick nicht auf „die Politik“ oder „die Gesellschaft“ im Allgemeinen –<br> sondern auf <strong>konkrete Rollen im System</strong>: Lehrkräfte, Verwaltungsakteur:innen, Journalist:innen, Aktivist:innen, Jurist:innen, Kulturschaffende und viele mehr.</p><h2 id="🎯-worum-es-in-diesem-kapitel-geht" tabindex="-1">🎯 Worum es in diesem Kapitel geht <a class="header-anchor" href="#🎯-worum-es-in-diesem-kapitel-geht" aria-label="Permalink to &quot;🎯 Worum es in diesem Kapitel geht&quot;">​</a></h2><p>Es geht um <strong>konkrete Eingriffspunkte</strong> – bezogen auf das, was Menschen <em>beruflich, institutionell oder zivil</em> tun (oder lassen):</p><ul><li>Wie sieht <strong>Verzögerung im jeweiligen Handlungsfeld aus</strong>?</li><li>Welche Routinen, Phrasen, Verfahren wirken als stille Blockade?</li><li>Und wo liegen die <strong>spezifischen Störpotenziale</strong> jeder Rolle?</li></ul><h2 id="🕵️-vom-appell-zur-analyse" tabindex="-1">🕵️ Vom Appell zur Analyse <a class="header-anchor" href="#🕵️-vom-appell-zur-analyse" aria-label="Permalink to &quot;🕵️ Vom Appell zur Analyse&quot;">​</a></h2><p>Hier geht es <strong>nicht um „Was kann ich tun?“</strong><br> Sondern um die schärfere Frage:</p><blockquote><p><strong>„Wo genau bin ich Teil des Systems – und wo kann ich es stören?“</strong></p></blockquote><p>Jede Rolle bringt andere Hebel mit – und andere Muster, die sie ungewollt stabilisieren kann.</p><h2 id="⚡-ziel-dieses-kapitels" tabindex="-1">⚡ Ziel dieses Kapitels <a class="header-anchor" href="#⚡-ziel-dieses-kapitels" aria-label="Permalink to &quot;⚡ Ziel dieses Kapitels&quot;">​</a></h2><ul><li>Verzögerung sichtbar machen – dort, wo sie oft nicht so heißt</li><li>Handlungsmacht präzisieren – jenseits von Konsum oder Protest</li><li>Sprache und Struktur <strong>von innen her irritieren</strong></li></ul><blockquote><p><strong>„Nicht alle sind schuld – aber alle sind irgendwo wirksam.“</strong></p></blockquote><p>Dieses Kapitel will zeigen, <strong>wie diese Wirksamkeit gegen systemische Trägheit gerichtet werden kann.</strong><br> Gezielt. Konkreter. Störend.</p>',14)),o(n)])}const _=i(a,[["render",u]]);export{k as __pageData,_ as default};
