import{_ as t,C as n,c as i,o as a,ag as s,G as l}from"./chunks/framework.S-Qvb3wi.js";const k=JSON.parse('{"title":"9.1 Lehrkräfte","description":"","frontmatter":{"title":"9.1 Lehrkräfte"},"headers":[],"relativePath":"09_rollen/01_lehrkraefte.md","filePath":"09_rollen/01_lehrkraefte.md","lastUpdated":1753279356000}'),h={name:"09_rollen/01_lehrkraefte.md"};function o(u,e,d,c,g,m){const r=n("Footer");return a(),i("div",null,[e[0]||(e[0]=s('<h1 id="_9-1-lehrkrafte-–-„verzogerung-unterrichten" tabindex="-1">9.1 Lehrkräfte – „Verzögerung unterrichten“ <a class="header-anchor" href="#_9-1-lehrkrafte-–-„verzogerung-unterrichten" aria-label="Permalink to &quot;9.1 Lehrkräfte – „Verzögerung unterrichten“&quot;">​</a></h1><h2 id="🧑‍🏫-systemische-position" tabindex="-1">🧑‍🏫 Systemische Position <a class="header-anchor" href="#🧑‍🏫-systemische-position" aria-label="Permalink to &quot;🧑‍🏫 Systemische Position&quot;">​</a></h2><p>Lehrkräfte formen Realitätsverständnis – durch Stoff, Sprache, Haltung, Auswahl.<br> Bildung kann stabilisieren – oder öffnen. Reproduzieren – oder stören.</p><p>Die Rolle von Bildung im Kontext der Klimakrise ist vielfach untersucht – etwa durch O’Brien (2012), Lange (2018), Göpel (2020) oder Kliemann &amp; Fischer (2022).<br> Der Fokus liegt zunehmend auf der Frage, wie Schule nicht nur informiert, sondern auch handlungsfähig macht – und was sie dabei strukturell verhindert.</p><h2 id="🕰️-verzogerung-im-bildungsraum" tabindex="-1">🕰️ Verzögerung im Bildungsraum <a class="header-anchor" href="#🕰️-verzogerung-im-bildungsraum" aria-label="Permalink to &quot;🕰️ Verzögerung im Bildungsraum&quot;">​</a></h2><ul><li>Klimakrise erscheint als Naturphänomen, nicht als Machtstruktur</li><li>Lehrpläne priorisieren „objektive Darstellung“ – meiden systemische Analyse</li><li>Handlung wird individualisiert („Was kannst DU tun?“), nicht politisiert</li><li>Fachcurricula trennen ökonomische, soziale und ökologische Logik</li><li>Politische Aushandlung wird entkontextualisiert – z. B. als „Kontroverse“</li></ul><p>Diese Muster lassen sich unabhängig von Schulform oder Fachrichtung beobachten – zeigen sich jedoch unterschiedlich ausgeprägt in Grundschule, Sekundarstufe oder Berufsschule.</p><h2 id="🔧-beispiel-1-„lehrbuch-storung" tabindex="-1">🔧 Beispiel 1: „Lehrbuch-Störung“ <a class="header-anchor" href="#🔧-beispiel-1-„lehrbuch-storung" aria-label="Permalink to &quot;🔧 Beispiel 1: „Lehrbuch-Störung“&quot;">​</a></h2><p><strong>Kontext:</strong> Geografiebuch, Klasse 9<br><strong>Typische Aufgabe:</strong> „Analysiere diese CO₂-Grafik und überlege, was du selbst tun kannst.“</p><h3 id="storende-erweiterung" tabindex="-1">Störende Erweiterung <a class="header-anchor" href="#storende-erweiterung" aria-label="Permalink to &quot;Störende Erweiterung&quot;">​</a></h3><p><strong>Arbeitsblatt:</strong> <em>„Was wird hier gesagt – und was verschwiegen?“</em></p><table tabindex="0"><thead><tr><th>Aussage</th><th>Frage zur Systemlogik</th></tr></thead><tbody><tr><td>„Deutschland hat CO₂ reduziert.“</td><td>Wurde Produktion einfach ins Ausland verlagert?</td></tr><tr><td>„Die Politik hat Maßnahmen beschlossen.“</td><td>Welche wurden verzögert oder verwässert – und durch wen?</td></tr><tr><td>„Jede:r kann etwas beitragen.“</td><td>Wer verhindert strukturelle Veränderung aktiv?</td></tr></tbody></table><p><strong>Ziel:</strong><br> Vom <em>individuellen Verhalten</em> zur <em>systemischen Analyse</em> – ohne Schuldzuschreibung, aber mit Machtbewusstsein.</p><h2 id="🔧-beispiel-2-„systemtheater" tabindex="-1">🔧 Beispiel 2: „Systemtheater“ <a class="header-anchor" href="#🔧-beispiel-2-„systemtheater" aria-label="Permalink to &quot;🔧 Beispiel 2: „Systemtheater“&quot;">​</a></h2><p><strong>Kontext:</strong> Projektwoche, Thema „Zukunft &amp; Klima“<br><strong>Typischer Ablauf:</strong> Bastelprojekte, Visionen 2050</p><h3 id="alternative-aktion" tabindex="-1">Alternative Aktion <a class="header-anchor" href="#alternative-aktion" aria-label="Permalink to &quot;Alternative Aktion&quot;">​</a></h3><p><strong>Rollenspiel:</strong> <em>„Die Verzögerungskonferenz“</em></p><ul><li>Schüler:innen spielen reale Akteure (Ministerien, Lobby, NGO, Industrie)</li><li>Verwenden echte Politiker:innenzitate (z. B. aus Parteiprogrammen, Bundestagsprotokollen)</li><li>Diskutieren: <em>Was wird gesagt – was wird verhindert?</em></li></ul><p><strong>Reflexion danach:</strong><br> „Welche dieser Phrasen habt ihr schon in Talkshows, Schulbüchern oder Nachrichten gehört?“<br> „Was wird als realistisch bezeichnet – und was nicht? Warum?“</p><p><strong>Hinweis zur Umsetzung:</strong><br> Format eignet sich ab Klassenstufe 9, auch im Politik-, Ethik- oder Deutschunterricht. Rollen können vorbereitet oder gemeinsam entwickelt werden.</p><h2 id="📎-mikrointerventionen-fur-den-unterrichtsalltag" tabindex="-1">📎 Mikrointerventionen für den Unterrichtsalltag <a class="header-anchor" href="#📎-mikrointerventionen-fur-den-unterrichtsalltag" aria-label="Permalink to &quot;📎 Mikrointerventionen für den Unterrichtsalltag&quot;">​</a></h2><ul><li>Tafelbild: <em>„Was wird gesagt – was bleibt ungesagt?“</em><br> → Als Einstieg oder Nachbereitung politischer Debatten</li><li>Wochenfrage: <em>„Wer profitiert vom Nicht-Handeln?“</em><br> → Diskussion über Akteursinteressen jenseits moralischer Appelle</li><li>Rechercheauftrag: <em>„Wer hat wann welches Klimaversprechen gemacht – und gebrochen?“</em><br> → Anschlussfähig an MINT- oder gesellschaftswissenschaftlichen Unterricht</li></ul><h2 id="📊-didaktische-hinweise" tabindex="-1">📊 Didaktische Hinweise <a class="header-anchor" href="#📊-didaktische-hinweise" aria-label="Permalink to &quot;📊 Didaktische Hinweise&quot;">​</a></h2><ul><li>Formatvisualisierung kann durch einfache Icons, Arbeitsblätter oder Padlet-Boards ergänzt werden</li><li>Für Grundschule/Orientierungsstufe sind angepasste Formate mit Bildern, Rollenkarten oder vereinfachten Texten notwendig</li><li>Höhere Jahrgänge können zusätzlich mit Originalquellen (z. B. Bundestagsdrucksachen) arbeiten</li></ul><h2 id="💡-fazit" tabindex="-1">💡 Fazit <a class="header-anchor" href="#💡-fazit" aria-label="Permalink to &quot;💡 Fazit&quot;">​</a></h2><blockquote><p>Bildung, die sich neutral nennt, hilft dem Status quo.<br> Bildung, die stört, kann Zukunft schaffen.</p></blockquote><p>Lehrkräfte können keine Revolution machen.<br> Aber sie können <strong>Sprache sichtbar machen – und Verantwortung verschieben</strong>.<br> Genau das stört. Und genau da beginnt Handlung.</p><p><strong>Literaturhinweise (für weiterführende Leselisten):</strong></p><ul><li>O’Brien, K. (2012). Global Environmental Change and Human Security.</li><li>Göpel, M. (2020). Unsere Welt neu denken.</li><li>Lange, J.-M. (2018). Bildung für nachhaltige Entwicklung als transformative Bildung.</li><li>Kliemann, J. &amp; Fischer, D. (2022). Politische Bildung in der Klimakrise. In: GPJE Jahrbuch</li></ul>',29)),l(r)])}const p=t(h,[["render",o]]);export{k as __pageData,p as default};
