import{_ as r,C as n,c as t,o as a,ag as s,G as l}from"./chunks/framework.S-Qvb3wi.js";const f=JSON.parse('{"title":"9.2 Journalist:innen","description":"","frontmatter":{"title":"9.2 Journalist:innen"},"headers":[],"relativePath":"09_rollen/02_journalistinnen.md","filePath":"09_rollen/02_journalistinnen.md","lastUpdated":1753279356000}'),o={name:"09_rollen/02_journalistinnen.md"};function h(u,e,d,c,g,m){const i=n("Footer");return a(),t("div",null,[e[0]||(e[0]=s('<h1 id="_9-2-journalist-innen-–-„verzogerung-schreiben-lernen" tabindex="-1">9.2 Journalist:innen – „Verzögerung schreiben lernen“ <a class="header-anchor" href="#_9-2-journalist-innen-–-„verzogerung-schreiben-lernen" aria-label="Permalink to &quot;9.2 Journalist:innen – „Verzögerung schreiben lernen“&quot;">​</a></h1><h2 id="📰-systemische-position" tabindex="-1">📰 Systemische Position <a class="header-anchor" href="#📰-systemische-position" aria-label="Permalink to &quot;📰 Systemische Position&quot;">​</a></h2><p>Journalismus schafft Wirklichkeit – durch Auswahl, Ton, Sprache, Perspektive.<br> Verzögerung wird selten benannt – aber oft verstärkt: durch Frames, Balance-Reflexe, Strukturlosigkeit.</p><p><strong>Medienethisch relevant:</strong> Wenn sprachliche Neutralität systemische Blockade nicht benennt, wird sie zur stillen Komplizin. Die Verantwortung journalistischer Arbeit liegt nicht nur in der Berichterstattung – sondern in der Wahl des Narrativs.</p><h2 id="⏳-verzogerung-im-medienraum" tabindex="-1">⏳ Verzögerung im Medienraum <a class="header-anchor" href="#⏳-verzogerung-im-medienraum" aria-label="Permalink to &quot;⏳ Verzögerung im Medienraum&quot;">​</a></h2><ul><li>Konsens wird relativiert („Zwei Seiten“ trotz 97 % Wissenschaft)</li><li>Verantwortung wird personalisiert („Klimaminister XY versagt“)</li><li>Komplexe Strukturen werden vereinfacht – oder gar nicht erklärt</li><li>Politik und Wirtschaft dürfen „Zukunft“ versprechen – ohne Gegenwart zu verändern</li><li>Debattenlogik ersetzt Ursachenanalyse („Streit um Verbrennerverbot“ statt „CO₂-Budgets und Machtverhältnisse“)</li></ul><p>Diese Muster zeigen sich in öffentlich-rechtlichen wie privaten Medien, in Kommentarspalten ebenso wie in Nachrichtensendungen.</p><h2 id="🔧-beispiel-1-narrativ-check-vor-veroffentlichung" tabindex="-1">🔧 Beispiel 1: Narrativ-Check vor Veröffentlichung <a class="header-anchor" href="#🔧-beispiel-1-narrativ-check-vor-veroffentlichung" aria-label="Permalink to &quot;🔧 Beispiel 1: Narrativ-Check vor Veröffentlichung&quot;">​</a></h2><p><strong>Kontext:</strong> Artikelentwurf zur „grünen Transformation der Industrie“<br><strong>Standardzitate:</strong></p><ul><li>Ministerium: „Wasserstoff als Schlüssel für klimaneutrale Industrie“</li><li>Konzern: „Technologieoffenheit ist entscheidend“</li></ul><h3 id="storender-check-–-vor-publikation" tabindex="-1">Störender Check – vor Publikation <a class="header-anchor" href="#storender-check-–-vor-publikation" aria-label="Permalink to &quot;Störender Check – vor Publikation&quot;">​</a></h3><p><strong>Fragen zur Redaktionssitzung oder Einzelprüfung:</strong></p><ul><li>Wird hier Strukturkritik durch Techniksprache ersetzt?</li><li>Wurde die historische Linie geprüft? (Seit wann wird „Wasserstoff“ versprochen?)</li><li>Gibt es eine Quelle, die konkret benennt, <em>was unterlassen wurde</em>?</li><li>Welche Interessen stehen hinter den zitierten Aussagen?</li></ul><h3 id="alternativformulierungen" tabindex="-1">Alternativformulierungen <a class="header-anchor" href="#alternativformulierungen" aria-label="Permalink to &quot;Alternativformulierungen&quot;">​</a></h3><blockquote><p>Statt: <em>„Ein neues Gesetz soll die Industrie klimaneutral machen.“</em> Sag: <em>„Ein weiteres Gesetz verspricht Klimaneutralität – ohne Verbindlichkeit oder Sanktion.“</em></p></blockquote><blockquote><p>Statt: <em>„Technologieoffenheit als Schlüssel.“</em> Frag:&gt; <em>„Was wurde in der Vergangenheit durch Technologieoffenheit verschoben – und nicht entschieden?“</em></p></blockquote><h2 id="🔧-beispiel-2-„verzogerungshistorie-schreiben" tabindex="-1">🔧 Beispiel 2: „Verzögerungshistorie schreiben“ <a class="header-anchor" href="#🔧-beispiel-2-„verzogerungshistorie-schreiben" aria-label="Permalink to &quot;🔧 Beispiel 2: „Verzögerungshistorie schreiben“&quot;">​</a></h2><p><strong>Formatidee:</strong> <em>„Wie alles begann – und nicht entschieden wurde“</em></p><p><strong>Beispielthema:</strong> Wasserstoffstrategie</p><ul><li>Seit wann wird Wasserstoff als Lösung genannt? (z. B. 2006, 2011, 2019…)</li><li>Was wurde versprochen – und was umgesetzt?</li><li>Wer hat profitiert? Wer hat gebremst?</li></ul><h3 id="mogliche-umsetzungsformate" tabindex="-1">Mögliche Umsetzungsformate <a class="header-anchor" href="#mogliche-umsetzungsformate" aria-label="Permalink to &quot;Mögliche Umsetzungsformate&quot;">​</a></h3><ul><li>Zeitstrahl mit Originalzitaten</li><li>Interview mit Energiehistoriker:in</li><li>Analyse: <em>Wie viele Gesetze wurden „geplant“, aber nie verabschiedet?</em></li><li>Podcast-Serie: <em>„Klimapolitik auf Pause“</em></li></ul><p><strong>Ziel:</strong><br> Verzögerung als Prozess <strong>sichtbar machen – nicht nur Empörung referieren</strong>.</p><h2 id="🔧-beispiel-3-framing-sichtbar-machen" tabindex="-1">🔧 Beispiel 3: Framing sichtbar machen <a class="header-anchor" href="#🔧-beispiel-3-framing-sichtbar-machen" aria-label="Permalink to &quot;🔧 Beispiel 3: Framing sichtbar machen&quot;">​</a></h2><p><strong>Mini-Format:</strong> <em>„Was gesagt wurde – und was gemeint war“</em></p><table tabindex="0"><thead><tr><th>Originalzitat</th><th>Systemische Übersetzung</th></tr></thead><tbody><tr><td>„Wir brauchen Zeit für tragfähige Lösungen.“</td><td>„Wir wollen keine Maßnahmen mit echten Folgen.“</td></tr><tr><td>„Der Markt wird es richten.“</td><td>„Wir wollen politische Verantwortung vermeiden.“</td></tr><tr><td>„Wir müssen ausgewogen berichten.“</td><td>„Wir setzen Konsens und Lobby auf eine Stufe.“</td></tr></tbody></table><h3 id="einsatzmoglichkeiten" tabindex="-1">Einsatzmöglichkeiten <a class="header-anchor" href="#einsatzmoglichkeiten" aria-label="Permalink to &quot;Einsatzmöglichkeiten&quot;">​</a></h3><ul><li>Social-Media-Serie (z. B. als Kachelformat oder Story)</li><li>Mini-Kachel im Print oder Newsletter</li><li>Online-Rubrik: „Verzögerungszitat der Woche“</li><li>interne Redaktionsfortbildung: <em>„Sprachkritik als Klimajournalismus“</em></li></ul><h2 id="🛠-erganzende-praxisformate" tabindex="-1">🛠 Ergänzende Praxisformate <a class="header-anchor" href="#🛠-erganzende-praxisformate" aria-label="Permalink to &quot;🛠 Ergänzende Praxisformate&quot;">​</a></h2><ul><li><strong>Live-Analyse:</strong> Gemeinsames Lesen und Kommentieren eines politischen Statements</li><li><strong>Medienmonitoring:</strong> Schüler:innen oder Studierende dokumentieren typische Frames</li><li><strong>Interviewtraining:</strong> Wie fragt man „nach der Lücke“, nicht nur „nach der Meinung“?</li></ul><h2 id="📚-weiterbildungs-literaturhinweis" tabindex="-1">📚 Weiterbildungs- &amp; Literaturhinweis <a class="header-anchor" href="#📚-weiterbildungs-literaturhinweis" aria-label="Permalink to &quot;📚 Weiterbildungs- &amp; Literaturhinweis&quot;">​</a></h2><ul><li>Ulrich, P. (2023): „Framing und Klima. Zur Verantwortung des Journalismus“</li><li>Netzwerk Klimajournalismus Deutschland</li><li>Fortbildungsformate u. a. bei CORRECTIV, klimafakten.de, Reporter:innenforum</li></ul><h2 id="💡-fazit" tabindex="-1">💡 Fazit <a class="header-anchor" href="#💡-fazit" aria-label="Permalink to &quot;💡 Fazit&quot;">​</a></h2><blockquote><p>Sprache kann beruhigen – oder beunruhigen.<br> Journalist:innen entscheiden, was sichtbar wird: ein Skandal – oder ein Muster.</p></blockquote><p>Verzögerung beginnt dort, wo Begriffe nicht hinterfragt we</p>',35)),l(i)])}const p=r(o,[["render",h]]);export{f as __pageData,p as default};
