import{_ as r,C as n,c as i,o as s,ag as a,G as l}from"./chunks/framework.S-Qvb3wi.js";const f=JSON.parse('{"title":"9.3 Jurist:innen","description":"","frontmatter":{"title":"9.3 Jurist:innen"},"headers":[],"relativePath":"09_rollen/03_juristinnen.md","filePath":"09_rollen/03_juristinnen.md","lastUpdated":1753279356000}'),u={name:"09_rollen/03_juristinnen.md"};function o(h,e,d,g,c,m){const t=n("Footer");return s(),i("div",null,[e[0]||(e[0]=a('<h1 id="_9-3-jurist-innen-–-„verzogerung-normieren" tabindex="-1">9.3 Jurist:innen – „Verzögerung normieren?“ <a class="header-anchor" href="#_9-3-jurist-innen-–-„verzogerung-normieren" aria-label="Permalink to &quot;9.3 Jurist:innen – „Verzögerung normieren?“&quot;">​</a></h1><h2 id="⚖️-systemische-funktion" tabindex="-1">⚖️ Systemische Funktion <a class="header-anchor" href="#⚖️-systemische-funktion" aria-label="Permalink to &quot;⚖️ Systemische Funktion&quot;">​</a></h2><p>Das Recht soll Ordnung schaffen – aber oft stabilisiert es Unordnung:<br> durch Formalismus, Entkopplung von Realität, Zuständigkeitsschleifen.<br> Verzögerung wird nicht nur <strong>toleriert</strong>, sondern häufig <strong>kodifiziert</strong> – durch Sprache, Verfahren, Prüfpflichten ohne Folgenbindung.</p><h2 id="⏳-verzogerung-im-juristischen-raum" tabindex="-1">⏳ Verzögerung im juristischen Raum <a class="header-anchor" href="#⏳-verzogerung-im-juristischen-raum" aria-label="Permalink to &quot;⏳ Verzögerung im juristischen Raum&quot;">​</a></h2><ul><li>„Soll“-Formulierungen statt verbindlicher Rechtsverpflichtungen</li><li>Prüfaufträge ohne Ergebnisbindung oder Sanktion</li><li>Föderale Kompetenzverteilung = Verantwortungsdiffusion</li><li>Schutzpflichten enden im Ressourcen- oder Ermessensvorbehalt</li><li>Gerichtliche Untätigkeit durch Rückgriff auf Unbestimmtheitslogiken</li></ul><p><strong>Kontextualisierung:</strong> Diese Muster lassen sich systematisch in Umwelt-, Planungs-, Haushalts- und Energierecht nachweisen. In vielen Gesetzgebungsakten dient juristische Sprache nicht der Klarheit – sondern der Verschiebung von Zuständigkeit und Entscheidung.</p><h2 id="🔧-beispiel-1-gesetzestext-analysieren-–-„strukturverweigerung-als-norm" tabindex="-1">🔧 Beispiel 1: Gesetzestext analysieren – „Strukturverweigerung als Norm“ <a class="header-anchor" href="#🔧-beispiel-1-gesetzestext-analysieren-–-„strukturverweigerung-als-norm" aria-label="Permalink to &quot;🔧 Beispiel 1: Gesetzestext analysieren – „Strukturverweigerung als Norm“&quot;">​</a></h2><p><strong>Auszug (fiktiv, aber realistisch):</strong></p><blockquote><p>„Die zuständigen Stellen sollen unter Einbeziehung wirtschaftlicher, sozialer und ökologischer Belange Maßnahmen zur Minderung von Treibhausgasemissionen prüfen und gegebenenfalls ergreifen.“</p></blockquote><h3 id="analyse" tabindex="-1">Analyse <a class="header-anchor" href="#analyse" aria-label="Permalink to &quot;Analyse&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Formulierung</th><th>Wirkung</th><th>Intervention</th></tr></thead><tbody><tr><td>„sollen“</td><td>rechtlich unverbindlich, politisch beruhigend</td><td>Warum keine Pflicht (→ „haben zu“)?</td></tr><tr><td>„unter Einbeziehung von…“</td><td>beliebig auslegbar – keine Priorisierung</td><td>Wer definiert Gewichtung?</td></tr><tr><td>„prüfen und ggf. ergreifen“</td><td>Doppelverzögerung: erst prüfen, dann evtl. handeln</td><td>Was passiert, wenn geprüft – aber nicht gehandelt wird?</td></tr></tbody></table><p>📎 <em>Miniaktion:</em><br> Kommentierte Version des Gesetzes im Ausbildungs- oder Weiterbildungsformat → Titel:<br><strong>„Wie Sprache Wandel verhindert – eine juristische Lesung“</strong></p><h2 id="🔧-beispiel-2-gerichtsverfahren-als-blockadehebel" tabindex="-1">🔧 Beispiel 2: Gerichtsverfahren als Blockadehebel <a class="header-anchor" href="#🔧-beispiel-2-gerichtsverfahren-als-blockadehebel" aria-label="Permalink to &quot;🔧 Beispiel 2: Gerichtsverfahren als Blockadehebel&quot;">​</a></h2><p><strong>Fall:</strong> Klimaklage gegen ein Bundesland<br><strong>Entscheidung:</strong></p><blockquote><p>„Die Kläger:innen haben nicht hinreichend dargelegt, dass eine unmittelbare Grundrechtsverletzung zu erwarten ist.“</p></blockquote><h3 id="strukturproblem" tabindex="-1">Strukturproblem <a class="header-anchor" href="#strukturproblem" aria-label="Permalink to &quot;Strukturproblem&quot;">​</a></h3><ul><li>Zukunft = Unbestimmtheit = Untätigkeit</li><li>Risiko ≠ Rechtsanspruch</li><li>Vorsorgepflicht wird zur Argumentationsbremse statt zur Handlungsnorm</li></ul><p>📎 <em>Störformat:</em><br> Verfasse eine fiktive Begründung, wie ein Gericht entscheiden <strong>könnte</strong>, wenn es systemisch denkt:</p><blockquote><p><em>„Das bloße Fehlen eines quantifizierten Schadens begründet im Kontext der Klimakrise keine Untätigkeitspflicht. Der Staat schuldet Vorsorge – nicht Rückschau.“</em></p></blockquote><h2 id="🔧-beispiel-3-kommentierungspflicht-als-leerstelle" tabindex="-1">🔧 Beispiel 3: Kommentierungspflicht als Leerstelle <a class="header-anchor" href="#🔧-beispiel-3-kommentierungspflicht-als-leerstelle" aria-label="Permalink to &quot;🔧 Beispiel 3: Kommentierungspflicht als Leerstelle&quot;">​</a></h2><p><strong>Hintergrund:</strong><br> Viele Gesetze enthalten Prüfpflichten (z. B. Umweltfolgenabschätzungen, Nachhaltigkeitschecks), aber kaum Konsequenzpflichten.</p><p>📌 <em>Störfrage in Beteiligungsverfahren:</em></p><blockquote><p>„Wie wurde das Ergebnis der UFA konkret in den Gesetzesentwurf eingearbeitet?“<br> „Welche Maßnahmen wurden <em>nicht</em> beschlossen – trotz ermittelter Umweltfolgen?“<br> „Warum reicht Prüfung ohne Korrektur?“</p></blockquote><p><strong>Ziel:</strong><br> Verrechtlichung nicht als Schutz – sondern als Bremslogik sichtbar machen</p><h2 id="📚-zusatzformat-verzogerungs-glossar-fur-jurist-innen" tabindex="-1">📚 Zusatzformat: Verzögerungs-Glossar für Jurist:innen <a class="header-anchor" href="#📚-zusatzformat-verzogerungs-glossar-fur-jurist-innen" aria-label="Permalink to &quot;📚 Zusatzformat: Verzögerungs-Glossar für Jurist:innen&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Begriff</th><th>Systemischer Effekt</th></tr></thead><tbody><tr><td>Verhältnismäßigkeit</td><td>Legitimation für Untätigkeit bei Zielkonflikten</td></tr><tr><td>Ressourcenvorbehalt</td><td>Verschiebung von Verantwortung auf Haushalt/Budget</td></tr><tr><td>Föderalismus</td><td>Zuständigkeit wird zirkulär verteilt</td></tr><tr><td>Formelle Legalität</td><td>Strukturwandel wird am Maßstab des Verfahrens gestoppt</td></tr><tr><td>Prüfauftrag</td><td>Handlung wird ersetzt durch Nachweispflicht ohne Wirkung</td></tr></tbody></table><p>➡️ <em>Einsatz:</em> Jurist:innenfortbildung, Ausbildung in Verwaltungs- oder Umweltrecht, Plakat in Referendar:innenzimmern</p><h2 id="🛠-didaktische-erweiterung" tabindex="-1">🛠 Didaktische Erweiterung <a class="header-anchor" href="#🛠-didaktische-erweiterung" aria-label="Permalink to &quot;🛠 Didaktische Erweiterung&quot;">​</a></h2><ul><li><strong>Case Study</strong>: Analysiere eine reale Gesetzesbegründung auf Bremsformulierungen</li><li><strong>Methode</strong>: „Umformulieren als Intervention“ – aus „sollen“ wird „müssen“, aus Prüfauftrag wird Handlungspflicht</li><li><strong>Simulation</strong>: Fiktives Gericht im Jahr 2035 urteilt rückblickend über heutige Verzögerungspraktiken</li></ul><h2 id="💡-fazit" tabindex="-1">💡 Fazit <a class="header-anchor" href="#💡-fazit" aria-label="Permalink to &quot;💡 Fazit&quot;">​</a></h2><blockquote><p>Recht ist nie neutral. Es ist nie nur Rahmen – sondern auch Bremse oder Hebel.<br> Wer Gesetzestexte nur liest, sieht Regeln.<br> Wer sie stört, erkennt Strukturen – und kann sie aufbrechen.</p></blockquote><p>Jurist:innen können systemische Verzögerung <strong>lesbar machen – und haftbar.</strong></p>',32)),l(t)])}const k=r(u,[["render",o]]);export{f as __pageData,k as default};
