import{_ as n,C as r,c as i,o as a,ag as s,G as o}from"./chunks/framework.S-Qvb3wi.js";const b=JSON.parse('{"title":"9.4 Aktivist:innen","description":"","frontmatter":{"title":"9.4 Aktivist:innen"},"headers":[],"relativePath":"09_rollen/04_aktivistinnen.md","filePath":"09_rollen/04_aktivistinnen.md","lastUpdated":1753279356000}'),l={name:"09_rollen/04_aktivistinnen.md"};function d(h,e,u,g,m,c){const t=r("Footer");return a(),i("div",null,[e[0]||(e[0]=s('<h1 id="_9-4-aktivist-innen-–-„widerstand-gegen-was-genau" tabindex="-1">9.4 Aktivist:innen – „Widerstand gegen was, genau?“ <a class="header-anchor" href="#_9-4-aktivist-innen-–-„widerstand-gegen-was-genau" aria-label="Permalink to &quot;9.4 Aktivist:innen – „Widerstand gegen was, genau?“&quot;">​</a></h1><h2 id="🧑‍🎤-systemische-rolle" tabindex="-1">🧑‍🎤 Systemische Rolle <a class="header-anchor" href="#🧑‍🎤-systemische-rolle" aria-label="Permalink to &quot;🧑‍🎤 Systemische Rolle&quot;">​</a></h2><p>Aktivismus will Wandel beschleunigen – doch läuft oft in vorbereitete Gassen:<br> ritualisiert, berechenbar, symbolisiert.</p><blockquote><p><em>Nicht jeder Protest stört.</em><br><em>Mancher Protest stabilisiert – weil er erwartet wird.</em></p></blockquote><h2 id="🚧-typische-blockaden-im-aktivismus" tabindex="-1">🚧 Typische Blockaden im Aktivismus <a class="header-anchor" href="#🚧-typische-blockaden-im-aktivismus" aria-label="Permalink to &quot;🚧 Typische Blockaden im Aktivismus&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Muster</th><th>Beschreibung</th><th>Folge</th></tr></thead><tbody><tr><td>Ritualisierung</td><td>Wiederholte Formate („Freitags, gleiche Route“)</td><td>Politisches Rauschen ohne Reibung</td></tr><tr><td>Radikalitätsdilemma</td><td>Zu laut = diskreditiert / zu leise = vereinnahmt</td><td>Selbstzensur oder Wirkungslosigkeit</td></tr><tr><td>Systemblindheit</td><td>„Gegen das System“ – aber ohne Analyse konkreter Mechanismen</td><td>Empörung ohne Hebelwirkung</td></tr><tr><td>Medienlogik-Verinnerlichung</td><td>Aktionen nach Klick- und Bildlogik gestaltet</td><td>Sichtbarkeit statt struktureller Irritation</td></tr><tr><td>Koalitionsunfähigkeit</td><td>Konkurrenz um Radikalität, statt Bündnisfähigkeit</td><td>Fragmentierung</td></tr></tbody></table><h2 id="🔧-beispiel-1-„ritualstorung-–-raus-aus-der-komfortzone-des-protests" tabindex="-1">🔧 Beispiel 1: „Ritualstörung – raus aus der Komfortzone des Protests“ <a class="header-anchor" href="#🔧-beispiel-1-„ritualstorung-–-raus-aus-der-komfortzone-des-protests" aria-label="Permalink to &quot;🔧 Beispiel 1: „Ritualstörung – raus aus der Komfortzone des Protests“&quot;">​</a></h2><p><strong>Kontext:</strong><br> Geplante Demo, Route bekannt, Ablauf klar, Slogans vertraut.<br> Medien wissen, was kommt – Reaktion: „Wir hören die Sorgen“.</p><h3 id="alternative-aktion-verzogerungskonferenz-straßenperformance" tabindex="-1">Alternative Aktion: <strong>Verzögerungskonferenz (Straßenperformance)</strong> <a class="header-anchor" href="#alternative-aktion-verzogerungskonferenz-straßenperformance" aria-label="Permalink to &quot;Alternative Aktion: **Verzögerungskonferenz (Straßenperformance)**&quot;">​</a></h3><ul><li>Aktivist:innen verkörpern reale Rollen (Ministerien, Lobby, NGO, Industrie)</li><li>Verwenden echte Zitate aus Politik, Industrie, Medien</li><li>Simulation: <em>30 Jahre Blockade – in 30 Minuten</em></li></ul><p><strong>Ziel:</strong><br> Verzögerung als Inszenierung enttarnen – <em>nicht nur Untätigkeit</em>, sondern <strong>Strategie</strong>.</p><h2 id="🔧-beispiel-2-„framing-hacken-–-raus-aus-der-radikalitatsfalle" tabindex="-1">🔧 Beispiel 2: „Framing hacken – raus aus der Radikalitätsfalle“ <a class="header-anchor" href="#🔧-beispiel-2-„framing-hacken-–-raus-aus-der-radikalitatsfalle" aria-label="Permalink to &quot;🔧 Beispiel 2: „Framing hacken – raus aus der Radikalitätsfalle“&quot;">​</a></h2><p><strong>Problem:</strong><br> Medien berichten über Protest als <em>„Störung“</em> – nicht über Ursachen.</p><p><strong>Aktion:</strong><br><strong>Pressekonferenz mit anderer Drehung:</strong></p><blockquote><p>Titel: <em>„Was wir nicht sagen dürfen – aber müssen“</em></p></blockquote><ul><li>Vorlesen von Zuschreibungen: „Klimaradikale“, „Extremisten“, „Sekte“</li><li>Faktencheck: Wer hat wann systematisch verzögert – und mit welchen Begriffen?</li></ul><p>📎 <em>Formatidee:</em><br> Kachel-Serie / Blog / Insta-Story: <em>„Was sie über uns sagen – was wir wirklich tun“</em></p><p><strong>Ziel:</strong><br> Framing umkehren: Nicht „radikal“ – sondern <em>präzise in der Systemdiagnose</em>.</p><h2 id="🔧-beispiel-3-„koalitionen-gegen-das-normale" tabindex="-1">🔧 Beispiel 3: „Koalitionen gegen das Normale“ <a class="header-anchor" href="#🔧-beispiel-3-„koalitionen-gegen-das-normale" aria-label="Permalink to &quot;🔧 Beispiel 3: „Koalitionen gegen das Normale“&quot;">​</a></h2><p><strong>Blockade:</strong><br> Gruppen arbeiten parallel, mit ähnlicher Analyse – aber ohne geteilte Sprache oder Koordination.</p><h3 id="interventionsformat-mapping-the-delay" tabindex="-1">Interventionsformat: <strong>Mapping the Delay</strong> <a class="header-anchor" href="#interventionsformat-mapping-the-delay" aria-label="Permalink to &quot;Interventionsformat: **Mapping the Delay**&quot;">​</a></h3><p>Workshop mit mehreren Gruppen, gemeinsames Mapping:</p><table tabindex="0"><thead><tr><th>Akteur</th><th>Mittel</th><th>Narrativ</th><th>Wirkung</th></tr></thead><tbody><tr><td>BMW</td><td>PR, Sponsoring</td><td>„Wir wollen grün sein.“</td><td>Greenwashing, Zeitgewinn</td></tr><tr><td>Bundesregierung</td><td>Gesetzgebung</td><td>„Technologieoffenheit“</td><td>Vermeidung struktureller Pfade</td></tr><tr><td>FDP-Fraktion</td><td>Sprachpolitik</td><td>„Freiheit statt Verbote“</td><td>Individualisierung der Schuld</td></tr></tbody></table><p>➡️ Gemeinsames Ergebnis:<br> → Geteiltes Vokabular<br> → Koordinierte Störaktionen an Bruchstellen – nicht an Symbolen</p><h2 id="🧯-reflexionswerkzeug-„bin-ich-noch-storung-–-oder-schon-ritual" tabindex="-1">🧯 Reflexionswerkzeug: „Bin ich noch Störung – oder schon Ritual?“ <a class="header-anchor" href="#🧯-reflexionswerkzeug-„bin-ich-noch-storung-–-oder-schon-ritual" aria-label="Permalink to &quot;🧯 Reflexionswerkzeug: „Bin ich noch Störung – oder schon Ritual?“&quot;">​</a></h2><p><strong>Fragen für Gruppenreflexion:</strong></p><ol><li>Was war unser letztes Ziel – und wie wurde Wirkung gemessen?</li><li>Wer hat sich durch uns bewegt – wer hat nur kommentiert?</li><li>Welche Routinen reproduzieren wir – obwohl sie wenig verändern?</li><li>Wann haben wir zuletzt jemanden <em>wirklich irritiert</em> – nicht nur erreicht?</li></ol><p>➡️ Format: Flipchart, Retrospektive-Tool, Camp-Material</p><h2 id="📎-bonusformat-„aktion-ohne-bild" tabindex="-1">📎 Bonusformat: „Aktion ohne Bild“ <a class="header-anchor" href="#📎-bonusformat-„aktion-ohne-bild" aria-label="Permalink to &quot;📎 Bonusformat: „Aktion ohne Bild“&quot;">​</a></h2><p><strong>Ziel:</strong> Etwas tun, das <em>nicht medial verwertbar</em> ist – aber strukturell wirkt.</p><h3 id="beispiele" tabindex="-1">Beispiele <a class="header-anchor" href="#beispiele" aria-label="Permalink to &quot;Beispiele&quot;">​</a></h3><ul><li>Echte Zitate aus Lobby-Hearings anonym per Post an Ausschussmitglieder senden</li><li>Broschüre: <em>„So wird verzögert“</em> – mit Originalzitaten aus Parteiprogrammen und Gesetzesentwürfen</li><li>Mailing an Redaktionen: <em>„10 Floskeln, die systemische Untätigkeit tarnen“</em></li></ul><h2 id="🎓-transfer-anwendung" tabindex="-1">🎓 Transfer &amp; Anwendung <a class="header-anchor" href="#🎓-transfer-anwendung" aria-label="Permalink to &quot;🎓 Transfer &amp; Anwendung&quot;">​</a></h2><ul><li><strong>Workshopidee:</strong> „Strategien der Gegenmacht – Jenseits der Sichtbarkeit“</li><li><strong>Praxisübung:</strong> Mapping der eigenen Aktivismusgeschichte auf Reibungswirkung</li><li><strong>Diskussionsrunde:</strong> „Protest als Praxis – oder Ritual?“</li></ul><h2 id="💡-fazit" tabindex="-1">💡 Fazit <a class="header-anchor" href="#💡-fazit" aria-label="Permalink to &quot;💡 Fazit&quot;">​</a></h2><blockquote><p>Aktivismus, der nicht stört, wird dekoriert.<br> Aktivismus, der Muster zeigt, wird gefährlich.</p></blockquote><p>Wer Protest strukturell denkt,<br> spricht nicht in Parolen – sondern in <strong>Verzögerungsdiagnosen</strong>.<br> Das ist radikal – im besten Sinne: <strong>an die Wurzel gehend.</strong></p>',37)),o(t)])}const p=n(l,[["render",d]]);export{b as __pageData,p as default};
