import{_ as n,C as a,c as s,o as r,ag as i,G as l}from"./chunks/framework.S-Qvb3wi.js";const b=JSON.parse('{"title":"9.5 Alltagsperspektive","description":"","frontmatter":{"title":"9.5 Alltagsperspektive"},"headers":[],"relativePath":"09_rollen/05_alltag.md","filePath":"09_rollen/05_alltag.md","lastUpdated":1753279356000}'),h={name:"09_rollen/05_alltag.md"};function o(d,e,c,g,u,p){const t=a("Footer");return r(),s("div",null,[e[0]||(e[0]=i(`<h1 id="_9-5-alltagsperspektive-–-„verzogerung-im-wohnzimmer" tabindex="-1">9.5 Alltagsperspektive – „Verzögerung im Wohnzimmer“ <a class="header-anchor" href="#_9-5-alltagsperspektive-–-„verzogerung-im-wohnzimmer" aria-label="Permalink to &quot;9.5 Alltagsperspektive – „Verzögerung im Wohnzimmer“&quot;">​</a></h1><h2 id="🏠-systemischer-ort" tabindex="-1">🏠 Systemischer Ort <a class="header-anchor" href="#🏠-systemischer-ort" aria-label="Permalink to &quot;🏠 Systemischer Ort&quot;">​</a></h2><p>Der Alltag reproduziert das System – durch Sprache, Routinen, Schweigen.<br> Nicht durch böse Absicht – sondern durch <strong>Gewöhnung an das Unangemessene</strong>.</p><h2 id="🧩-typische-verzogerungsmuster-im-alltag" tabindex="-1">🧩 Typische Verzögerungsmuster im Alltag <a class="header-anchor" href="#🧩-typische-verzogerungsmuster-im-alltag" aria-label="Permalink to &quot;🧩 Typische Verzögerungsmuster im Alltag&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Muster</th><th>Erscheinungsform</th><th>Effekt</th></tr></thead><tbody><tr><td><strong>Entpolitisierte Sorge</strong></td><td>„Ich hab ja auch Angst, aber was soll man machen?“</td><td>Gefühl von Ohnmacht → Unterlassung</td></tr><tr><td><strong>Moralversatz</strong></td><td>„Ich kaufe bio, fahre wenig – ich tu doch, was ich kann.“</td><td>Verantwortung bleibt im Konsumbereich</td></tr><tr><td><strong>Themenverschiebung</strong></td><td>„Das ist mir grad zu negativ.“</td><td>Schutz des emotionalen Gleichgewichts</td></tr><tr><td><strong>Ironisierung</strong></td><td>„Na gut, dann hören wir halt auf zu atmen!“</td><td>Diskursbruch durch Lächerlichmachen</td></tr><tr><td><strong>Framing durch Normalität</strong></td><td>„Man kann ja nicht auf alles verzichten.“</td><td>Bestehendes erscheint unausweichlich</td></tr></tbody></table><h2 id="🔧-beispiel-1-kuchentischdialog-–-mikroverzogerung-im-gesprach" tabindex="-1">🔧 Beispiel 1: Küchentischdialog – Mikroverzögerung im Gespräch <a class="header-anchor" href="#🔧-beispiel-1-kuchentischdialog-–-mikroverzogerung-im-gesprach" aria-label="Permalink to &quot;🔧 Beispiel 1: Küchentischdialog – Mikroverzögerung im Gespräch&quot;">​</a></h2><p><strong>Kontext:</strong> Abendessen nach Nachrichtensendung über Extremwetter<br><strong>Teilnehmer:innen:</strong> Anna (24), Vater (52), Mutter (49)</p><p><strong>Dialogfragment:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Anna: Es ist doch absurd – wir wissen seit 40 Jahren, was passieren wird, und trotzdem passiert nichts.</span></span>
<span class="line"><span>Vater: Na ja, wir können nicht einfach alles abstellen. Es geht halt nicht so schnell.</span></span>
<span class="line"><span>Mutter: Ich finde auch, manchmal übertreiben sie. Man kann doch nicht alles auf die Politik schieben.</span></span>
<span class="line"><span>Anna: Aber wer dann? Ich kann nicht mit meinem Jutebeutel das CO₂-Budget retten.</span></span>
<span class="line"><span>Vater (lacht): Dann musst du halt Klima-Ministerin werden.</span></span>
<span class="line"><span>Mutter: Können wir das Thema lassen? Es zieht runter.</span></span></code></pre></div><p><strong>Analyse:</strong></p><ul><li>Zeit-Relativierung („es geht halt nicht so schnell“)</li><li>Verantwortungsdiffusion</li><li>Ironisierung</li><li>Emotionaler Gesprächsabbruch</li></ul><p><strong>Störimpulse für Anna:</strong></p><ul><li>Rückfrage: <em>„Was meinst du mit ‚es geht nicht‘ – technisch oder politisch?“</em></li><li>Visualisierung: <em>„Dürfen wir mal über das reden, was bewusst verzögert wird?“</em></li><li>Konfrontation: <em>„81 % der CO₂-Zunahme passierte, nachdem wir es längst wussten.“</em></li></ul><h2 id="🔧-beispiel-2-whatsapp-chat-–-mikrovermeidung" tabindex="-1">🔧 Beispiel 2: WhatsApp-Chat – Mikrovermeidung <a class="header-anchor" href="#🔧-beispiel-2-whatsapp-chat-–-mikrovermeidung" aria-label="Permalink to &quot;🔧 Beispiel 2: WhatsApp-Chat – Mikrovermeidung&quot;">​</a></h2><p><strong>Kontext:</strong> WG-Gruppe, Semesterplanung</p><p><strong>Verlauf:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Lea: Habt ihr den Klimabericht gesehen? Die sagen, Kipppunkte sind fast erreicht.</span></span>
<span class="line"><span>Tom: Uff. Ich kann’s nicht mehr hören. Wird immer schlimmer, aber keiner tut was.</span></span>
<span class="line"><span>Mira: Ja, ich versuch eh, weniger Fleisch zu essen.</span></span>
<span class="line"><span>Tom: Ich hab ja überlegt, ob ich mir ’n E-Roller zulege – zählt das?</span></span>
<span class="line"><span>Lea: Ich mein’s ernst. Ich hab Angst, dass ich in 20 Jahren nichts gemacht hab.</span></span>
<span class="line"><span>(2 Stunden keine Antwort)</span></span></code></pre></div><p><strong>Vermeidungsformen:</strong></p><ul><li>Lifestyle-Framing (E-Roller)</li><li>Moralisierung (weniger Fleisch)</li><li>Schweigen bei Tiefenangst</li></ul><p><strong>Störversuche von Lea:</strong></p><ul><li>Systemfrage: <em>„Findet ihr auch, alles wird aufs Individuum geschoben?“</em></li><li>Einladung: <em>„Ich glaub, ich brauch Leute, die diese Angst auch zulassen können.“</em></li><li>Ironiekonter: <em>„Wenn E-Roller das Klima retten, kauf ich zwei – einen für den Nordpol.“</em></li></ul><h2 id="🔧-beispiel-3-burokaffee-storung" tabindex="-1">🔧 Beispiel 3: Bürokaffee-Störung <a class="header-anchor" href="#🔧-beispiel-3-burokaffee-storung" aria-label="Permalink to &quot;🔧 Beispiel 3: Bürokaffee-Störung&quot;">​</a></h2><p><strong>Kontext:</strong> Stadtverwaltung, Kaffeepause<br><strong>Thema:</strong> autofreie Innenstadt</p><p><strong>Dialog:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Kollege 1: Die wollen echt die Innenstadt dicht machen – was für ein Irrsinn.</span></span>
<span class="line"><span>Kollegin 2: Da denkt niemand an die Pendler – das ist wieder so eine grüne Spinnerei.</span></span>
<span class="line"><span>(Du hörst zu.)</span></span></code></pre></div><p><strong>Störoptionen:</strong></p><ul><li>Faktischer Konter: <em>„70 % der Fläche gehört dem Auto – aber nur 30 % fahren damit rein.“</em></li><li>Verantwortungsverschiebung thematisieren: <em>„Wer entscheidet das – und für wen?“</em></li><li>Frame brechen: <em>„Warum ist es gleich ein Verbot – und nicht Schutz?“</em></li></ul><hr><h2 id="📂-werkzeug-alltagsverzogerungs-decoder" tabindex="-1">📂 Werkzeug: Alltagsverzögerungs-Decoder <a class="header-anchor" href="#📂-werkzeug-alltagsverzogerungs-decoder" aria-label="Permalink to &quot;📂 Werkzeug: Alltagsverzögerungs-Decoder&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Aussage</th><th>Versteckter Frame</th><th>Mögliche Störung</th></tr></thead><tbody><tr><td>„Das ist mir zu negativ.“</td><td>Emotionales Vermeidungsrecht</td><td>„Was ist schlimmer – reden oder schweigen?“</td></tr><tr><td>„Ich tu ja schon was.“</td><td>Moralische Selbstvergewisserung</td><td>„Glaubst du, das reicht gegen Systemträgheit?“</td></tr><tr><td>„Wir müssen realistisch bleiben.“</td><td>Norm-Stabilisierung durch Pragmatismus</td><td>„Realistisch für wen – und mit welchem Horizont?“</td></tr><tr><td>„Man kann ja eh nix machen.“</td><td>Ohnmachtsmantra</td><td>„Wer will, dass du das glaubst?“</td></tr></tbody></table><hr><h2 id="💡-fazit-nicht-weltrettung-–-sondern-realitatsbruch" tabindex="-1">💡 Fazit: Nicht Weltrettung – sondern Realitätsbruch <a class="header-anchor" href="#💡-fazit-nicht-weltrettung-–-sondern-realitatsbruch" aria-label="Permalink to &quot;💡 Fazit: Nicht Weltrettung – sondern Realitätsbruch&quot;">​</a></h2><blockquote><p><em>Verzögerung lebt davon, dass niemand stört.</em><br><em>Störung beginnt nicht in Gesetzen – sondern in Gesprächen.</em></p></blockquote><p>Wenn du <strong>einmal fragst</strong>, wo andere nur nicken,<br> wenn du <strong>einmal offen lässt</strong>, wo andere glätten,<br> wenn du <strong>einmal aushältst</strong>, was andere umschiffen –<br> dann hast du gestört. Und vielleicht: geöffnet.</p>`,34)),l(t)])}const f=n(h,[["render",o]]);export{b as __pageData,f as default};
