import{_ as t,C as r,c as a,o as s,ag as i,G as l}from"./chunks/framework.S-Qvb3wi.js";const b=JSON.parse('{"title":"9.6 Verwaltungsakteur:innen","description":"","frontmatter":{"title":"9.6 Verwaltungsakteur:innen"},"headers":[],"relativePath":"09_rollen/06_verwaltung.md","filePath":"09_rollen/06_verwaltung.md","lastUpdated":1753279356000}'),o={name:"09_rollen/06_verwaltung.md"};function u(d,e,g,h,c,m){const n=r("Footer");return s(),a("div",null,[e[0]||(e[0]=i('<h1 id="_9-6-verwaltungsakteur-innen-–-„verzogern-per-erlass" tabindex="-1">9.6 Verwaltungsakteur:innen – „Verzögern per Erlass?“ <a class="header-anchor" href="#_9-6-verwaltungsakteur-innen-–-„verzogern-per-erlass" aria-label="Permalink to &quot;9.6 Verwaltungsakteur:innen – „Verzögern per Erlass?“&quot;">​</a></h1><h2 id="🏢-systemische-position" tabindex="-1">🏢 Systemische Position <a class="header-anchor" href="#🏢-systemische-position" aria-label="Permalink to &quot;🏢 Systemische Position&quot;">​</a></h2><p>Verwaltungen setzen um, wovon andere reden.<br> Doch in der Umsetzung liegt oft die eigentliche <strong>Verzögerungsschleife</strong>.</p><blockquote><p><em>Nicht Gesetze bremsen den Wandel – sondern ihre Umsetzung in Routinen.</em></p></blockquote><h2 id="⏳-verzogerung-in-der-verwaltung" tabindex="-1">⏳ Verzögerung in der Verwaltung <a class="header-anchor" href="#⏳-verzogerung-in-der-verwaltung" aria-label="Permalink to &quot;⏳ Verzögerung in der Verwaltung&quot;">​</a></h2><ul><li>Zuständigkeiten werden verteilt, statt koordiniert</li><li>Erlasse und Verordnungen dienen der Vermeidung von Verantwortung</li><li>Verwaltungsdeutsch verschleiert politische Setzungen</li><li>Form schlägt Wirkung: „Hauptsache korrekt, nicht wirksam“</li></ul><h2 id="🔧-beispiel-1-aktenstau-durch-„ermessensspielraum" tabindex="-1">🔧 Beispiel 1: Aktenstau durch „Ermessensspielraum“ <a class="header-anchor" href="#🔧-beispiel-1-aktenstau-durch-„ermessensspielraum" aria-label="Permalink to &quot;🔧 Beispiel 1: Aktenstau durch „Ermessensspielraum“&quot;">​</a></h2><p><strong>Kontext:</strong> Kommune prüft Radwegeausbau, will Flächen umwidmen <strong>Verwaltungsantwort (fiktiv, aber realistisch):</strong></p><blockquote><p>„Die Prüfung möglicher Zielkonflikte mit bestehenden Flächennutzungsplänen ergibt derzeit keinen eindeutigen Handlungsauftrag.“</p></blockquote><h3 id="analyse" tabindex="-1">Analyse <a class="header-anchor" href="#analyse" aria-label="Permalink to &quot;Analyse&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Formulierung</th><th>Systemeffekt</th></tr></thead><tbody><tr><td>„Prüfung ergibt keinen Auftrag“</td><td>Vermeidung durch semantisches Vakuum</td></tr><tr><td>„mögliche Zielkonflikte“</td><td>Vorwegnahme von Widerstand = Vorwand</td></tr><tr><td>„derzeit“</td><td>Entzeitlichung → „vielleicht irgendwann“</td></tr></tbody></table><p><strong>Intervention:</strong><br> → <strong>Kommentierter Übersetzungsvorschlag</strong> für Fortbildungen:</p><blockquote><p><em>„Wir könnten – aber es könnte Ärger geben. Also tun wir es lieber nicht.“</em></p></blockquote><h2 id="🔧-beispiel-2-amtssprache-als-beruhigungsinstrument" tabindex="-1">🔧 Beispiel 2: Amtssprache als Beruhigungsinstrument <a class="header-anchor" href="#🔧-beispiel-2-amtssprache-als-beruhigungsinstrument" aria-label="Permalink to &quot;🔧 Beispiel 2: Amtssprache als Beruhigungsinstrument&quot;">​</a></h2><p><strong>Situation:</strong> Verwaltungsbescheid zu Klimaanpassung</p><p><strong>Typische Formulierung:</strong></p><blockquote><p>„Im Rahmen verfügbarer Haushaltsmittel wird angestrebt, perspektivisch an den Erfordernissen der Resilienzstruktur zu arbeiten.“</p></blockquote><h3 id="storende-klartextversion" tabindex="-1">Störende Klartextversion <a class="header-anchor" href="#storende-klartextversion" aria-label="Permalink to &quot;Störende Klartextversion&quot;">​</a></h3><blockquote><p><em>„Wir setzen nur um, was niemanden stört – und niemand einklagt.“</em></p></blockquote><p><strong>Workshop-Idee:</strong><br> → <em>„Verwaltungssprache übersetzen“</em> – 10 Originalsätze in Klartext bringen → Reflexion: <em>„Was würde passieren, wenn wir wirklich sagten, was wir tun – oder nicht tun?“</em></p><h2 id="🔧-beispiel-3-zustandigkeitskarussell-stoppen" tabindex="-1">🔧 Beispiel 3: Zuständigkeitskarussell stoppen <a class="header-anchor" href="#🔧-beispiel-3-zustandigkeitskarussell-stoppen" aria-label="Permalink to &quot;🔧 Beispiel 3: Zuständigkeitskarussell stoppen&quot;">​</a></h2><p><strong>Beobachtung:</strong><br> Viele Vorhaben scheitern nicht am Budget – sondern daran, dass niemand „zuerst“ zuständig ist.</p><p><strong>Systemstörung:</strong><br> → Interner Mapping-Workshop: <em>„Was wäre, wenn niemand mehr Zuständigkeit verschieben dürfte?“</em></p><h3 id="ubung" tabindex="-1">Übung <a class="header-anchor" href="#ubung" aria-label="Permalink to &quot;Übung&quot;">​</a></h3><ul><li>Fallakte analysieren</li><li>Frage: <em>„Wer müsste eingreifen – auch wenn er oder sie nicht muss?“</em></li><li>Ziel: Systemkarte mit dokumentierten Entscheidungsvermeidungen</li></ul><h2 id="💡-fazit" tabindex="-1">💡 Fazit <a class="header-anchor" href="#💡-fazit" aria-label="Permalink to &quot;💡 Fazit&quot;">​</a></h2><blockquote><p>Verwaltung ist keine neutrale Maschine. Sie kann Wandel bremsen – oder ermöglichen.</p></blockquote><p>Wer Verwaltung versteht,<br> kann stören – wo andere sich auf den „Dienstweg“ berufen.</p><p>Verantwortung beginnt nicht beim Gesetz – sondern bei denen,<br> die es <strong>auslegen, verschieben oder verkapseln</strong>.</p>',29)),l(n)])}const k=t(o,[["render",u]]);export{b as __pageData,k as default};
