import{_ as t,C as s,c as n,o as i,ag as a,G as l}from"./chunks/framework.S-Qvb3wi.js";const b=JSON.parse('{"title":"9.7 Hochschulen & Wissenschaft","description":"","frontmatter":{"title":"9.7 Hochschulen & Wissenschaft"},"headers":[],"relativePath":"09_rollen/07_hochschulen.md","filePath":"09_rollen/07_hochschulen.md","lastUpdated":1753279356000}'),o={name:"09_rollen/07_hochschulen.md"};function h(u,e,c,d,g,m){const r=s("Footer");return i(),n("div",null,[e[0]||(e[0]=a('<h1 id="_9-7-hochschulen-wissenschaft-–-„verzogerung-erforschen-–-oder-verlangern" tabindex="-1">9.7 Hochschulen &amp; Wissenschaft – „Verzögerung erforschen – oder verlängern?“ <a class="header-anchor" href="#_9-7-hochschulen-wissenschaft-–-„verzogerung-erforschen-–-oder-verlangern" aria-label="Permalink to &quot;9.7 Hochschulen &amp; Wissenschaft – „Verzögerung erforschen – oder verlängern?“&quot;">​</a></h1><h2 id="🎓-systemische-position" tabindex="-1">🎓 Systemische Position <a class="header-anchor" href="#🎓-systemische-position" aria-label="Permalink to &quot;🎓 Systemische Position&quot;">​</a></h2><p>Wissenschaft hat Autorität – aber selten Handlung.<br> Sie beschreibt die Welt – oft ohne sie zu stören.</p><blockquote><p><em>Wissen allein ändert nichts.</em><br><em>Was zählt, ist:</em> <strong><em>Wo es anschlussfähig wird – und wo nicht.</em></strong></p></blockquote><h2 id="⏳-verzogerung-in-der-akademischen-welt" tabindex="-1">⏳ Verzögerung in der akademischen Welt <a class="header-anchor" href="#⏳-verzogerung-in-der-akademischen-welt" aria-label="Permalink to &quot;⏳ Verzögerung in der akademischen Welt&quot;">​</a></h2><ul><li>Forschung bleibt systemintern: „mehr Daten“, „mehr Modelle“, „mehr Paper“</li><li>Politische Relevanz wird als Aktivismus diffamiert</li><li>Lehrveranstaltungen reproduzieren alte Paradigmen (Wachstum, Neutralität)</li><li>Drittmittellogik verhindert Kritik an mächtigen Strukturen</li></ul><h2 id="🔧-beispiel-1-forschung-als-beruhigung" tabindex="-1">🔧 Beispiel 1: Forschung als Beruhigung <a class="header-anchor" href="#🔧-beispiel-1-forschung-als-beruhigung" aria-label="Permalink to &quot;🔧 Beispiel 1: Forschung als Beruhigung&quot;">​</a></h2><p><strong>Kontext:</strong> Ein interdisziplinäres Zentrum erhält Förderung für „klimabezogene Zukunftsforschung“<br><strong>Zielsetzung:</strong> „Generierung von Wissen zur transformativen Gesellschaftsgestaltung“</p><p><strong>Tatsächliche Outputs:</strong></p><ul><li>6 Konferenzen</li><li>12 Paper in Paywall-Journals</li><li>1 Policy Brief mit „Technologieoffenheit“ als Handlungspfad</li></ul><h3 id="systemische-kritik" tabindex="-1">Systemische Kritik <a class="header-anchor" href="#systemische-kritik" aria-label="Permalink to &quot;Systemische Kritik&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Strukturproblem</th><th>Effekt</th></tr></thead><tbody><tr><td>Keine Umsetzungsschnittstelle</td><td>Wissen bleibt Wissen</td></tr><tr><td>Sprachbarriere</td><td>Ergebnisse erreichen keine Praxisakteure</td></tr><tr><td>Selbstreferentialität</td><td>Forschung wird zum Selbstzweck</td></tr></tbody></table><p><strong>Intervention:</strong><br> → <strong>Forschungsbeirat mit Nicht-Akademiker:innen</strong></p><blockquote><p><em>„Wer profitiert von dem, was ihr nicht sagt?“</em></p></blockquote><h2 id="🔧-beispiel-2-lehre-als-reproduktion" tabindex="-1">🔧 Beispiel 2: Lehre als Reproduktion <a class="header-anchor" href="#🔧-beispiel-2-lehre-als-reproduktion" aria-label="Permalink to &quot;🔧 Beispiel 2: Lehre als Reproduktion&quot;">​</a></h2><p><strong>Beispiel:</strong> VWL-Vorlesung im 3. Semester – Fokus auf Wachstum, Externalitäten, Marktlogik</p><p><strong>Unsichtbar gemacht:</strong></p><ul><li>Machtverhältnisse</li><li>Klimafolgen als Verteilungskonflikt</li><li>Strukturelle Pfadabhängigkeit von „Lösungen“</li></ul><p><strong>Alternative:</strong><br> → <em>Seminarformat:</em> <strong>„Verzögerungsgeschichte des Klimawandels“</strong></p><ul><li>Wer wusste was – und wann?</li><li>Welche Forschung wurde ignoriert – und warum?</li><li>Welche Narrative wurden durch welche Disziplinen verstärkt?</li></ul><h2 id="🔧-beispiel-3-wissenschaftskommunikation-als-systemstorung" tabindex="-1">🔧 Beispiel 3: Wissenschaftskommunikation als Systemstörung <a class="header-anchor" href="#🔧-beispiel-3-wissenschaftskommunikation-als-systemstorung" aria-label="Permalink to &quot;🔧 Beispiel 3: Wissenschaftskommunikation als Systemstörung&quot;">​</a></h2><p><strong>Problem:</strong> Viele Forschende sagen in Interviews:</p><blockquote><p><em>„Dazu kann ich nichts sagen. Ich bin keine Politikerin.“</em></p></blockquote><p><strong>Frage:</strong><br> → Warum glauben wir, dass <strong>Wahrheit ≠ Position</strong> ist?</p><p><strong>Stör-Experiment:</strong><br> → <em>„Was ich nicht sagen darf – aber denke“</em></p><ul><li>Forschende veröffentlichen ihre persönlichen politischen Schlüsse – ohne institutionelle Sprecherrolle</li><li>Formate: Blog, Audio, Poster-Serie auf dem Campus</li></ul><h2 id="📚-workshopidee-akademische-selbstentzauberung" tabindex="-1">📚 Workshopidee: Akademische Selbstentzauberung <a class="header-anchor" href="#📚-workshopidee-akademische-selbstentzauberung" aria-label="Permalink to &quot;📚 Workshopidee: Akademische Selbstentzauberung&quot;">​</a></h2><p><strong>Titel:</strong> <em>„Ist unsere Forschung Anschluss – oder Alibi?“</em></p><p><strong>Leitfragen:</strong></p><ol><li>Welche Praxisakteure lesen unsere Arbeit – und welche nicht?</li><li>Welche Sprache verhindern wir – obwohl sie gebraucht wird?</li><li>Was würde passieren, wenn wir <em>nicht</em> mehr neutral wären?</li></ol><p><strong>Ziel:</strong><br> → Selbstverständnis-Verschiebung<br> → <em>Von Beschreibung zur Adressierung</em></p><h2 id="💡-fazit" tabindex="-1">💡 Fazit <a class="header-anchor" href="#💡-fazit" aria-label="Permalink to &quot;💡 Fazit&quot;">​</a></h2><blockquote><p>Hochschulen reproduzieren Weltbilder.<br> Wenn sie nicht stören, verstärken sie das Bestehende – sogar mit besten Absichten.</p></blockquote><p><strong>Neutralität ist kein Schutz.<br> Sie ist eine Entscheidung.</strong></p><p>Wissenschaft kann transformativ sein –<br> wenn sie sich traut, <strong>Wirklichkeit nicht nur zu analysieren, sondern zu adressieren.</strong></p>',35)),l(r)])}const k=t(o,[["render",h]]);export{b as __pageData,k as default};
