import{_ as n,C as t,c as r,o as s,ag as a,G as l}from"./chunks/framework.S-Qvb3wi.js";const b=JSON.parse('{"title":"9.8 Kirchen & spirituelle Gemeinschaften","description":"","frontmatter":{"title":"9.8 Kirchen & spirituelle Gemeinschaften"},"headers":[],"relativePath":"09_rollen/08_kirchen.md","filePath":"09_rollen/08_kirchen.md","lastUpdated":1753279356000}'),o={name:"09_rollen/08_kirchen.md"};function u(h,e,c,d,g,m){const i=t("Footer");return s(),r("div",null,[e[0]||(e[0]=a('<h1 id="_9-8-kirchen-spirituelle-gemeinschaften-–-„verzogerung-segnen" tabindex="-1">9.8 Kirchen &amp; spirituelle Gemeinschaften – „Verzögerung segnen?“ <a class="header-anchor" href="#_9-8-kirchen-spirituelle-gemeinschaften-–-„verzogerung-segnen" aria-label="Permalink to &quot;9.8 Kirchen &amp; spirituelle Gemeinschaften – „Verzögerung segnen?“&quot;">​</a></h1><h2 id="⛪-systemische-position" tabindex="-1">⛪ Systemische Position <a class="header-anchor" href="#⛪-systemische-position" aria-label="Permalink to &quot;⛪ Systemische Position&quot;">​</a></h2><p>Kirchen und spirituelle Räume geben Sinn – und oft Beruhigung.<br> Doch auch das kann verzögern:</p><blockquote><p><em>Wenn Hoffnung das Handeln ersetzt.</em><br><em>Wenn Trost die Analyse verdrängt.</em><br><em>Wenn Versöhnung statt Konfrontation kultiviert wird.</em></p></blockquote><h2 id="⏳-verzogerung-in-spirituellen-kontexten" tabindex="-1">⏳ Verzögerung in spirituellen Kontexten <a class="header-anchor" href="#⏳-verzogerung-in-spirituellen-kontexten" aria-label="Permalink to &quot;⏳ Verzögerung in spirituellen Kontexten&quot;">​</a></h2><ul><li>Krisen werden moralisiert („Wir müssen umkehren“) statt politisiert</li><li>Verantwortung wird ins Individuum verlagert („Sei achtsam“, „Tu deinen Teil“)</li><li>Schuld wird eingestanden – aber strukturelle Täter:innen bleiben unsichtbar</li><li>Liturgie überformt politische Analyse</li></ul><h2 id="🔧-beispiel-1-klimapredigt-als-ritual-–-ohne-wirklichkeit" tabindex="-1">🔧 Beispiel 1: Klimapredigt als Ritual – ohne Wirklichkeit <a class="header-anchor" href="#🔧-beispiel-1-klimapredigt-als-ritual-–-ohne-wirklichkeit" aria-label="Permalink to &quot;🔧 Beispiel 1: Klimapredigt als Ritual – ohne Wirklichkeit&quot;">​</a></h2><p><strong>Beobachtung:</strong></p><ul><li>Sonntagspredigt zum „Schöpfungsmonat“</li><li>Thema: „Bewahrung der Schöpfung“</li><li>Ton: „Wir müssen dankbarer werden, bescheidener, bewusster leben“</li></ul><p><strong>Unsichtbar gemacht:</strong></p><ul><li>Systemische Blockaden</li><li>Politische Machtverhältnisse</li><li>Verantwortung von Institutionen</li></ul><p><strong>Alternative Predigtdramaturgie:</strong><br> → <em>„Was genau zerstört wird – und wer davon profitiert“</em></p><ul><li>Zahlen &amp; Fakten zu Verzögerung</li><li>Beispiele unterlassener Politik</li><li>Aufruf zu zivilem Ungehorsam als Gewissensentscheidung</li></ul><h2 id="🔧-beispiel-2-liturgie-storen" tabindex="-1">🔧 Beispiel 2: Liturgie stören <a class="header-anchor" href="#🔧-beispiel-2-liturgie-storen" aria-label="Permalink to &quot;🔧 Beispiel 2: Liturgie stören&quot;">​</a></h2><p><strong>Aktion:</strong> <em>„Die unterlassene Fürbitte“</em></p><ul><li>Gottesdienst mit <strong>leeren Fürbitten</strong></li></ul><blockquote><p><em>„Wir bitten dich für all jene… die politische Macht hätten – und sie nicht nutzen.“</em><br><em>(Stille)</em><br><em>„Für jene, die Verantwortung delegieren – an kommende Generationen.“</em><br><em>(Stille)</em></p></blockquote><p><strong>Ziel:</strong><br> Nicht emotional trösten – sondern <em>geistlich irritieren</em></p><h2 id="🔧-beispiel-3-theologie-der-verantwortung-–-statt-moral-der-bescheidenheit" tabindex="-1">🔧 Beispiel 3: Theologie der Verantwortung – statt Moral der Bescheidenheit <a class="header-anchor" href="#🔧-beispiel-3-theologie-der-verantwortung-–-statt-moral-der-bescheidenheit" aria-label="Permalink to &quot;🔧 Beispiel 3: Theologie der Verantwortung – statt Moral der Bescheidenheit&quot;">​</a></h2><p><strong>Typischer Impuls:</strong></p><blockquote><p>„Wir alle tragen Verantwortung. Jeder kleine Schritt zählt.“</p></blockquote><p><strong>Störende Umformulierung:</strong></p><blockquote><p>„Einige tragen Verantwortung – und weichen ihr aus.<br> Und wir als Kirche haben zu lange nicht gefragt: <em>Wer verzögert systematisch – und warum?</em>“</p></blockquote><p>📎 <em>Formatidee:</em><br> → Themensonntag: <strong>„Nicht alle sind schuld – aber manche verantwortlich“</strong><br> → Mit Klartext-Stellungnahmen in Kirchenzeitung oder Gemeinde-Newsletter</p><h2 id="📚-exerzitien-zur-systemkritik" tabindex="-1">📚 Exerzitien zur Systemkritik <a class="header-anchor" href="#📚-exerzitien-zur-systemkritik" aria-label="Permalink to &quot;📚 Exerzitien zur Systemkritik&quot;">​</a></h2><p><strong>Neues Format:</strong> <em>„Spiritualität des Eingriffs“</em></p><p>Impulse:</p><ul><li>Was heißt Buße in einem strukturellen Kontext?</li><li>Wie klingt ein Gebet, das Macht adressiert – nicht nur Demut?</li><li>Was würde passieren, wenn wir vom Evangelium aus „Lobbyismus“ predigen würden?</li></ul><p>➡️ <em>Ziel:</em><br> Rituale nicht abschaffen – sondern <strong>öffnen für Wirklichkeit</strong></p><h2 id="💡-fazit" tabindex="-1">💡 Fazit <a class="header-anchor" href="#💡-fazit" aria-label="Permalink to &quot;💡 Fazit&quot;">​</a></h2><blockquote><p>Wer Hoffnung predigt, muss benennen, was sie verhindert.<br> Wer Schuld anspricht, muss zeigen, wo sie wirksam ist – nicht nur wo sie weh tut.</p></blockquote><p>Kirchen können Stabilisator oder Störsender sein.<br> Sie müssen sich entscheiden:</p><p><strong>Versöhnen wir – oder benennen wir?<br> Trösten wir – oder beunruhigen wir?<br> Segnen wir – oder unterbrechen wir?</strong></p>',33)),l(i)])}const k=n(o,[["render",u]]);export{b as __pageData,k as default};
