import{_ as t,C as r,c as i,o as a,ag as o,G as l}from"./chunks/framework.S-Qvb3wi.js";const b=JSON.parse('{"title":"9.10 Kinder & Jugendliche","description":"","frontmatter":{"title":"9.10 Kinder & Jugendliche"},"headers":[],"relativePath":"09_rollen/10_kinder-und-jugendliche.md","filePath":"09_rollen/10_kinder-und-jugendliche.md","lastUpdated":1753279356000}'),s={name:"09_rollen/10_kinder-und-jugendliche.md"};function h(u,e,d,c,g,m){const n=r("Footer");return a(),i("div",null,[e[0]||(e[0]=o('<h1 id="_9-10-kinder-jugendliche-–-„verzogerung-erben" tabindex="-1">9.10 Kinder &amp; Jugendliche – „Verzögerung erben?“ <a class="header-anchor" href="#_9-10-kinder-jugendliche-–-„verzogerung-erben" aria-label="Permalink to &quot;9.10 Kinder &amp; Jugendliche – „Verzögerung erben?“&quot;">​</a></h1><h2 id="🧒-systemische-position" tabindex="-1">🧒 Systemische Position <a class="header-anchor" href="#🧒-systemische-position" aria-label="Permalink to &quot;🧒 Systemische Position&quot;">​</a></h2><p>Sie sind die meistbeschworene Zukunft – aber kaum mit Handlungsmacht ausgestattet.<br> Statt echter Mitsprache erhalten sie:</p><blockquote><p><em>Ermutigung. Projekte. Mahnungen.</em><br> Doch die Systemlogik bleibt:<br><strong>„Ihr seid die Zukunft – aber wir entscheiden heute.“</strong></p></blockquote><h2 id="⏳-verzogerung-im-umgang-mit-jungen-menschen" tabindex="-1">⏳ Verzögerung im Umgang mit jungen Menschen <a class="header-anchor" href="#⏳-verzogerung-im-umgang-mit-jungen-menschen" aria-label="Permalink to &quot;⏳ Verzögerung im Umgang mit jungen Menschen&quot;">​</a></h2><ul><li>Infantilisiert („Ihr seid so engagiert!“)</li><li>Depolitisiert (Klimaschutz = „Verhalten verbessern“)</li><li>Moralisierend (Lob für Bescheidenheit, nicht für Kritik)</li><li>Symbolisiert („Ihr seid unsere Hoffnung“) – statt strukturell eingebunden</li></ul><h2 id="🔧-beispiel-1-projekttag-ohne-macht" tabindex="-1">🔧 Beispiel 1: Projekttag ohne Macht <a class="header-anchor" href="#🔧-beispiel-1-projekttag-ohne-macht" aria-label="Permalink to &quot;🔧 Beispiel 1: Projekttag ohne Macht&quot;">​</a></h2><p><strong>Typischer Ablauf:</strong></p><ul><li>Kinder malen Plakate: „Rettet die Erde“</li><li>Lehrer:innen sagen: „Ihr könnt etwas verändern“</li><li>Am Ende: Applaus – aber keine Brücke zur Realität</li></ul><p><strong>Alternative:</strong><br> → <strong>Realitätsmodul:</strong> <em>„Wer verzögert was – in unserer Stadt?“</em></p><p>Fragen:</p><ul><li>Wer entscheidet über Straßen, Flächen, Investitionen?</li><li>Welche Versprechen hat unsere Stadt gemacht – und was ist passiert?</li><li>Wo können wir nachfragen – oder intervenieren?</li></ul><p><strong>Ziel:</strong><br> Kritik als Kompetenz – nicht nur Mitmachen</p><h2 id="🔧-beispiel-2-schulbuchkonfrontation" tabindex="-1">🔧 Beispiel 2: Schulbuchkonfrontation <a class="header-anchor" href="#🔧-beispiel-2-schulbuchkonfrontation" aria-label="Permalink to &quot;🔧 Beispiel 2: Schulbuchkonfrontation&quot;">​</a></h2><p><strong>Situation:</strong> Schulbuchtext zum Klimawandel endet mit:</p><blockquote><p>„Wenn jeder etwas beiträgt, können wir das Ziel noch erreichen.“</p></blockquote><h3 id="storung" tabindex="-1">Störung <a class="header-anchor" href="#storung" aria-label="Permalink to &quot;Störung&quot;">​</a></h3><p>→ Neue Aufgabe:</p><blockquote><p><em>„Formuliere den Text um – für eine Realität, in der jemand aktiv verzögert.“</em></p></blockquote><p><strong>Beispielantwort (Schüler:in):</strong></p><blockquote><p>„Obwohl Millionen Menschen sich bemühen, blockieren einige wenige jede wirksame Veränderung. Warum? Weil sie davon profitieren.“</p></blockquote><p>➡️ Bildung = Politisierung – nicht Pädagogisierung</p><h2 id="🔧-beispiel-3-jugend-spricht-zuruck" tabindex="-1">🔧 Beispiel 3: Jugend spricht zurück <a class="header-anchor" href="#🔧-beispiel-3-jugend-spricht-zuruck" aria-label="Permalink to &quot;🔧 Beispiel 3: Jugend spricht zurück&quot;">​</a></h2><p><strong>Format:</strong> <em>„Was wir hören – und was wir denken“</em></p><p>Zwei-Spalten-Plakat oder Insta-Serie:</p><table tabindex="0"><thead><tr><th>Was Erwachsene sagen</th><th>Was Jugendliche denken</th></tr></thead><tbody><tr><td>„Ihr seid so engagiert – das macht Hoffnung.“</td><td>„Aber ihr tut trotzdem nichts.“</td></tr><tr><td>„Wir machen das ja für euch.“</td><td>„Dann fragt uns wenigstens mal.“</td></tr><tr><td>„Früher hatten wir auch Probleme.“</td><td>„Ja – und ihr habt sie nicht gelöst.“</td></tr></tbody></table><p><strong>Ziel:</strong><br> Sprach-Macht sichtbar machen.<br> Moral durch Realität ersetzen.</p><h2 id="📚-toolbox-fur-jugendliche-systemkritik" tabindex="-1">📚 Toolbox für jugendliche Systemkritik <a class="header-anchor" href="#📚-toolbox-fur-jugendliche-systemkritik" aria-label="Permalink to &quot;📚 Toolbox für jugendliche Systemkritik&quot;">​</a></h2><p><strong>Titel:</strong> <em>„Nicht nur Zukunft – auch Gegenwart“</em></p><p>Module:</p><ol><li><strong>Wer verzögert?</strong> – Power-Map-Übung für Schulen</li><li><strong>Wie klingt Macht?</strong> – Analyse von Politiker:innensprache</li><li><strong>Was tun?</strong> – Miniaktionen für lokale Sichtbarkeit <ul><li>z. B. Brief an Lokalzeitung</li><li>Statement in Ausschuss</li><li>Offener Brief mit konkreten Forderungen</li></ul></li></ol><p>➡️ Ziel: Handlung als Analyse – nicht nur Aktionismus</p><h2 id="💡-fazit" tabindex="-1">💡 Fazit <a class="header-anchor" href="#💡-fazit" aria-label="Permalink to &quot;💡 Fazit&quot;">​</a></h2><blockquote><p>Jugendliche sind nicht nur betroffen – sie sind Zeug:innen einer strukturierten Verzögerung.<br> Wer ihnen zuhört, muss mehr tun als bestärken.<br><strong>Er muss ihnen Handlung zutrauen – und Macht übergeben.</strong></p></blockquote><p>Nicht: <em>„Ihr seid die Hoffnung.“</em><br> Sondern: <em>„Ihr seid der Maßstab – an dem wir scheitern oder handeln.“</em></p>',35)),l(n)])}const k=t(s,[["render",h]]);export{b as __pageData,k as default};
