import{_ as r,C as a,c as s,o,ag as t,G as i}from"./chunks/framework.S-Qvb3wi.js";const p=JSON.parse('{"title":"10.1 Systemlogik in Zahlen","description":"Subventionen, Klagen, Handelsregeln: harte Kennzahlen, die zeigen, warum Verzögerung kein Zufall ist, sondern betriebswirtschaftliche Logik.\\n","frontmatter":{"title":"10.1 Systemlogik in Zahlen","description":"Subventionen, Klagen, Handelsregeln: harte Kennzahlen, die zeigen, warum Verzögerung kein Zufall ist, sondern betriebswirtschaftliche Logik.\\n","hint":"Quellenprüfung durchführen"},"headers":[],"relativePath":"10_anhang/01_zahlen.md","filePath":"10_anhang/01_zahlen.md","lastUpdated":1753279356000}'),d={name:"10_anhang/01_zahlen.md"};function l(f,e,h,u,c,g){const n=a("Footer");return o(),s("div",null,[e[0]||(e[0]=t('<h1 id="_10-1-systemlogik-in-zahlen-–-die-okonomie-der-verzogerung" tabindex="-1">10.1 Systemlogik in Zahlen – Die Ökonomie der Verzögerung <a class="header-anchor" href="#_10-1-systemlogik-in-zahlen-–-die-okonomie-der-verzogerung" aria-label="Permalink to &quot;10.1 Systemlogik in Zahlen – Die Ökonomie der Verzögerung&quot;">​</a></h1><p>Die Verzögerung war <strong>kein Zufall</strong>.<br> Sie war bilanziert. Berechnet. Abgesichert.</p><h2 id="🏦-betriebswirtschaft-statt-irrtum" tabindex="-1">🏦 Betriebswirtschaft statt Irrtum <a class="header-anchor" href="#🏦-betriebswirtschaft-statt-irrtum" aria-label="Permalink to &quot;🏦 Betriebswirtschaft statt Irrtum&quot;">​</a></h2><p>Was auf den ersten Blick wie politische Uneinigkeit oder bürokratische Trägheit wirkt, folgt häufig einer klaren ökonomischen Logik: <strong>Rendite vor Risiko</strong>.</p><p>Strategien der Verzögerung und Verhinderung entstehen nicht etwa <strong>trotz</strong>, sondern <strong>wegen des Wissens um die Klimakrise</strong>.<br> Sie sind vertraglich geschützt, finanziell attraktiv und institutionell verankert – Ausdruck einer betriebswirtschaftlich durchdachten Systemlogik.</p><h2 id="🔢-zehn-kennzahlen-die-das-system-beschreiben" tabindex="-1">🔢 Zehn Kennzahlen, die das System beschreiben <a class="header-anchor" href="#🔢-zehn-kennzahlen-die-das-system-beschreiben" aria-label="Permalink to &quot;🔢 Zehn Kennzahlen, die das System beschreiben&quot;">​</a></h2><p>Die folgende Tabelle versammelt zentrale Machtachsen und ökonomische Anreize, die die globale Klimapolitik prägen – und strukturelle Blockaden für echten Wandel schaffen:</p><table tabindex="0"><thead><tr><th><strong>Kennzahl</strong></th><th><strong>Wert / Fakt</strong></th></tr></thead><tbody><tr><td><strong>Globale fossile Subventionen (2022)</strong></td><td><strong>7 Billionen US-$</strong> weltweit.<sup class="footnote-ref"><a href="#fn1" id="fnref1">[1]</a></sup></td></tr><tr><td><strong>ISDS-Klagen gegen Umweltgesetze</strong></td><td><strong>&gt; 100</strong> Investorenklagen gegen Staaten wegen Umweltgesetzen.<sup class="footnote-ref"><a href="#fn2" id="fnref2">[2]</a></sup></td></tr><tr><td><strong>WTO-Handelsregeln</strong></td><td>Erschweren CO₂-Grenzausgleich, Schutzmaßnahmen &amp; lokale Produktion.<sup class="footnote-ref"><a href="#fn3" id="fnref3">[3]</a></sup></td></tr><tr><td><strong>Investitionsanteil Top-5-Ölkonzerne</strong></td><td><strong>&gt; 95 %</strong> ihrer Investitionen fließen weiterhin in fossile Projekte.<sup class="footnote-ref"><a href="#fn4" id="fnref4">[4]</a></sup></td></tr><tr><td><strong>Größte Anteilseigner Ölkonzerne</strong></td><td>BlackRock · Vanguard · State Street halten systemrelevante Anteile.<sup class="footnote-ref"><a href="#fn5" id="fnref5">[5]</a></sup></td></tr><tr><td><strong>71 % der globalen Emissionen seit 1988</strong></td><td>Gehen auf nur <strong>100 Konzerne</strong> zurück.<sup class="footnote-ref"><a href="#fn6" id="fnref6">[6]</a></sup></td></tr><tr><td><strong>Lobbyziele</strong></td><td>Einfluss auf EU-Taxonomie und deren nationale Umsetzung sowie Berichtspflichten.<sup class="footnote-ref"><a href="#fn7" id="fnref7">[7]</a></sup></td></tr><tr><td><strong>Klimarisiko in Fondsstrategien (weltweit)</strong></td><td>Nur <strong>~5 %</strong> der Fonds folgen einem 1,5°C-kompatiblen Pfad.<sup class="footnote-ref"><a href="#fn8" id="fnref8">[8]</a></sup></td></tr><tr><td><strong>Versicherung fossiler Projekte (2023)</strong></td><td>Großteil der Versicherer deckt weiterhin neue Kohle- &amp; LNG-Projekte.<sup class="footnote-ref"><a href="#fn9" id="fnref9">[9]</a></sup></td></tr><tr><td><strong>Fossil‑Lobby‑Ausgaben USA (2022)</strong></td><td>Ca. 126 Mio US‑$ für direkte Lobbyarbeit auf Bundesebene.<sup class="footnote-ref"><a href="#fn10" id="fnref10">[10]</a></sup></td></tr><tr><td><strong>Umweltschädliche Subventionen (Deutschland)</strong></td><td><strong>~65 Mrd. €/Jahr</strong>, z. B. Dieselprivileg &amp; Steuerbefreiung für Kerosin.<sup class="footnote-ref"><a href="#fn11" id="fnref11">[11]</a></sup></td></tr></tbody></table><p><strong>Beispiel Deutschland:</strong><br> Trotz erklärter Klimaziele bleiben das Dieselprivileg oder die Steuerfreiheit für Kerosin bestehen – Maßnahmen, die fossile Mobilität begünstigen und jährlich Milliardensummen kosten.</p><h2 id="🏭-wer-emittiert-–-und-warum-das-wichtig-ist" tabindex="-1">🏭 Wer emittiert – und warum das wichtig ist <a class="header-anchor" href="#🏭-wer-emittiert-–-und-warum-das-wichtig-ist" aria-label="Permalink to &quot;🏭 Wer emittiert – und warum das wichtig ist&quot;">​</a></h2><p>Laut dem <em>Carbon Majors Report 2017</em> sind <strong>71 % der industriellen Treibhausgasemissionen zwischen 1988 und 2015</strong> auf lediglich <strong>100 Unternehmen</strong> zurückzuführen. Besonders deutlich wird diese Konzentration bei den zehn größten Emittenten:</p><h3 id="🔟-top-10-fossile-emittenten-1988–2015" tabindex="-1">🔟 Top 10 fossile Emittenten (1988–2015) <a class="header-anchor" href="#🔟-top-10-fossile-emittenten-1988–2015" aria-label="Permalink to &quot;🔟 Top 10 fossile Emittenten (1988–2015)&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Rang</th><th>Unternehmen</th><th>Sektor</th><th>CO₂e-Emissionen [Gt]</th><th>Anteil global [%]</th><th>Typ</th><th>Land</th></tr></thead><tbody><tr><td>1</td><td>China (Coal)</td><td>Kohle</td><td>128.93</td><td>14.3</td><td>Staatlich</td><td>China</td></tr><tr><td>2</td><td>Saudi Arabian Oil Company (Aramco)</td><td>Öl &amp; Gas</td><td>40.56</td><td>4.5</td><td>Staatlich</td><td>Saudi-Arabien</td></tr><tr><td>3</td><td>Gazprom OAO</td><td>Öl &amp; Gas</td><td>35.22</td><td>3.9</td><td>Staatlich</td><td>Russland</td></tr><tr><td>4</td><td>National Iranian Oil Co</td><td>Öl &amp; Gas</td><td>20.51</td><td>2.3</td><td>Staatlich</td><td>Iran</td></tr><tr><td>5</td><td>ExxonMobil Corp</td><td>Öl &amp; Gas</td><td>17.79</td><td>2.0</td><td>Privat</td><td>USA</td></tr><tr><td>6</td><td>Coal India</td><td>Kohle</td><td>16.84</td><td>1.9</td><td>Staatlich</td><td>Indien</td></tr><tr><td>7</td><td>Petroleos Mexicanos (Pemex)</td><td>Öl &amp; Gas</td><td>16.80</td><td>1.9</td><td>Staatlich</td><td>Mexiko</td></tr><tr><td>8</td><td>Russia (Coal)</td><td>Kohle</td><td>16.74</td><td>1.9</td><td>Staatlich</td><td>Russland</td></tr><tr><td>9</td><td>Royal Dutch Shell PLC</td><td>Öl &amp; Gas</td><td>15.02</td><td>1.7</td><td>Privat</td><td>Niederlande</td></tr><tr><td>10</td><td>China National Petroleum Corp (CNPC)</td><td>Öl &amp; Gas</td><td>14.04</td><td>1.6</td><td>Staatlich</td><td>China</td></tr></tbody></table><blockquote><p><strong>Hinweis zur Datenquelle:</strong><br> Die Tabelle basiert auf dem <em>Carbon Majors Report 2017 (Richard Heede, CDP &amp; Climate Accountability Institute)</em> und bezieht sich auf die kumulierten industriellen Treibhausgasemissionen (Scope 1+3) der Jahre 1988–2015. Eine aktuellere, vergleichbar umfassende Auswertung auf Unternehmensebene liegt derzeit nicht vor.<sup class="footnote-ref"><a href="#fn6" id="fnref6:1">[6:1]</a></sup></p></blockquote><p>Laut dem <em>Carbon Majors Report 2017</em> sind <strong>71 % der weltweiten industriellen Treibhausgasemissionen seit 1988 auf nur 100 fossile Unternehmen zurückzuführen</strong>. Besonders bemerkenswert: <strong>25 dieser Unternehmen allein sind für 51 % der globalen industriellen Emissionen</strong> verantwortlich.</p><p>Diese Tabelle zeigt eindrücklich: Die Klimakrise ist <strong>nicht die Folge diffusen Konsums allein</strong>, sondern lässt sich <strong>konkret auf wirtschaftliche Akteure zurückverfolgen</strong>.<br> Die Zahlen beziehen sich auf <strong>kumulative Emissionen</strong> aus Produktion und Verbrennung fossiler Rohstoffe im Zeitraum <strong>1988–2015 (Scope 1+3)</strong>.</p><blockquote><p>Ein Großteil der Emissionen ist nicht nur historisch messbar – er ist auch strukturell abgesichert: durch Lizenzen, Subventionen, Investitionsabkommen und Marktregeln.</p></blockquote><h2 id="🧠-systemischer-kontext-sicherheit-statt-wandel" tabindex="-1">🧠 Systemischer Kontext: Sicherheit statt Wandel <a class="header-anchor" href="#🧠-systemischer-kontext-sicherheit-statt-wandel" aria-label="Permalink to &quot;🧠 Systemischer Kontext: Sicherheit statt Wandel&quot;">​</a></h2><p>Diese Zahlen erzählen keine neue Geschichte – aber sie machen <strong>strukturelle Zusammenhänge sichtbar</strong>.</p><p>Nicht <strong>Unwissen</strong>, sondern <strong>institutionelle Sicherheiten</strong> bestimmen die Handlungslogik:</p><ul><li><strong>Fondsportfolios</strong> setzen auf bewährte Renditen statt auf klimagerechte Transformation.</li><li><strong>ISDS-Klauseln</strong> (Investor-Staat-Schiedsverfahren) ermöglichen es Konzernen, Staaten wegen Umweltauflagen zu verklagen.</li><li><strong>Handelsverträge</strong> und regulatorische Schlupflöcher festigen Status quo und Besitzstände.</li></ul><p>So entsteht ein Gefüge, das bestehende Interessen schützt – und grundlegenden Wandel verzögert.</p><blockquote><p>Das System verzögert nicht, <strong>weil es scheitert</strong>,<br> sondern weil es <strong>funktioniert</strong> – nach seiner eigenen Logik.</p></blockquote><blockquote><p>Ein System, das weiß, was es tut –<br> und genau deshalb so schwer zu verändern ist.</p></blockquote><h2 id="🧾-quellenbasis" tabindex="-1">🧾 Quellenbasis <a class="header-anchor" href="#🧾-quellenbasis" aria-label="Permalink to &quot;🧾 Quellenbasis&quot;">​</a></h2><blockquote><p>Diese Tabelle listet belegte Aussagen des Kapitels.</p></blockquote><table tabindex="0"><thead><tr><th>Quelle / Autor</th><th>Jahr</th><th>Quelle (Typ)</th><th>Belegt was?</th><th>Verlinkung / Fundort</th></tr></thead><tbody><tr><td>IEA &amp; IMF</td><td>2023</td><td>Policy Report</td><td>Globale fossile Subventionen</td><td><sup class="footnote-ref"><a href="#fn1" id="fnref1:1">[1:1]</a></sup></td></tr><tr><td>UNCTAD</td><td>o. J.</td><td>Offizielles Dokument</td><td>Anzahl ISDS-Klagen gegen Umweltgesetze</td><td><sup class="footnote-ref"><a href="#fn2" id="fnref2:1">[2:1]</a></sup></td></tr><tr><td>WTO</td><td>o. J.</td><td>Offizielles Dokument</td><td>Handelsregeln erschweren CO₂-Grenzausgleich &amp; Klimaschutz</td><td><sup class="footnote-ref"><a href="#fn3" id="fnref3:1">[3:1]</a></sup></td></tr><tr><td>International Energy Agency</td><td>2024</td><td>Policy Report</td><td>Investitionen der Top-5-Ölkonzerne in fossile Projekte</td><td><sup class="footnote-ref"><a href="#fn4" id="fnref4:1">[4:1]</a></sup></td></tr><tr><td>U.S. SEC</td><td>o. J.</td><td>Regulatorische Datenquelle</td><td>Anteilseigner Ölkonzerne</td><td><sup class="footnote-ref"><a href="#fn5" id="fnref5:1">[5:1]</a></sup></td></tr><tr><td>InfluenceMap</td><td>2020</td><td>NGO-Bericht</td><td>Lobbyziele bei EU-Taxonomie und Klimagesetzen</td><td><sup class="footnote-ref"><a href="#fn7" id="fnref7:1">[7:1]</a></sup></td></tr><tr><td>InfluenceMap</td><td>2022</td><td>NGO-Bericht</td><td>Anteil 1,5°C-kompatibler Fondsstrategien weltweit</td><td><sup class="footnote-ref"><a href="#fn8" id="fnref8:1">[8:1]</a></sup></td></tr><tr><td>Insure Our Future</td><td>2023</td><td>NGO-Bericht</td><td>Versicherung fossiler Projekte durch große Anbieter</td><td><sup class="footnote-ref"><a href="#fn9" id="fnref9:1">[9:1]</a></sup></td></tr><tr><td>OpenSecrets.org</td><td>2022</td><td>NGO-Bericht</td><td>Fossil-Lobby-Ausgaben in den USA</td><td><sup class="footnote-ref"><a href="#fn10" id="fnref10:1">[10:1]</a></sup></td></tr><tr><td>Umweltbundesamt</td><td>2021</td><td>Regierungsbericht</td><td>Umweltschädliche Subventionen in Deutschland</td><td><sup class="footnote-ref"><a href="#fn11" id="fnref11:1">[11:1]</a></sup></td></tr><tr><td>CDP &amp; Climate Accountability Institute</td><td>2017</td><td>NGO-Bericht</td><td>Anteil der Emissionen großer fossiler Unternehmen (Carbon Majors)</td><td><sup class="footnote-ref"><a href="#fn6" id="fnref6:2">[6:2]</a></sup></td></tr></tbody></table>',27)),i(n),e[1]||(e[1]=t('<hr class="footnotes-sep"><section class="footnotes"><ol class="footnotes-list"><li id="fn1" class="footnote-item"><p>IEA &amp; IMF (2023): <em>Energy Subsidies 2022</em>. [Policy Report]. Verfügbar unter: <a href="https://www.imf.org/en/Topics/climate-change/energy-subsidies" target="_blank" rel="noreferrer">https://www.imf.org/en/Topics/climate-change/energy-subsidies</a> [Zugriff am: 20.07.2025]. <a href="#fnref1" class="footnote-backref">↩︎</a> <a href="#fnref1:1" class="footnote-backref">↩︎</a></p></li><li id="fn2" class="footnote-item"><p>UNCTAD (o. J.): <em>Investment Dispute Settlement Navigator</em>. [Offizielles Dokument]. Verfügbar unter: <a href="https://investmentpolicy.unctad.org/investment-dispute-settlement" target="_blank" rel="noreferrer">https://investmentpolicy.unctad.org/investment-dispute-settlement</a> [Zugriff am: 20.07.2025]. <a href="#fnref2" class="footnote-backref">↩︎</a> <a href="#fnref2:1" class="footnote-backref">↩︎</a></p></li><li id="fn3" class="footnote-item"><p>WTO (o. J.): <em>Climate and Trade Overview</em>. [Offizielles Dokument]. Verfügbar unter: <a href="https://www.wto.org/english/tratop_e/envir_e/climate_intro_e.htm" target="_blank" rel="noreferrer">https://www.wto.org/english/tratop_e/envir_e/climate_intro_e.htm</a> [Zugriff am: 20.07.2025]. <a href="#fnref3" class="footnote-backref">↩︎</a> <a href="#fnref3:1" class="footnote-backref">↩︎</a></p></li><li id="fn4" class="footnote-item"><p>International Energy Agency (2024): <em>World Energy Investment 2024 – Overview and key findings</em>. [Policy Report]. S. 1–2. Verfügbar unter: <a href="https://www.iea.org/reports/world-energy-investment-2024/overview-and-key-findings" target="_blank" rel="noreferrer">https://www.iea.org/reports/world-energy-investment-2024/overview-and-key-findings</a> [Zugriff am: 20.07.2025]. <a href="#fnref4" class="footnote-backref">↩︎</a> <a href="#fnref4:1" class="footnote-backref">↩︎</a></p></li><li id="fn5" class="footnote-item"><p>U.S. SEC (o. J.): <em>Company Filings Database</em>. [Regulatorische Datenquelle]. Verfügbar unter: <a href="https://www.sec.gov/edgar/searchedgar/companysearch.html" target="_blank" rel="noreferrer">https://www.sec.gov/edgar/searchedgar/companysearch.html</a> [Zugriff am: 20.07.2025]. <a href="#fnref5" class="footnote-backref">↩︎</a> <a href="#fnref5:1" class="footnote-backref">↩︎</a></p></li><li id="fn6" class="footnote-item"><p>CDP &amp; Climate Accountability Institute (2017): <em>Carbon Majors Report</em>. Richard Heede. [NGO-Bericht]. Verfügbar unter: <a href="https://cdn.cdp.net/cdp-production/cms/reports/documents/000/002/327/original/Carbon-Majors-Report-2017.pdf" target="_blank" rel="noreferrer">https://cdn.cdp.net/cdp-production/cms/reports/documents/000/002/327/original/Carbon-Majors-Report-2017.pdf</a> [Zugriff am: 20.07.2025]. <a href="#fnref6" class="footnote-backref">↩︎</a> <a href="#fnref6:1" class="footnote-backref">↩︎</a> <a href="#fnref6:2" class="footnote-backref">↩︎</a></p></li><li id="fn7" class="footnote-item"><p>InfluenceMap (2020): <em>Lobbying on the EU Taxonomy’s Green Criteria</em>. [NGO-Bericht]. Verfügbar unter: <a href="https://influencemap.org/EN/report/Lobbying-on-the-EU-Taxonomy-s-Green-Criteria-9fa94d19d713248426018f89410d2fbd" target="_blank" rel="noreferrer">https://influencemap.org/EN/report/Lobbying-on-the-EU-Taxonomy-s-Green-Criteria-9fa94d19d713248426018f89410d2fbd</a> [Zugriff am: 20.07.2025]. <a href="#fnref7" class="footnote-backref">↩︎</a> <a href="#fnref7:1" class="footnote-backref">↩︎</a></p></li><li id="fn8" class="footnote-item"><p>InfluenceMap (2022): <em>Finance and Climate Change: A Comprehensive Climate Assessment of the World’s Largest Financial Institutions</em>. [NGO-Bericht]. Verfügbar unter: <a href="https://g20sfwg.org/wp-content/uploads/2022/07/Finance-and-Climate-Change.-A-Comprehensive-Climate-Assessment-of-the-Worlds-Largest-Financial-Institutions-InfluenceMap.pdf" target="_blank" rel="noreferrer">https://g20sfwg.org/wp-content/uploads/2022/07/Finance-and-Climate-Change.-A-Comprehensive-Climate-Assessment-of-the-Worlds-Largest-Financial-Institutions-InfluenceMap.pdf</a> [Zugriff am: 20.07.2025]. <a href="#fnref8" class="footnote-backref">↩︎</a> <a href="#fnref8:1" class="footnote-backref">↩︎</a></p></li><li id="fn9" class="footnote-item"><p>Insure Our Future (2023): <em>Fossil Fuel Insurance Scorecard 2023</em>. [NGO-Bericht]. Verfügbar unter: <a href="https://global.insure-our-future.com/wp-content/uploads/2023/11/IOF-2023-Scorecard.pdf" target="_blank" rel="noreferrer">https://global.insure-our-future.com/wp-content/uploads/2023/11/IOF-2023-Scorecard.pdf</a> [Zugriff am: 20.07.2025]. <a href="#fnref9" class="footnote-backref">↩︎</a> <a href="#fnref9:1" class="footnote-backref">↩︎</a></p></li><li id="fn10" class="footnote-item"><p>OpenSecrets.org (2022): <em>Fossil Fuel Industry Lobbying Totals</em>. [NGO-Bericht]. Verfügbar unter: <a href="https://www.opensecrets.org/federal-lobbying/sectors/summary?id=E" target="_blank" rel="noreferrer">https://www.opensecrets.org/federal-lobbying/sectors/summary?id=E</a> [Zugriff am: 20.07.2025]. <a href="#fnref10" class="footnote-backref">↩︎</a> <a href="#fnref10:1" class="footnote-backref">↩︎</a></p></li><li id="fn11" class="footnote-item"><p>Umweltbundesamt (2021): <em>Umweltschädliche Subventionen in Deutschland – Aktualisierte Ausgabe 2021</em>. [Regierungsbericht]. Verfügbar unter: <a href="https://www.umweltbundesamt.de/publikationen/umweltschaedliche-subventionen-in-deutschland-0" target="_blank" rel="noreferrer">https://www.umweltbundesamt.de/publikationen/umweltschaedliche-subventionen-in-deutschland-0</a> [Zugriff am: 20.07.2025]. <a href="#fnref11" class="footnote-backref">↩︎</a> <a href="#fnref11:1" class="footnote-backref">↩︎</a></p></li></ol></section>',2))])}const b=r(d,[["render",l]]);export{p as __pageData,b as default};
