import{_ as r,C as n,c as s,o as i,ag as a,G as d}from"./chunks/framework.S-Qvb3wi.js";const k=JSON.parse('{"title":"10.2 Glossar","description":"Schlüsselbegriffe aus Medien, Psychologie, Politik und Klimawissenschaft – kompakt erklärt, um systemische Muster schneller zu durchschauen.\\n","frontmatter":{"title":"10.2 Glossar","description":"Schlüsselbegriffe aus Medien, Psychologie, Politik und Klimawissenschaft – kompakt erklärt, um systemische Muster schneller zu durchschauen.\\n"},"headers":[],"relativePath":"10_anhang/02_glossar.md","filePath":"10_anhang/02_glossar.md","lastUpdated":1753279356000}'),o={name:"10_anhang/02_glossar.md"};function h(l,e,g,u,c,m){const t=n("Footer");return i(),s("div",null,[e[0]||(e[0]=a('<h1 id="_10-2-glossar-–-fur-alle-die-tiefer-graben-wollen" tabindex="-1">10.2 Glossar – Für alle, die tiefer graben wollen <a class="header-anchor" href="#_10-2-glossar-–-fur-alle-die-tiefer-graben-wollen" aria-label="Permalink to &quot;10.2 Glossar – Für alle, die tiefer graben wollen&quot;">​</a></h1><p>Manche Begriffe tauchen im Text nur am Rand auf – und tragen doch ganze Argumentationslinien. Dieses Glossar hilft dir dabei, <strong>systemische Zusammenhänge schneller und präziser zu erkennen</strong>.</p><p>Die Begriffe sind thematisch geordnet: von Mediendynamiken über psychologische Mechanismen bis hin zu politischen Strukturen und wissenschaftlichen Grundlagen. Ideal zum Nachschlagen oder gezielten Vertiefen.</p><h2 id="📰-medien-diskurse" tabindex="-1">📰 Medien &amp; Diskurse <a class="header-anchor" href="#📰-medien-diskurse" aria-label="Permalink to &quot;📰 Medien &amp; Diskurse&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Begriff</th><th>Erklärung</th></tr></thead><tbody><tr><td><strong>Disinformation</strong></td><td>Vorsätzliche Verbreitung falscher oder irreführender Informationen – oft zur Delegitimierung von Klimaschutz.</td></tr><tr><td><strong>Deutungshoheit</strong></td><td>Wer bestimmt, was als „normal“, „pragmatisch“ oder „extrem“ gilt – ein zentrales Instrument diskursiver Macht.</td></tr><tr><td><strong>False Balance</strong></td><td>Trügerisches Gleichgewicht: Wissenschaftlicher Konsens und Einzelmeinung erscheinen gleichwertig – das führt zu Verunsicherung statt Aufklärung.</td></tr><tr><td><strong>Gleichgewichtsillusion</strong></td><td>Die Vorstellung, Wahrheit liege „in der Mitte“ – auch wenn Fakten klar für eine Seite sprechen. Führt zu irreführender Neutralität.</td></tr><tr><td><strong>Greenwashing</strong></td><td>Strategie, bei der Unternehmen sich öffentlich als umweltfreundlich darstellen – während ihre tatsächliche Praxis dem widerspricht.</td></tr><tr><td><strong>Verzögerungsdiskurse</strong></td><td>Rhetoriken, die Handlungsdruck verschieben – etwa durch Technikgläubigkeit, Problemverharmlosung oder vermeintlichen Pragmatismus.</td></tr><tr><td><strong>Narrativ</strong></td><td>Deutungsmuster, das komplexe Zusammenhänge in eingängige Geschichten übersetzt – und so unser Bild von Realität mitprägt.</td></tr><tr><td><strong>„Unsichere Wissenschaft“</strong></td><td>Framing-Strategie zur Schwächung von Fakten – durch übermäßige Betonung verbleibender Unsicherheiten.</td></tr><tr><td><strong>Framing</strong></td><td>Sprache beeinflusst Bewertung: Schon Begriffe wie „Belastung“ vs. „Schutz“ lassen politische Maßnahmen unterschiedlich erscheinen.</td></tr><tr><td><strong>Greenhushing</strong></td><td>Taktisches Schweigen über Umweltziele – aus Angst vor öffentlicher Kritik oder juristischer Verantwortung.</td></tr><tr><td><strong>Dramaturgische Inszenierung</strong></td><td>Medienformate, die Konflikte überbetonen, statt Inhalte differenziert zu erklären – oft zulasten wissenschaftlicher Genauigkeit.</td></tr><tr><td><strong>Agenda-Setting</strong></td><td>Einfluss darauf, welche Themen öffentlich sichtbar werden – und welche systematisch ausgeblendet bleiben.</td></tr></tbody></table><h2 id="🧠-psychologie-verhalten" tabindex="-1">🧠 Psychologie &amp; Verhalten <a class="header-anchor" href="#🧠-psychologie-verhalten" aria-label="Permalink to &quot;🧠 Psychologie &amp; Verhalten&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Begriff</th><th>Erklärung</th></tr></thead><tbody><tr><td><strong>Kognitive Dissonanz</strong></td><td>Innerer Widerspruch zwischen Wissen und Handeln – <em>„Ich weiß es, aber ich tue es nicht.“</em></td></tr><tr><td><strong>Moral Licensing</strong></td><td>Der psychologische Effekt, dass eine gute Tat eine schlechte rechtfertigt. Beispiel: Ökostrom kaufen – und dann doch fliegen.</td></tr><tr><td><strong>Selbstwirksamkeit</strong></td><td>Die Überzeugung, durch eigenes Handeln etwas bewirken zu können – zentral für Motivation und Handlungsbereitschaft.</td></tr><tr><td><strong>Soft Denial</strong></td><td>Leises Leugnen: <em>„Ja, aber …“</em> – Zustimmung zur Problemlage, aber Ausweichen vor Konsequenzen.</td></tr><tr><td><strong>Kollektive Wirksamkeit</strong></td><td>Glaube an die Veränderungskraft von Gruppen – wirkt stärkend gegen Ohnmachtsgefühle.</td></tr><tr><td><strong>Krisenkonkurrenz</strong></td><td>Eindruck, dass andere Krisen (z. B. Krieg, Inflation) vorrangig seien – das Klima rückt dadurch in den Hintergrund.</td></tr><tr><td><strong>Psychologische Resignation</strong></td><td>Gefühl, dass nichts mehr hilft – entsteht oft durch Überforderung, Dauerkrise oder fehlende Rückmeldung auf Handeln.</td></tr></tbody></table><h2 id="🏛️-politik-macht" tabindex="-1">🏛️ Politik &amp; Macht <a class="header-anchor" href="#🏛️-politik-macht" aria-label="Permalink to &quot;🏛️ Politik &amp; Macht&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Begriff</th><th>Erklärung</th></tr></thead><tbody><tr><td><strong>Governance Lücke</strong></td><td>Diskrepanz zwischen wissenschaftlichem Wissen über Klimarisiken und politischem Handeln – oft durch Strukturdefizite.</td></tr><tr><td><strong>Lobbyismus</strong></td><td>Gezielte Einflussnahme auf politische Entscheidungen – meist im Interesse wirtschaftlicher oder industrieller Akteure.</td></tr><tr><td><strong>Konsensprinzip</strong></td><td>Entscheidungsregel in UN-Verhandlungen: Jeder Staat muss zustimmen – strukturelle Blockaden sind dadurch vorprogrammiert.</td></tr><tr><td><strong>ISDS-Klauseln</strong></td><td>Investor-Staat-Schiedsverfahren: Konzerne können Staaten verklagen, wenn Gesetze ihre Gewinnerwartung beeinträchtigen.</td></tr><tr><td><strong>Recht als Machtinstrument</strong></td><td>Nutzung juristischer Verfahren nicht zur Klärung von Sachverhalten, sondern zur Verteidigung ökonomischer Interessen – etwa durch ISDS-Klagen gegen Klimagesetze.</td></tr><tr><td><strong>Symbolpolitik</strong></td><td>Politische Rhetorik ohne bindende Maßnahmen – erzeugt öffentliche Wirkung, verhindert aber tiefgreifende Veränderungen.</td></tr><tr><td><strong>Freihandelslogik</strong></td><td>Politik, bei der Marktinteressen Vorrang haben – oft zulasten von Umwelt- oder Menschenrechtsstandards.</td></tr></tbody></table><h2 id="🧩-systemlogik-struktur" tabindex="-1">🧩 Systemlogik &amp; Struktur <a class="header-anchor" href="#🧩-systemlogik-struktur" aria-label="Permalink to &quot;🧩 Systemlogik &amp; Struktur&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Begriff</th><th>Erklärung</th></tr></thead><tbody><tr><td><strong>Carbon Lock-In</strong></td><td>Festgefahrene Abhängigkeit von fossilen Infrastrukturen – macht den Ausstieg teuer und politisch riskant.</td></tr><tr><td><strong>Diskursmacht</strong></td><td>Die Fähigkeit, Begriffe, Deutungsrahmen und Narrative so zu setzen, dass sie gesellschaftliche Wahrnehmung und politische Entscheidungen prägen.</td></tr><tr><td><strong>Epistemische Gewalt</strong></td><td>Strukturelle Unterdrückung oder Abwertung bestimmter Wissensformen – etwa indigener oder postkolonialer Perspektiven – zugunsten „westlicher“ Deutungsmacht.</td></tr><tr><td><strong>Korrekturpotenzial</strong></td><td>Grad der realistischen Möglichkeit, eine systemisch verankerte Strategie zu verändern – etwa durch Öffentlichkeit, Rechtsprechung oder politischen Druck.</td></tr><tr><td><strong>Kostenwahrheit</strong></td><td>Narrative Verzerrung: Klimaschutz erscheint teuer, ohne die viel höheren Folgekosten des Nicht-Handelns mitzudenken.</td></tr><tr><td><strong>Postkoloniale Ungleichheit</strong></td><td>Historisch gewachsene Machtasymmetrien, die Klimarisiken, Ressourcen und Verantwortlichkeiten global ungleich verteilen.</td></tr><tr><td><strong>Systemische Verzögerung</strong></td><td>Zusammenspiel ökonomischer, politischer und kultureller Faktoren, das Wandel verlangsamt oder verhindert.</td></tr><tr><td><strong>Systemisches Versagen</strong></td><td>Ausbleibende Reaktion trotz Wissen – durch institutionelle Trägheit, diffuse Verantwortung und ungleiche Machtverhältnisse.</td></tr><tr><td><strong>Technikversprechen</strong></td><td>Die Hoffnung auf zukünftige Technologien dient als Vorwand, heute nicht handeln zu müssen.</td></tr><tr><td><strong>Scheinkompromiss</strong></td><td>Maßnahme oder Formulierung, die Fortschritt suggeriert – aber real keine Wirkung entfaltet.</td></tr><tr><td><strong>Soft Power</strong></td><td>Einfluss durch Image, Sprache und Normen – also ohne Zwang oder juristische Mittel.</td></tr><tr><td><strong>Verantwortungsdiffusion</strong></td><td>Wenn Zuständigkeiten unklar verteilt sind, sinkt die Wahrscheinlichkeit wirksamer Maßnahmen – auch bei kollektivem Wissen.</td></tr></tbody></table><h2 id="🔬-wissenschaft-institutionen" tabindex="-1">🔬 Wissenschaft &amp; Institutionen <a class="header-anchor" href="#🔬-wissenschaft-institutionen" aria-label="Permalink to &quot;🔬 Wissenschaft &amp; Institutionen&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Begriff</th><th>Erklärung</th></tr></thead><tbody><tr><td><strong>Peer-reviewed</strong></td><td>Fachliche Prüfung wissenschaftlicher Arbeiten – kein Wahrheitsgarant, aber methodischer Mindeststandard im Forschungsprozess.</td></tr><tr><td><strong>IPCC</strong></td><td>Zwischenstaatliches UN-Gremium zur Bündelung des Klimaforschungsstands – zentrale Basis für internationale Klimapolitik.</td></tr><tr><td><strong>Kipppunkte</strong></td><td>Kritische Schwellen im Erdsystem – werden sie überschritten, drohen unumkehrbare Veränderungen (z. B. Abschmelzen von Eisschilden).</td></tr><tr><td><strong>CCS</strong></td><td>„Carbon Capture and Storage“ – CO₂-Abscheidung und Speicherung als technischer Hoffnungsträger mit vielen offenen Fragen.</td></tr><tr><td><strong>Geoengineering</strong></td><td>Gezielte Eingriffe ins Klimasystem zur Temperaturkontrolle – wissenschaftlich umstritten und ethisch hochproblematisch.</td></tr></tbody></table><h2 id="🧱-kernbegriffe" tabindex="-1">🧱 Kernbegriffe <a class="header-anchor" href="#🧱-kernbegriffe" aria-label="Permalink to &quot;🧱 Kernbegriffe&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Begriff</th><th>Erklärung</th></tr></thead><tbody><tr><td><strong>Klimawandel</strong></td><td>Langfristige Veränderung des globalen Klimas – aktuell vor allem durch menschliches Handeln verursacht.</td></tr><tr><td><strong>Klimakrise</strong></td><td>Zuspitzung des Klimawandels – betont Dringlichkeit, Systemrelevanz und Eskalationsrisiken.</td></tr><tr><td><strong>Klimapolitik</strong></td><td>Strategien zur Emissionsminderung und zur Anpassung an die Folgen des Klimawandels.</td></tr><tr><td><strong>Soziale Kipppunkte</strong></td><td>Wendepunkte im gesellschaftlichen Wandel – nach deren Überschreiten verändern sich Einstellungen rasant.</td></tr></tbody></table><blockquote><p><strong>Tipp:</strong> Weitere Rechercheinstrumente findest du im <em>Werkzeugkasten</em> oder in offenen Archiven wie <a href="https://climatefiles.org" target="_blank" rel="noreferrer">climatefiles.org</a> und <a href="https://influenceMap.org" target="_blank" rel="noreferrer">influenceMap.org</a>.</p></blockquote>',16)),d(t)])}const b=r(o,[["render",h]]);export{k as __pageData,b as default};
