import{_ as r,C as i,c as t,o as s,ag as a,G as l}from"./chunks/framework.S-Qvb3wi.js";const b=JSON.parse('{"title":"10.3 Zielgruppen-Module","description":"Konkrete Einsatzideen für Lehrkräfte, Kommunen, NGOs, Medien und alle, die das Dossier in ihrem Kontext lebendig machen wollen.\\n","frontmatter":{"title":"10.3 Zielgruppen-Module","description":"Konkrete Einsatzideen für Lehrkräfte, Kommunen, NGOs, Medien und alle, die das Dossier in ihrem Kontext lebendig machen wollen.\\n"},"headers":[],"relativePath":"10_anhang/03_zielgruppen.md","filePath":"10_anhang/03_zielgruppen.md","lastUpdated":1753279356000}'),o={name:"10_anhang/03_zielgruppen.md"};function u(d,e,h,g,c,p){const n=i("Footer");return s(),t("div",null,[e[0]||(e[0]=a('<h1 id="_10-3-zielgruppen-module-–-wie-du-dieses-dossier-einsetzen-kannst" tabindex="-1">10.3 Zielgruppen-Module – Wie du dieses Dossier einsetzen kannst <a class="header-anchor" href="#_10-3-zielgruppen-module-–-wie-du-dieses-dossier-einsetzen-kannst" aria-label="Permalink to &quot;10.3 Zielgruppen-Module – Wie du dieses Dossier einsetzen kannst&quot;">​</a></h1><p>Dieses Dossier ist kein Lehrbuch – <strong>sondern ein Werkzeug</strong>.<br> Die zentrale Frage lautet daher nicht nur: <em>Was steht drin?</em><br> Sondern vor allem: <em>Was kannst du damit tun – in deinem Kontext?</em></p><p>Das folgende Kapitel liefert praxisnahe Anregungen für verschiedene Rollen und Akteur:innen. Es versteht sich als modulares Angebot zur Weiterverwendung, Anpassung und gemeinsamen Weiterentwicklung.</p><h2 id="🎓-fur-lehrkrafte-und-bildungsakteur-innen" tabindex="-1">🎓 Für Lehrkräfte und Bildungsakteur:innen <a class="header-anchor" href="#🎓-fur-lehrkrafte-und-bildungsakteur-innen" aria-label="Permalink to &quot;🎓 Für Lehrkräfte und Bildungsakteur:innen&quot;">​</a></h2><p><strong>Ideen für den Einsatz in Schule und außerschulischer Bildung:</strong></p><ul><li><p><strong>Szenenanalyse im Unterricht</strong><br> Schüler:innen untersuchen die fiktionalen Szenen des Dossiers:<br> Wer handelt? Wer reagiert? Und wer bleibt still?</p></li><li><p><strong>Projektarbeit: &quot;Verzögerung im Schulalltag&quot;</strong><br> Alltägliche Beispiele aufgreifen – etwa bei der Planung von Klassenfahrten, dem Schulessen oder bei digitalen Ausstattungsvorhaben.</p></li><li><p><strong>Kreativformate entwickeln</strong><br> Ausstellung, Hörspiel oder Theaterstück:<br> Wie lassen sich die Erzählungen des Dossiers mit der eigenen Lebensrealität verknüpfen?<br><em>Systemische Geschichten aus dem Jetzt.</em></p></li></ul><blockquote><p><strong>Fachübergreifender Tipp:</strong><br> Das Thema eignet sich hervorragend für Politik-, MINT-, Ethik- oder Deutschunterricht –<br> denn systemische Trägheit betrifft viele Bereiche zugleich.</p></blockquote><h2 id="🏛️-fur-kommunen-und-lokale-entscheider-innen" tabindex="-1">🏛️ Für Kommunen und lokale Entscheider:innen <a class="header-anchor" href="#🏛️-fur-kommunen-und-lokale-entscheider-innen" aria-label="Permalink to &quot;🏛️ Für Kommunen und lokale Entscheider:innen&quot;">​</a></h2><p><strong>Impulse für kommunale Praxis und lokale Klimapolitik:</strong></p><ul><li><p><strong>Fossile Werbung ausschließen</strong><br> Satzungen für Werbeflächen im öffentlichen Raum prüfen – z. B. bei Bussen, Gebäuden oder Stadtfesten.<br> → Vorbilder: Amsterdam und Stockholm.</p></li><li><p><strong>Klimaräte einführen</strong><br> Geloste Bürgerräte ermöglichen mutige und demokratisch legitimierte Entscheidungen.<br> → Werkzeuge und Beispiele: <a href="https://citizensassembly.eu" target="_blank" rel="noreferrer">https://citizensassembly.eu</a></p></li><li><p><strong>Förderpraxis hinterfragen</strong><br> Welche kommunalen Förderungen oder Bauprojekte stärken noch fossile Infrastrukturen?<br> Eine gezielte Bestandsaufnahme lohnt sich.</p></li></ul><blockquote><p><strong>Umsetzungstipp:</strong><br> Oft reicht eine Änderung bei Ausschreibungen oder Standards – mit großer struktureller Wirkung.</p></blockquote><h2 id="📢-fur-multiplikator-innen-ngos-und-medien" tabindex="-1">📢 Für Multiplikator:innen, NGOs und Medien <a class="header-anchor" href="#📢-fur-multiplikator-innen-ngos-und-medien" aria-label="Permalink to &quot;📢 Für Multiplikator:innen, NGOs und Medien&quot;">​</a></h2><p><strong>Anwendung im Bereich Kommunikation, Recherche und Aktivierung:</strong></p><ul><li><p><strong>Sprache als Machtmittel analysieren</strong><br> Politische Programme, PR-Texte oder Strategiepapiere untersuchen:<br> Welche Begriffe verschleiern Untätigkeit oder erzeugen Scheinalternativen?<br> → Beispiele: <em>&quot;Technologieoffenheit&quot;</em>, <em>&quot;nachhaltiges Wachstum&quot;</em></p></li><li><p><strong>Mediale Adaptionen entwickeln</strong><br> Inhalte des Dossiers in neue Formate übersetzen:<br> → Podcast-Reihe <em>„Verzögerung als Strategie“</em><br> → Social-Media-Karussell oder grafisches Story-Format zu systemischen Bruchstellen</p></li><li><p><strong>Regionale Kontexte einarbeiten</strong><br> Eigene Recherchen ergänzen das Dossier:<br> Welche lokalen Lobbystrukturen, fossilen Projekte oder politischen Hürden sind relevant?</p></li></ul><blockquote><p><strong>Organisationsinterner Tipp:</strong><br> Nutzt das Dossier nicht nur extern – sondern auch als Reflexionsgrundlage für eure eigene Strategie- oder Bildungsarbeit.</p></blockquote><h2 id="💼-fur-unternehmen-und-berater-innen" tabindex="-1">💼 Für Unternehmen und Berater:innen <a class="header-anchor" href="#💼-fur-unternehmen-und-berater-innen" aria-label="Permalink to &quot;💼 Für Unternehmen und Berater:innen&quot;">​</a></h2><p><strong>Einsatz als Reflexions- und Transformationswerkzeug:</strong></p><ul><li><p><strong>&quot;Netto-Null&quot;-Selbstcheck durchführen</strong><br> Stimmen öffentliche Aussagen, Investitionen und Strategiepläne wirklich überein –<br> oder dominiert <em>Greenhushing</em> (bewusstes Verschweigen ökologischer Ambitionen)?</p></li><li><p><strong>Awareness-Schulungen entwickeln</strong><br> Inhalte gezielt in ESG-, Kommunikations- oder Nachhaltigkeitstrainings einbinden.</p></li><li><p><strong>Narrative kritisch hinterfragen</strong><br> Welche Geschichten erzählt das Unternehmen über Wandel, Verantwortung oder Innovation –<br> und wie glaubwürdig sind sie?</p></li></ul><blockquote><p><strong>Kommunikationstipp:</strong><br> Glaubwürdigkeit beginnt damit, eigene Narrative sichtbar zu machen –<br> und bei Bedarf neu zu justieren.</p></blockquote><h2 id="🎭-fur-kulturschaffende-und-kreativbranchen" tabindex="-1">🎭 Für Kulturschaffende und Kreativbranchen <a class="header-anchor" href="#🎭-fur-kulturschaffende-und-kreativbranchen" aria-label="Permalink to &quot;🎭 Für Kulturschaffende und Kreativbranchen&quot;">​</a></h2><p><strong>Gestalterische Umsetzungen zwischen Kunst und Politik:</strong></p><ul><li><p><strong>Systemlogiken erzählen</strong><br> Im Mittelpunkt: Nicht nur die Klimakrise – sondern auch die Mechanismen von Verzögerung, Ausblendung und Ablenkung.<br> Wer profitiert? Wer verliert? Wer wird beruhigt?</p></li><li><p><strong>Szenen adaptieren</strong><br> Theater, Comic, Audiofeature oder Installation:<br> Was passiert, wenn das Dossier performativ, visuell oder räumlich umgesetzt wird?</p></li><li><p><strong>Interdisziplinäre Allianzen bilden</strong><br> Zusammenarbeit von Kunst, Journalismus und Wissenschaft:<br> → Beispiele: <a href="https://climateclock.world" target="_blank" rel="noreferrer">https://climateclock.world</a>, <a href="https://notanagency.eu" target="_blank" rel="noreferrer">https://notanagency.eu</a></p></li></ul><blockquote><p><strong>Narrativ-Tipp:</strong><br> Kunst verändert keine Fakten – aber sie erweitert den Raum für das,<br> was Menschen für möglich halten.</p></blockquote><h2 id="🧪-fur-hochschulen-und-wissenschaftliche-akteur-innen" tabindex="-1">🧪 Für Hochschulen und wissenschaftliche Akteur:innen <a class="header-anchor" href="#🧪-fur-hochschulen-und-wissenschaftliche-akteur-innen" aria-label="Permalink to &quot;🧪 Für Hochschulen und wissenschaftliche Akteur:innen&quot;">​</a></h2><p><strong>Verwendung in Lehre, Forschung und transdisziplinärer Praxis:</strong></p><ul><li><p><strong>Seminare mit systemischer Perspektive</strong><br> Fokus: <em>Verzögerung als Struktur</em> – etwa in Politikwissenschaft, Kommunikationsforschung oder Umweltethik.</p></li><li><p><strong>Fallstudie für Forschung und Analyse</strong><br> Anwendung auf Machtverhältnisse, Framingprozesse oder soziale Kipppunkte in der Klimapolitik.</p></li><li><p><strong>Reallabore und Think-and-Do-Tanks etablieren</strong><br> Kollaborationen zwischen Wissenschaft, Aktivismus und kommunaler Praxis fördern.<br> Ziel: Bestehende Klimastrategien kritisch und praxisnah weiterdenken.</p></li></ul><blockquote><p><strong>Wissenschaftstipp:</strong><br> Das Dossier ist kein abgeschlossenes Objekt –<br> sondern ein Resonanzraum für systemisches Denken und transformative Impulse.</p></blockquote><h2 id="🧒-fur-bildung-jugend-und-sozialarbeit" tabindex="-1">🧒 Für Bildung, Jugend- und Sozialarbeit <a class="header-anchor" href="#🧒-fur-bildung-jugend-und-sozialarbeit" aria-label="Permalink to &quot;🧒 Für Bildung, Jugend- und Sozialarbeit&quot;">​</a></h2><p><strong>Zugänge für handlungsorientierte Bildungsarbeit:</strong></p><ul><li><p><strong>Workshops: Klimaverzögerung im Alltag</strong><br> Peer-Formate, die Erfahrungswissen aktivieren und neue Handlungsräume eröffnen.</p></li><li><p><strong>Fiktionale Szenen als Gesprächseinstieg</strong><br> Szenen aus dem Dossier nutzen, um über Alltagswahrnehmung, Biografien<br> oder systemische Trägheit ins Gespräch zu kommen.</p></li><li><p><strong>Gerechtigkeitsfragen einbinden</strong><br> Ökologische Themen mit sozialer und kultureller Gerechtigkeit verbinden –<br> etwa in queeren, migrantischen oder prekären Lebensrealitäten.</p></li></ul><blockquote><p><strong>Empowerment-Tipp:</strong><br> Systemkritik braucht keine Fachsprache –<br> sondern Räume, in denen persönliche Erfahrungen auf politische Strukturen treffen.</p></blockquote><h2 id="🔄-allgemeiner-hinweis-zur-weiterverwendung" tabindex="-1">🔄 Allgemeiner Hinweis zur Weiterverwendung <a class="header-anchor" href="#🔄-allgemeiner-hinweis-zur-weiterverwendung" aria-label="Permalink to &quot;🔄 Allgemeiner Hinweis zur Weiterverwendung&quot;">​</a></h2><p>Alle Module dürfen <strong>frei genutzt, angepasst und weiterentwickelt</strong> werden –<br> ob für Workshops, Unterricht, Kampagnen oder politische Prozesse.</p><p>Die Inhalte stehen unter der Lizenz <a href="https://creativecommons.org/licenses/by-sa/4.0" target="_blank" rel="noreferrer">CC BY-SA 4.0</a> – das heißt:</p><blockquote><p>Der Text gehört nicht dem Urheber – sondern <strong>denen, die ihn einsetzen.</strong><br> Wenn du etwas veränderst, gib an, was von dir stammt.<br> Und gib dein Werk unter derselben Lizenz weiter: <strong>CC BY-SA 4.0</strong></p></blockquote>',35)),l(n)])}const k=r(o,[["render",u]]);export{b as __pageData,k as default};
