import{_ as r,C as i,c as a,o as n,ag as s,G as l}from"./chunks/framework.S-Qvb3wi.js";const c=JSON.parse('{"title":"11.1 Das Verzögerungssystem","description":"","frontmatter":{"title":"11.1 Das Verzögerungssystem"},"headers":[],"relativePath":"11_didaktik/01_verzoegerungssystem.md","filePath":"11_didaktik/01_verzoegerungssystem.md","lastUpdated":1753279356000}'),o={name:"11_didaktik/01_verzoegerungssystem.md"};function d(u,e,g,h,m,k){const t=i("Footer");return n(),a("div",null,[e[0]||(e[0]=s('<h1 id="_11-1-das-verzogerungssystem" tabindex="-1">11.1 Das Verzögerungssystem <a class="header-anchor" href="#_11-1-das-verzogerungssystem" aria-label="Permalink to &quot;11.1 Das Verzögerungssystem&quot;">​</a></h1><h2 id="🧭-ziel" tabindex="-1">🧭 Ziel <a class="header-anchor" href="#🧭-ziel" aria-label="Permalink to &quot;🧭 Ziel&quot;">​</a></h2><p>Lernende verstehen, dass die ausbleibende Reaktion auf die Klimakrise kein Zufall ist – sondern auf ein strukturell wirksames <strong>Verzögerungssystem</strong> zurückgeht. Sie lernen die zentralen Akteure, Muster und Mechanismen kennen – und analysieren, wie Verantwortung verschoben und Handlung neutralisiert wird.</p><h2 id="🎯-lernziele" tabindex="-1">🎯 Lernziele <a class="header-anchor" href="#🎯-lernziele" aria-label="Permalink to &quot;🎯 Lernziele&quot;">​</a></h2><ul><li>Die Rolle von Wissenschaft, Politik, Industrie, Medien und Gesellschaft im Systemkontext reflektieren</li><li>Strategien institutionalisierter Verzögerung erkennen (z. B. False Balance, Symbolpolitik)</li><li>Narrative und Legitimationsmuster identifizieren</li><li>Eigene Bewertungen argumentativ begründen</li></ul><h2 id="🗂️-inhalte-auszug-aus-dem-dossier" tabindex="-1">🗂️ Inhalte (auszug aus dem Dossier) <a class="header-anchor" href="#🗂️-inhalte-auszug-aus-dem-dossier" aria-label="Permalink to &quot;🗂️ Inhalte (auszug aus dem Dossier)&quot;">​</a></h2><blockquote><p><em>&quot;Die Klimakrise ist kein Wissensproblem – sondern ein Systemproblem. Verzögerung ist keine Panne, sondern Architektur.&quot;</em></p></blockquote><h3 id="kernelemente-des-verzogerungssystems" tabindex="-1">Kernelemente des Verzögerungssystems <a class="header-anchor" href="#kernelemente-des-verzogerungssystems" aria-label="Permalink to &quot;Kernelemente des Verzögerungssystems&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Bereich</th><th>Verzögerungsmechanismus</th></tr></thead><tbody><tr><td>Wissenschaft</td><td>Entkoppelung von Mandat und Wirkung</td></tr><tr><td>Politik</td><td>Zielrhetorik ohne Sanktionslogik</td></tr><tr><td>Industrie</td><td>Strategische Ablenkung, Greenwashing, Lobbies</td></tr><tr><td>Medien</td><td>False Balance, Kurzfristlogik</td></tr><tr><td>Gesellschaft</td><td>Überforderung, Individualisierung, Fragmentierung</td></tr></tbody></table><h2 id="🧠-begriffe-konzepte" tabindex="-1">🧠 Begriffe &amp; Konzepte <a class="header-anchor" href="#🧠-begriffe-konzepte" aria-label="Permalink to &quot;🧠 Begriffe &amp; Konzepte&quot;">​</a></h2><ul><li><strong>Carbon Lock-In</strong></li><li><strong>False Balance</strong></li><li><strong>Verantwortungsverschiebung</strong></li><li><strong>Narrative der Wirksamkeit (&quot;Wir tun doch was!&quot;)</strong></li><li><strong>Symbolpolitik vs. Strukturpolitik</strong></li></ul><h2 id="🧩-methoden-aufgaben" tabindex="-1">🧩 Methoden / Aufgaben <a class="header-anchor" href="#🧩-methoden-aufgaben" aria-label="Permalink to &quot;🧩 Methoden / Aufgaben&quot;">​</a></h2><h3 id="_1-einstieg-impuls" tabindex="-1">1. Einstieg (Impuls) <a class="header-anchor" href="#_1-einstieg-impuls" aria-label="Permalink to &quot;1. Einstieg (Impuls)&quot;">​</a></h3><blockquote><p>„Warum passiert so wenig – obwohl wir so viel wissen?“<br> Diskussion in Kleingruppen, 10 Min. → Sammlung auf Tafel oder Pad</p></blockquote><h3 id="_2-mapping-wer-verzogert-–-und-wie" tabindex="-1">2. Mapping: Wer verzögert – und wie? <a class="header-anchor" href="#_2-mapping-wer-verzogert-–-und-wie" aria-label="Permalink to &quot;2. Mapping: Wer verzögert – und wie?&quot;">​</a></h3><p>Arbeitsblatt: Tabelle ausfüllen mit konkreten Beispielen (z. B. „IPCC → Bericht → keine Umsetzung“, „Shell → Green Claims vs. Investitionen“)<br> → anschließender Austausch</p><h3 id="_3-zitatdebatte-positionierung" tabindex="-1">3. Zitatdebatte (Positionierung) <a class="header-anchor" href="#_3-zitatdebatte-positionierung" aria-label="Permalink to &quot;3. Zitatdebatte (Positionierung)&quot;">​</a></h3><blockquote><p>„Verzögerung ist kein Unfall – sie ist Architektur.“<br> → Was meint das? Ist das übertrieben? – Diskussion im Plenum</p></blockquote><h3 id="_4-transferfrage-individuell-oder-gruppe" tabindex="-1">4. Transferfrage (individuell oder Gruppe) <a class="header-anchor" href="#_4-transferfrage-individuell-oder-gruppe" aria-label="Permalink to &quot;4. Transferfrage (individuell oder Gruppe)&quot;">​</a></h3><blockquote><p>Wo erkennst du in deinem Alltag Elemente dieses Systems?<br> Welche Rolle spielt Kommunikation in der Verzögerung?</p></blockquote><h2 id="📦-materialien-empfohlen" tabindex="-1">📦 Materialien (empfohlen) <a class="header-anchor" href="#📦-materialien-empfohlen" aria-label="Permalink to &quot;📦 Materialien (empfohlen)&quot;">​</a></h2><ul><li>Auszüge aus „Die Schlinge“, Kapitel „Verzögerungssystem“ oder „Dynamiken“</li><li>Zeitleiste (1957–2025) → Optional als A3-Plakat</li><li>Zitatekarten („Verzögerung ist kein Unfall…“)</li><li>Arbeitsblatt „Mapping der Akteure“ (Tabelle)</li></ul><h2 id="⏱️-zeitstruktur-vorschlag" tabindex="-1">⏱️ Zeitstruktur (Vorschlag) <a class="header-anchor" href="#⏱️-zeitstruktur-vorschlag" aria-label="Permalink to &quot;⏱️ Zeitstruktur (Vorschlag)&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Phase</th><th>Zeit</th></tr></thead><tbody><tr><td>Einstieg / Impuls</td><td>10 Min</td></tr><tr><td>Mapping / Gruppenarbeit</td><td>20 Min</td></tr><tr><td>Diskussion / Zitate</td><td>15 Min</td></tr><tr><td>Transfer / Reflexion</td><td>10 Min</td></tr><tr><td>Abschluss / Sammlung</td><td>5 Min</td></tr></tbody></table><h2 id="🧵-weiterfuhrende-module" tabindex="-1">🧵 Weiterführende Module <a class="header-anchor" href="#🧵-weiterfuhrende-module" aria-label="Permalink to &quot;🧵 Weiterführende Module&quot;">​</a></h2><ul><li>Modul: <strong>Systemmuster – Wissen → Wirkungslosigkeit</strong></li><li>Modul: <strong>Sprache &amp; Narrative im Wandel</strong></li><li>Modul: <strong>Werkzeugkasten – Was tun gegen strukturelle Blockade?</strong></li></ul>',26)),l(t)])}const p=r(o,[["render",d]]);export{c as __pageData,p as default};
