import{_ as s,C as i,c as t,o as r,ag as a,G as l}from"./chunks/framework.S-Qvb3wi.js";const b=JSON.parse('{"title":"Prolog – Warum dieses Dossier?","description":"Einstieg in \\"Die Schlinge\\": Warum dieses Dossier entstanden ist – und was dich darin erwartet.\\n","frontmatter":{"title":"Prolog – Warum dieses Dossier?","description":"Einstieg in \\"Die Schlinge\\": Warum dieses Dossier entstanden ist – und was dich darin erwartet.\\n"},"headers":[],"relativePath":"prolog.md","filePath":"prolog.md","lastUpdated":1753279356000}'),o={name:"prolog.md"};function d(u,e,h,c,g,m){const n=i("Footer");return r(),t("div",null,[e[0]||(e[0]=a('<h1 id="prolog-–-warum-dieses-dossier" tabindex="-1">Prolog – Warum dieses Dossier? <a class="header-anchor" href="#prolog-–-warum-dieses-dossier" aria-label="Permalink to &quot;Prolog – Warum dieses Dossier?&quot;">​</a></h1><blockquote><p><em>„Wir wissen doch längst, was zu tun ist.“</em></p></blockquote><p>Vielleicht hast du diesen Satz schon oft gehört. Vielleicht hast du ihn selbst gesagt. Vielleicht spürst du beim Thema Klimakrise eine seltsame Mischung aus Klarheit und Ohnmacht:</p><p>Die Fakten sind bekannt. Die Bedrohung ist real. Die Lösungen sind da. Und doch passiert… erschreckend wenig.</p><p>Dieses Dossier beginnt genau an diesem Punkt. Nicht bei den Fakten – sondern bei der Frage: <strong>Warum wirken sie nicht?</strong></p><h2 id="🧰-was-du-hier-findest" tabindex="-1">🧰 Was du hier findest <a class="header-anchor" href="#🧰-was-du-hier-findest" aria-label="Permalink to &quot;🧰 Was du hier findest&quot;">​</a></h2><p><strong>„Die Schlinge“</strong> ist kein Bericht. Kein Appell. Keine Meinung. Sondern ein Werkzeug: für alle, die hinter die Kulissen blicken wollen.</p><p>Es zeigt, warum Wandel oft nicht an Erkenntnis scheitert – sondern an Strukturen. Warum Verzögerung kein Zufall ist – sondern System. Und was es braucht, um dieses Muster zu durchbrechen.</p><h2 id="👀-wer-weiterlesen-sollte" tabindex="-1">👀 Wer weiterlesen sollte <a class="header-anchor" href="#👀-wer-weiterlesen-sollte" aria-label="Permalink to &quot;👀 Wer weiterlesen sollte&quot;">​</a></h2><p>Dieses Dossier richtet sich an Menschen, die…</p><ul><li>das Gefühl haben, in ihrer Arbeit ständig gegen unsichtbare Wände zu laufen</li><li>wissen, dass Aufklärung allein nicht reicht</li><li>die Spielregeln verstehen wollen – und sie verändern</li></ul><p>Ob du in Bildung, Medien, Recht, Verwaltung oder Aktivismus unterwegs bist: Hier findest du keine fertigen Antworten – aber viele Anhaltspunkte, wo Blockaden wirken. Und wie sie sich lösen lassen.</p><h2 id="📖-wie-du-lesen-kannst" tabindex="-1">📖 Wie du lesen kannst <a class="header-anchor" href="#📖-wie-du-lesen-kannst" aria-label="Permalink to &quot;📖 Wie du lesen kannst&quot;">​</a></h2><ul><li>Du <strong>musst nicht vorne anfangen</strong> – jedes Kapitel steht für sich.</li><li>Du <strong>musst nichts glauben</strong> – jede Aussage ist nachvollziehbar belegt.</li><li>Du <strong>darfst mitgestalten</strong> – dieses Dossier ist offen und kollaborativ angelegt.</li></ul><hr><blockquote><p><em>Veränderung beginnt dort, wo Muster sichtbar werden.</em><br><em>Und wo wir sie gemeinsam unterbrechen.</em></p></blockquote>',16)),l(n)])}const w=s(o,[["render",d]]);export{b as __pageData,w as default};
