import{_ as r,C as i,c as t,o as s,ag as a,G as h}from"./chunks/framework.S-Qvb3wi.js";const p=JSON.parse('{"title":"Über den Autor","description":"","frontmatter":{"title":"Über den Autor"},"headers":[],"relativePath":"ueber-den-autor.md","filePath":"ueber-den-autor.md","lastUpdated":1753279356000}'),c={name:"ueber-den-autor.md"};function o(d,e,l,u,b,m){const n=i("Footer");return s(),t("div",null,[e[0]||(e[0]=a('<h1 id="uber-den-autor" tabindex="-1">Über den Autor <a class="header-anchor" href="#uber-den-autor" aria-label="Permalink to &quot;Über den Autor&quot;">​</a></h1><p><strong>Björn Böttle</strong> (<em>Jahrgang 1976</em>) lebt mit seiner Familie in <strong>Bretten</strong>.<br> Er ist verheiratet, Vater zweier Kinder – und jemand, der sich fragt,<br> warum so wenig passiert, obwohl wir längst alles wissen.</p><p>Von Beruf ist er <strong>Informatiker</strong>. In seinem Arbeitsalltag beschäftigt er sich mit komplexen Systemen, Wechselwirkungen und Abhängigkeiten.<br> Diese Denkweise hat er auf gesellschaftliche Fragen übertragen – und begonnen, sich intensiver mit den Strukturen zu befassen, die Wandel verhindern.</p><blockquote><p>„Systeme folgen Regeln. Manche fördern Veränderung. Andere verhindern sie – leise, aber wirkungsvoll.“</p></blockquote><p>Mit <em>„Die Schlinge“</em> hat Böttle kein klassisches Sachbuch geschrieben,<br> sondern ein <strong>analytisch-dokumentarisches Werkzeug</strong>:<br> für alle, die mehr verstehen wollen als das Offensichtliche –<br> und die sich fragen, <strong>warum Wissen allein nicht reicht.</strong></p><p>Sein Ziel:<br> Komplexes zugänglich machen – und Muster sichtbar,<br> die Veränderung behindern, obwohl sie dringend nötig wäre.</p><h2 id="🧾-warum-ich-dieses-buch-geschrieben-habe" tabindex="-1">🧾 Warum ich dieses Buch geschrieben habe <a class="header-anchor" href="#🧾-warum-ich-dieses-buch-geschrieben-habe" aria-label="Permalink to &quot;🧾 Warum ich dieses Buch geschrieben habe&quot;">​</a></h2><p><em>Ein persönlicher Kommentar</em></p><p>Ich bin kein Politiker. Kein Klimaforscher. Kein Journalist.<br> Ich bin Bürger – Bewohner dieses Planeten, Ehemann, Vater, Sohn, Freund.<br> Und ich bin zunehmend beunruhigt. Und, ja: auch verärgert.</p><p>Nicht über das, was wir noch nicht wissen.<br> Sondern über das, was wir längst wissen – und trotzdem nicht tun.</p><p>Mich beschäftigt nicht das fehlende Wissen. Sondern das, was daraus (nicht) folgt.<br> Mich besorgt das Einlullen, das Vertrösten, das ständige Hinauszögern –<br> die Gleichgültigkeit, die sich zwischen all dem breitmacht.</p><p>Ich kann das nicht mehr hören:<br> „Wir müssen alle mitnehmen.“ – „Wir haben noch Zeit.“ – „Die Technik wird’s schon richten.“</p><p>In meinem Beruf habe ich gelernt, systemisch zu denken.<br> Aber dieses Buch ist kein Fachtext. Es ist ein Versuch, Muster sichtbar zu machen,<br> die uns immer wieder aufhalten – auch dann, wenn wir längst weiter sein könnten.</p><blockquote><p><em>Wir brauchen keine weiteren Beweise. Wir brauchen Klarheit über das, was uns blockiert – und den Mut, daran zu rütteln.</em></p></blockquote><p>Ich wollte nicht der nächste sein, der schweigt.</p><p><em>„Die Schlinge“</em> ist mein Beitrag dazu.</p><p>— <strong>Björn Böttle</strong><br><em>Bretten, im Juli 2025</em></p>',17)),h(n)])}const w=r(c,[["render",o]]);export{p as __pageData,w as default};
